<body>

    <div id="main-container">
        <!-- Left Panel: Controls -->
        <div id="control-panel">
            <label for="select-cfc">cfc plot</label>
            <select id="select-cfc">
                <option value="CFC-12"  selected>CFC-12</option>
                <option value="CFC-11">CFC-11</option>
                <option value="CFC-113">CFC-113</option>
                <option value="CCl4">CCl4</option>
            </select>

            <label for="checkbox-show-all">show all</label>
            <input type="checkbox" id="checkbox-show-all">

            <label for="slider-year">year</label>
            <input type="range" id="slider-year" min="1940" max="2020" value="1950" step="1">
            <span id="label-year">1950</span>

            <label for="slider-time">time (s)</label>
            <input type="range" id="slider-time" min="0" max="30" value="1" step="1">
            <span id="label-time">1</span>
        </div>

        <!-- Right Panel: Visualizations -->
        <div id="visualization-panel">
            <div id="plots-container">
                <div id="plot-ozone" class="plot"></div>
                <div id="plot-cfc" class="plot"></div>
            </div>
            <div id="scene-container">
                <canvas id="scene-canvas"></canvas>
            </div>
        </div>
    </div>

    <script>
        /* -------------------------------------------------------
           CFC DATA
        ------------------------------------------------------- */
        const cfcData = {
          'CFC-12':  { color: 'purple', name:'CFC-12',  marks:[[1940,5], [1950,20], [1960,50],  [1970,150], [1980,320], [1990,480], [2000,540], [2010,520], [2020,500]] },
          'CFC-11':  { color: 'orange', name:'CFC-11',  marks:[[1940,2], [1950,10], [1960,30],  [1970,90],  [1980,180], [1990,260], [2000,250], [2010,230], [2020,220]] },
          'CFC-113': { color: 'black',  name:'CFC-113', marks:[[1940,0], [1950,1], [1960,5],   [1970,15],  [1980,40],  [1990,80],  [2000,85],  [2010,75],  [2020,70]] },
          'CCl4':    { color: 'green',  name:'CCl4',    marks:[[1940,50],[1950,60],[1960,75],  [1970,90],  [1980,100], [1990,105], [2000,95],  [2010,85],  [2020,80]] }
        };

        /* -------------------------------------------------------
           HELPER: Linear interpolation
        ------------------------------------------------------- */
        function interp(x, pairs){
            if(x <= pairs[0][0]) return pairs[0][1];
            if(x >= pairs[pairs.length-1][0]) return pairs[pairs.length-1][1];
            for(let i=0;i<pairs.length-1;i++){
                const [x0,y0] = pairs[i], [x1,y1] = pairs[i+1];
                if(x >= x0 && x <= x1){
                    const t = (x - x0)/(x1 - x0);
                    return y0 + t*(y1 - y0);
                }
            }
            return 0;
        }

        /* -------------------------------------------------------
           PLOTS INIT & UPDATE
        ------------------------------------------------------- */
        const plotOzoneEl = document.getElementById('plot-ozone');
        const plotCfcEl   = document.getElementById('plot-cfc');

        function buildTrace(cfcKey){
            const c = cfcData[cfcKey];
            const x = c.marks.map(p=>p[0]);
            const y = c.marks.map(p=>p[1]);
            return {
                x, y, mode:'lines',
                name: c.name,
                line:{color:c.color},
                showlegend:true,
                visible:'legendonly'
            };
        }

        const cfcTraces = Object.keys(cfcData).map(k=>buildTrace(k));
        const highlightTrace = {
            x:[1950], y:[interp(1950, cfcData['CFC-12'].marks)],
            mode:'markers',
            marker:{color:'red', size:8},
            name:'selected',
            showlegend:false
        };
        Plotly.newPlot(plotCfcEl, [...cfcTraces,highlightTrace], {
            title:'CFC concentration',
            xaxis:{title:'year', range:[1920,2020]},
            yaxis:{range:[0,600], showticklabels:false},
            margin:{l:35, r:10, t:40, b:35},
            height:300
        });

        function updateCfcPlot(){
            const cfcKey   = selectCfc.value;
            const year     = sliderYear.value;
            const showAll  = checkboxShowAll.checked;
            const traces   =plotCfcEl.data;
            let color='red';
            traces.forEach(tr=>{
                if(tr.name === 'selected') return;
                tr.visible = showAll || tr.name===cfcKey ? true : 'legendonly';
                if(tr.name === cfcKey) color = tr.line.color;
            });
            const yVal = interp(year, cfcData[cfcKey].marks);
            Plotly.restyle(plotCfcEl, {
                visible: traces.map(t=>t.visible),
                x:[[year]],
                y:[[yVal]],
                'marker.color':[color]
            }, [0,1,2,3,4]);
        }

        function updateOzonePlot(){
            const cfcKey   = selectCfc.value;
            const year     = sliderYear.value;
            const tMax     = +sliderTime.value;
            const CFC = interp(year, cfcData[cfcKey].marks);
            const k = 0.001 * CFC; // pseudo-first-order slope
            const t = [...Array(Math.max(2,tMax+1)).keys()];
            const y = t.map(tt=>Math.min(1 + k*tt, 110));
            Plotly.newPlot(plotOzoneEl, [{
                x:t, y:y, mode:'lines', line:{color:'blue'}, name:'LHS'
            }], {
                title:'concentration of ozone',
                xaxis:{title:'time (s)', range:[0,30]},
                yaxis:{title:'\\(1/[O_3]\\)', range:[0,110]},
                margin:{l:45, r:10, t:40, b:45},
                height:300
            });
        }

        /* -------------------------------------------------------
           THREE.JS 3D SCENE
        ------------------------------------------------------- */
        const sceneContainer = document.getElementById('scene-canvas');
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0x111122);
        const camera = new THREE.PerspectiveCamera(45, sceneContainer.clientWidth/sceneContainer.clientHeight, 0.1, 1000);
        camera.position.set(0, 0, 6);
        const renderer = new THREE.WebGLRenderer({canvas:sceneContainer, antialias:true});
        renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);

        // Earth
        const earthGeo = new THREE.SphereGeometry(1, 32, 32);
        const earthMat = new THREE.MeshBasicMaterial({color:0x00aa00});
        const earth = new THREE.Mesh(earthGeo, earthMat);
        scene.add(earth);

        // Ozone layer
        const ozoneGeo = new THREE.SphereGeometry(1.2, 32, 32);
        const ozoneMat = new THREE.MeshBasicMaterial({color:0x00ff00, transparent:true, opacity:0.5});
        const ozoneLayer = new THREE.Mesh(ozoneGeo, ozoneMat);
        scene.add(ozoneLayer);

        // Atmosphere outer shell
        const atmGeo = new THREE.SphereGeometry(2.2, 32, 32);
        const atmMat = new THREE.MeshBasicMaterial({color:0xffaa88, transparent:true, opacity:0.05});
        const atm = new THREE.Mesh(atmGeo, atmMat);
        scene.add(atm);

        // Sun
        const sunGeo = new THREE.SphereGeometry(0.3, 16, 16);
        const sunMat = new THREE.MeshBasicMaterial({color:0xffff00});
        const sun = new THREE.Mesh(sunGeo, sunMat);
        sun.position.set(-4, 0, 0);
        scene.add(sun);

        // Radiation arrows
        const arrowGroup = new THREE.Group();
        for(let i=0;i<8;i++){
            const dir = new THREE.Vector3(1,0,0).normalize();
            const origin = sun.position.clone().add(new THREE.Vector3(0.4, (i-3.5)*0.12, 0));
            const arrow = new THREE.ArrowHelper(dir, origin, 2.8, 0xffaa00, 0.2, 0.05);
            arrowGroup.add(arrow);
        }
        scene.add(arrowGroup);

        function updateScene(){
            const cfcKey   = selectCfc.value;
            const year     = sliderYear.value;
            const CFC = interp(year, cfcData[cfcKey].marks);
            // opacity ~ 1 - (CFC/600)*0.8, clamped
            ozoneMat.opacity = Math.max(0.05, 1 - (CFC/600)*0.8);
        }

        /* -------------------------------------------------------
           GLOBAL UPDATE & LISTENERS
        ------------------------------------------------------- */
        const selectCfc        = document.getElementById('select-cfc');
        const checkboxShowAll  = document.getElementById('checkbox-show-all');
        const sliderYear       = document.getElementById('slider-year');
        const labelYear        = document.getElementById('label-year');
        const sliderTime       = document.getElementById('slider-time');
        const labelTime        = document.getElementById('label-time');

        function update(){
            labelYear.textContent = sliderYear.value;
            labelTime.textContent = sliderTime.value;
            updateCfcPlot();
            updateOzonePlot();
            updateScene();
        }
        [selectCfc, checkboxShowAll, sliderYear, sliderTime].forEach(el=>el.addEventListener('input', update));

        window.addEventListener('resize', ()=>{
            camera.aspect = sceneContainer.clientWidth / sceneContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
        });

        function animate(){
            renderer.render(scene, camera);
            requestAnimationFrame(animate);
        }
        animate();
        update();       // initial draw
    </script>
</body>
</html>
