<body>
    <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
    <p>
        In 240 BCE, the Greek astronomer Eratosthenes measured the Earth's circumference. 
        He observed that at noon on the summer solstice in Syene (modern-day Aswan), the sun was directly overhead, 
        casting no shadows. In Alexandria, about 500 miles north, the sun cast a shadow at an angle of 7°. 
        Using simple geometry and proportional reasoning, Eratosthenes calculated the Earth's circumference.
    </p>

    <div id="control-panel">
        <label for="slider-post-angle">post angle</label>
        <input type="range" id="slider-post-angle" min="1" max="60" step="0.1" value="7">
        <span id="display-post-angle">7.0</span>°

        <label for="checkbox-zoom">zoom</label>
        <input type="checkbox" id="checkbox-zoom">
    </div>

    <div id="visualization-container">
        <canvas id="demo-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let canvasSize = { width: 600, height: 450 };
        let angle = 7;
        let isZoomed = false;

        function setup() {
            let canvas = createCanvas(canvasSize.width, canvasSize.height);
            canvas.parent('visualization-container');
            angleMode(DEGREES);

            // Set up controls
            const slider = document.getElementById('slider-post-angle');
            const display = document.getElementById('display-post-angle');
            const checkbox = document.getElementById('checkbox-zoom');

            slider.addEventListener('input', (e) => {
                angle = parseFloat(e.target.value);
                display.textContent = angle.toFixed(1);
                redraw();
            });

            checkbox.addEventListener('change', (e) => {
                isZoomed = e.target.checked;
                redraw();
            });
        }

        function draw() {
            background('#F0F0F0');
            stroke(0);
            strokeWeight(1);

            if (!isZoomed) {
                // Full view of Earth
                let earthRadius = 150;
                let earthCenterX = 150;
                let earthCenterY = height / 2;

                // Draw Earth
                fill('#468296');
                circle(earthCenterX, earthCenterY, earthRadius * 2);

                // Labels
                noStroke();
                fill(255);
                textAlign(CENTER);
                textSize(20);
                text('Earth', earthCenterX, earthCenterY);

                // Sun rays (parallel)
                stroke('#DAA520');
                strokeWeight(2);
                for (let y = 50; y < height; y += 30) {
                    line(width - 50, y, width - 150, y);
                    // Arrowhead
                    line(width - 150, y, width - 160, y - 5);
                    line(width - 150, y, width - 160, y + 5);
                }

                // Syene (horizontal line)
                stroke(0);
                strokeWeight(1);
                drawingContext.setLineDash([5, 5]);
                line(earthCenterX, earthCenterY, earthCenterX + earthRadius, earthCenterY);

                // Syene label
                noStroke();
                drawingContext.setLineDash([]);
                fill(0);
                textAlign(LEFT);
                textSize(14);
                text('Syene', earthCenterX + earthRadius + 10, earthCenterY + 5);

                // Alexandria point
                let alexX = earthCenterX + earthRadius * cos(-angle);
                let alexY = earthCenterY + earthRadius * sin(-angle);

                // Alexandria radial line
                stroke(0);
                drawingContext.setLineDash([5, 5]);
                line(earthCenterX, earthCenterY, alexX, alexY);

                // Alexandria horizontal line
                line(alexX, alexY, earthCenterX + earthRadius + 200*cos(-angle), alexY);

                // Alexandria label
                noStroke();
                drawingContext.setLineDash([]);
                fill(0);
                text('Alexandria', alexX + 10, alexY + 5);

                // Angle arc
                noFill();
                stroke(0);
                arc(alexX, alexY, 40, 40, 180 - angle, 180);

                // Angle value
                fill(0);
                noStroke();
                textAlign(CENTER);
                textSize(12);
                text(angle.toFixed(1) + '°', alexX - 40*cos(angle/2), alexY - 40*sin(angle/2));

                // Observation triangle (small)
                push();
                translate(alexX, alexY);
                rotate(-angle);
                stroke(0);
                strokeWeight(2);
                line(0, 0, 20, 0);
                line(0, 0, 0, -20);
                line(0, -20, 20, 0);
                pop();

            } else {
                // Zoomed sector view
                let earthRadius = 300;
                let centerX = 100;
                let centerY = height - 50;

                // Draw Earth sector
                fill('#468296');
                arc(centerX, centerY, earthRadius, earthRadius, -90, -90 - angle, PIE);

                // Syene radius (horizontal)
                stroke(0);
                strokeWeight(1);
                drawingContext.setLineDash([5, 5]);
                line(centerX, centerY, centerX + earthRadius, centerY);

                // Alexandria radius
                line(centerX, centerY, centerX + earthRadius * cos(-angle), centerY + earthRadius * sin(-angle));

                // Labels
                noStroke();
                drawingContext.setLineDash([]);
                fill(0);
                textAlign(CENTER);
                textSize(16);
                text('Syene', centerX + earthRadius*1.1*cos(0), centerY + earthRadius*1.1*sin(0));
                text('Alexandria', centerX + earthRadius*1.1*cos(-angle), centerY + earthRadius*1.1*sin(-angle));

                // Sector angle arc
                noFill();
                stroke(0);
                arc(centerX, centerY, 100, 100, -90, -90 - angle);

                // Angle value
                fill(0);
                noStroke();
                textSize(18);
                textFont('monospace');
                text(angle.toFixed(1) + '°', centerX + 50*cos(-angle/2-45), centerY + 50*sin(-angle/2-45));

                // Post and shadow triangle
                push();
                translate(centerX + (earthRadius-50)*cos(-angle), centerY + (earthRadius-50)*sin(-angle));
                rotate(-angle);

                // Triangle
                fill('#D2B48C');
                stroke(0);
                noStroke();
                triangle(0, 0, 30, 0, 0, -30*tan(angle));

                // Outline
                stroke(0);
                strokeWeight(1);
                noFill();
                line(0, 0, 30, 0);
                line(0, 0, 0, -30*tan(angle));
                line(30, 0, 0, -30*tan(angle));
                pop();
            }

            noLoop();
        }

        function redraw() {
            clear();
            draw();
        }
    </script>
</body>
</html>
