      <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144">
    </div>
    <div>
      <label for="slider-moon-pull">pull from moon</label>
      <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40">
    </div>
    <div>
      <label for="slider-sun-pos">position of sun</label>
      <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144">
    </div>
    <div>
      <label for="slider-sun-pull">pull from sun</label>
      <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20">
    </div>
  </div>
  <div id="viz-container"></div>
</div>

<script>
  let moonPos, moonPull, sunPos, sunPull;
  const BASE_WATER_RADIUS = 180;
  const EARTH_RADIUS = 180;
  const SCALING_FACTOR = 0.3;

  function setup() {
    let canvas = createCanvas(500, 500);
    canvas.parent('viz-container');
    noLoop();
    updateSliders();
  }

  function draw() {
    background(255);
    // Retrieve slider values
    updateSliders();
    // Center of canvas
    let cx = 250;
    let cy = 250;
    // Convert angles to radians
    let moonAngle_rad = radians(moonPos);
    let sunAngle_rad = radians(sunPos);

    // Draw water layer
    fill('#aed6f1');
    stroke('#000000');
    strokeWeight(1);
    beginShape();
    for (let deg = 0; deg <= 360; deg += 1) {
      let theta = radians(deg);
      let moonEffect = moonPull * sq(cos(theta - moonAngle_rad));
      let sunEffect = sunPull * sq(cos(theta - sunAngle_rad));
      let totalRadius = BASE_WATER_RADIUS + SCALING_FACTOR * (moonEffect + sunEffect);
      let x = cx + totalRadius * cos(theta);
      let y = cy + totalRadius * sin(theta);
      vertex(x, y);
    }
    endShape(CLOSE);

    // Draw Earth on top
    noStroke();
    fill('#966F33');
    ellipse(cx, cy, EARTH_RADIUS * 2);
  }

  function updateSliders() {
    moonPos = parseFloat(document.getElementById('slider-moon-pos').value);
    moonPull = parseFloat(document.getElementById('slider-moon-pull').value);
    sunPos = parseFloat(document.getElementById('slider-sun-pos').value);
    sunPull = parseFloat(document.getElementById('slider-sun-pull').value);
  }

  // Redraw on any slider input
  document.getElementById('slider-moon-pos').addEventListener('input', () => redraw());
  document.getElementById('slider-moon-pull').addEventListener('input', () => redraw());
  document.getElementById('slider-sun-pos').addEventListener('input', () => redraw());
  document.getElementById('slider-sun-pull').addEventListener('input', () => redraw());

  // Reset button
  document.getElementById('btn-reset').addEventListener('click', () => {
    document.getElementById('slider-moon-pos').value = 144;
    document.getElementById('slider-moon-pull').value = 40;
    document.getElementById('slider-sun-pos').value = 144;
    document.getElementById('slider-sun-pull').value = 20;
    redraw();
  });
</script>

</body>
</html>
