    <!-- three.js -->
    <script src="https://cdn.jsdelivr.net/npm/three@0.160.0/build/three.min.js"></script>
    <script>
        // ---------- STATE ----------
        const state = {
            earthAngle: 180,
            cometAngle: 180,
            coma: 20,
            tail: 50,
            tailSpread: 20
        };

        // ---------- UTILITY ----------
        function orbitPosition(angleDeg, a, e) {
            const angle = angleDeg * Math.PI / 180;
            const r = a * (1 - e * e) / (1 + e * Math.cos(angle));
            return { x: r * Math.cos(angle), z: r * Math.sin(angle), y: 0 };
        }

        // ---------- SETUP ----------
        const telescopicCanvas = document.getElementById('telescopic-canvas');
        const orbitCanvas = document.getElementById('orbit-canvas');

        // Renderers
        const telescopicRenderer = new THREE.WebGLRenderer({ canvas: telescopicCanvas, antialias: true });
        const orbitRenderer = new THREE.WebGLRenderer({ canvas: orbitCanvas, antialias: true });

        // Scenes
        const telescopicScene = new THREE.Scene();
        const orbitScene = new THREE.Scene();
        orbitScene.background = new THREE.Color(0xffffff);

        // Cameras
        const telescopicCamera = new THREE.PerspectiveCamera(15, telescopicCanvas.clientWidth / telescopicCanvas.clientHeight, 1e-3, 100);
        const orbitCamera = new THREE.PerspectiveCamera(30, orbitCanvas.clientWidth / orbitCanvas.clientHeight, 1, 1000);
        orbitCamera.position.set(0, 60, 60);
        orbitCamera.lookAt(0, 0, 0);

        // Lighting
        orbitScene.add(new THREE.AmbientLight(0xffffff, 0.6));
        const sunLight = new THREE.PointLight(0xffffff, 2, 0);
        orbitScene.add(sunLight);

        telescopicScene.add(new THREE.AmbientLight(0xffffff, 0.5));

        // Orbital parameters
        const orbits = {
            earth:   { a: 4,  e: 0.0167, color: 0x0066ff },
            mars:    { a: 6.1, e: 0.0934, color: 0xff3300 },
            jupiter: { a: 20.8, e: 0.0489, color: 0xff9900 },
            comet:   { a: 14.4, e: 0.432, color: 0xffff00 },
        };

        // Draw orbits
        function drawOrbit(scene, { a, e, color }) {
            const pts = [];
            for (let theta = 0; theta <= 360; theta += 3) {
                const pos = orbitPosition(theta, a, e);
                pts.push(new THREE.Vector3(pos.x, 0, pos.z));
            }
            const geom = new THREE.BufferGeometry().setFromPoints(pts);
            const mat = new THREE.LineBasicMaterial({ color });
            const line = new THREE.Line(geom, mat);
            scene.add(line);
            return line;
        }
        Object.values(orbits).forEach(o => drawOrbit(orbitScene, o));

        // Sun
        const sun = new THREE.Mesh(
            new THREE.SphereGeometry(1, 32, 32),
            new THREE.MeshBasicMaterial({ color: 0xffcc00 })
        );
        orbitScene.add(sun);

        // Earth mesh
        const earth = new THREE.Mesh(
            new THREE.SphereGeometry(0.6, 32, 32),
            new THREE.MeshLambertMaterial({ color: orbits.earth.color })
        );
        orbitScene.add(earth);

        // Comet model
        const cometModel = new THREE.Group();
        // Nucleus
        const nucleus = new THREE.Mesh(
            new THREE.SphereGeometry(0.3, 32, 32),
            new THREE.MeshBasicMaterial({ color: 0xffffff })
        );
        cometModel.add(nucleus);

        // Coma
        const comaGeom = new THREE.SphereGeometry(1, 32, 32);
        const comaMat = new THREE.MeshBasicMaterial({ color: 0xccff33, transparent: true, opacity: 0.5 });
        const comaMesh = new THREE.Mesh(comaGeom, comaMat);
        cometModel.add(comaMesh);

        // Tail
        const tailGeom = new THREE.ConeGeometry(1, 2, 32);
        const tailMat = new THREE.MeshBasicMaterial({ color: 0xccff33, transparent: true, opacity: 0.4, side: THREE.DoubleSide });
        const tailMesh = new THREE.Mesh(tailGeom, tailMat);
        tailMesh.position.z = -1;
        tailMesh.rotation.x = Math.PI / 2;
        cometModel.add(tailMesh);

        orbitScene.add(cometModel);
        telescopicScene.add(cometModel);

        // ---------- UPDATE ----------
        function update() {
            // Earth
            const earthPos = orbitPosition(state.earthAngle, orbits.earth.a, orbits.earth.e);
            earth.position.set(earthPos.x, 0, earthPos.z);

            // Comet
            const cometPos = orbitPosition(state.cometAngle, orbits.comet.a, orbits.comet.e);
            cometModel.position.set(cometPos.x, 0, cometPos.z);

            // Tail direction (away from sun)
            const sunToComet = new THREE.Vector3(cometPos.x, 0, cometPos.z).normalize();
            cometModel.lookAt(
                cometPos.x - sunToComet.x,
                0,
                cometPos.z - sunToComet.z
            );

            // Scale
            comaMesh.scale.setScalar(0.1 + state.coma / 35);
            tailMesh.scale.set(
                0.2 + state.tailSpread / 100,
                1,
                0.2 + state.tail / 20
            );

            // Telescopic camera
            telescopicCamera.position.set(earthPos.x, 0, earthPos.z);
            telescopicCamera.lookAt(cometPos.x, 0, cometPos.z);

            // Render
            renderCanvases();
        }

        function renderCanvases() {
            telescopicRenderer.setSize(telescopicCanvas.clientWidth, telescopicCanvas.clientHeight);
            telescopicRenderer.render(telescopicScene, telescopicCamera);

            orbitRenderer.setSize(orbitCanvas.clientWidth, orbitCanvas.clientHeight);
            orbitRenderer.render(orbitScene, orbitCamera);
        }

        // ---------- EVENT LISTENERS ----------
        document.getElementById('slider-earth-position').addEventListener('input', e => {
            state.earthAngle = +e.target.value;
            update();
        });
        document.getElementById('slider-comet-position').addEventListener('input', e => {
            state.cometAngle = +e.target.value;
            update();
        });
        document.getElementById('slider-coma').addEventListener('input', e => {
            state.coma = +e.target.value;
            update();
        });
        document.getElementById('slider-tail').addEventListener('input', e => {
            state.tail = +e.target.value;
            update();
        });
        document.getElementById('slider-tail-spread').addEventListener('input', e => {
            state.tailSpread = +e.target.value;
            update();
        });

        // Initial frame
        update();
    </script>
</body>
</html>
