                <span class="value-display" id="label-m">2.845</span>
            </div>

            <!-- X Position Control -->
            <div class="slider-container">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" step="0.1" value="4.1">
                <span class="value-display" id="label-x">4.1</span>
            </div>

            <!-- Y Position Control -->
            <div class="slider-container">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" step="0.001" value="1.498">
                <span class="value-display" id="label-y">1.498</span>
            </div>
        </div>

