        <div id="visualization">
            <main></main>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let massSlider, xSlider, ySlider;
        let massLabel, xLabel, yLabel;

        let m = 2.845, x = 4.1, y = 1.498;
        const L = 10;
        const g = 9.81;

        // Canvas parameters
        const canvasWidth = 800;
        const canvasHeight = 500;
        const postLeftX = 100;
        const postRightX = 700;
        const postY = 150;
        const scaleFactor = 50;

        function setup() {
            // Create canvas in the visualization div
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('visualization');

            // Get DOM elements
            massSlider = document.getElementById('slider-m');
            xSlider = document.getElementById('slider-x');
            ySlider = document.getElementById('slider-y');
            massLabel = document.getElementById('label-m');
            xLabel = document.getElementById('label-x');
            yLabel = document.getElementById('label-y');

            // Add event listeners
            massSlider.addEventListener('input', updateVisualization);
            xSlider.addEventListener('input', updateVisualization);
            ySlider.addEventListener('input', updateVisualization);

            updateVisualization();
        }

        function draw() {
            background(255);

            m = parseFloat(massSlider.value);
            x = parseFloat(xSlider.value);
            y = parseFloat(ySlider.value);

            // Update labels
            massLabel.textContent = m.toFixed(3);
            xLabel.textContent = x.toFixed(1);
            yLabel.textContent = y.toFixed(3);

            // Calculate angles in radians
            const theta1 = atan(y / x);
            const theta2 = atan(y / (L - x));

            // Calculate gravitational force
            const Fg = m * g;

            // Calculate tensions
            const T1 = (Fg * cos(theta2)) / sin(theta1 + theta2);
            const T2 = (Fg * cos(theta1)) / sin(theta1 + theta2);

            // Get pixel coordinates
            const massPixelX = postLeftX + (x / L) * (postRightX - postLeftX);
            const massPixelY = postY + y * scaleFactor;

            // Draw dashed horizontal line
            strokeWeight(1);
            stroke(100, 100, 100);
            drawingContext.setLineDash([10, 5]);
            line(postLeftX, postY, postRightX, postY);
            drawingContext.setLineDash([]);

            // Draw posts
            stroke(0);
            strokeWeight(8);
            line(postLeftX - 25, postY, postLeftX + 25, postY);
            line(postLeftX - 25, postY, postLeftX - 25, postY + 200);
            line(postLeftX + 25, postY, postLeftX + 25, postY + 200);

            line(postRightX - 25, postY, postRightX + 25, postY);
            line(postRightX - 25, postY, postRightX - 25, postY + 200);
            line(postRightX + 25, postY, postRightX + 25, postY + 200);

            // Draw ropes
            strokeWeight(2);
            line(postLeftX, postY, massPixelX, massPixelY);
            line(postRightX, postY, massPixelX, massPixelY);

            // Draw mass
            fill(0);
            strokeWeight(0);
            ellipse(massPixelX, massPixelY, 40);

            // Draw angle arcs
            noFill();
            strokeWeight(2);
            stroke(255, 0, 0);

            // Left angle (θ₁)
            arc(postLeftX, postY, 60, 60, 0, theta1);

            // Right angle (θ₂)
            arc(postRightX, postY, 60, 60, PI - theta2, PI);

            // Display tension values
            fill(0);
            textSize(16);
            textAlign(CENTER);
            text(`T₁ = ${T1.toFixed(4)} N | T₂ = ${T2.toFixed(4)} N`, canvasWidth / 2, 40);

            // Display angle labels
            textSize(14);
            const labelOffset = 30;
            text(`θ₁`, postLeftX + 50, postY - 10);
            text(`θ₂`, postRightX - 50, postY - 10);
        }

        function updateVisualization() {
            redraw();
        }
    </script>
</body>
</html>
