    <style>
        body { margin: 0; font-family: 'Segoe UI', sans-serif; background:#fafafa; color:#333; }
        #main-container { display:flex; height:100vh; }
        #controls-container { width:400px; padding:20px; box-sizing:border-box; overflow-y:auto; background:#fff; border-right:1px solid #ccc; }
        #visuals-container { flex:1; display:flex; flex-direction:column; padding:10px; gap:10px; box-sizing:border-box; }
