                <input type="range" id="slider-proportional-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-proportional-gain">0.1</span>
            </div>

            <h2>derivative velocity gain</h2>
            <div class="control-row">
                <label for="slider-derivative-gain">derivative velocity gain</label>
                <input type="range" id="slider-derivative-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-derivative-gain">0.1</span>
            </div>

            <h2>release system</h2>
            <div class="control-row">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>

        <div id="visuals-container">
            <div id="scene-container"></div>
            <div id="plot-displacement"></div>
            <div id="plot-velocity"></div>
        </div>
    </div>

    <script>
        // --- State variables ---
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;
        let solutionX = [];
        let solutionV = [];
        const tMax = 10, N = 1000;

        // --- three.js setup ---
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, 1, 0.01, 100);
        camera.position.set(2.5, 2, 2.5);
        camera.lookAt(0, 0, 0);

        const renderer = new THREE.WebGLRenderer({ antialias:true });
        document.getElementById('scene-container').appendChild(renderer.domElement);
        renderer.setClearColor(0xf9f9f9);

        // Lights
        scene.add(new THREE.AmbientLight(0xffffff, 0.6));
        const dir = new THREE.DirectionalLight(0xffffff, 0.8);
        dir.position.set(5,10,5).normalize();
        scene.add(dir);

        // 3D objects
        const base = new THREE.Mesh(
            new THREE.BoxGeometry(4,0.2,2),
            new THREE.MeshStandardMaterial({ color:0x50c878 })
        );
        scene.add(base);

        const mass = new THREE.Mesh(
            new THREE.BoxGeometry(1,1,1),
            new THREE.MeshStandardMaterial({ color:0xfed22f })
        );
        mass.position.y = 0.5;
        scene.add(mass);

        const springGroup = new THREE.Group();
        // Simple cylinder spring visual
        const springGeo = new THREE.CylinderGeometry(0.15,0.15,1,16);
        const springMat = new THREE.MeshStandardMaterial({ color:0xee3333 });
        const spring = new THREE.Mesh(springGeo, springMat);
        spring.position.x = -1.5; // left half
        springGroup.add(spring);
        scene.add(springGroup);

        const damperGroup = new THREE.Group();
        const damperCyl = new THREE.Mesh(
            new THREE.CylinderGeometry(0.12,0.12,0.6,16),
            new THREE.MeshStandardMaterial({ color:0x3498db })
        );
        damperCyl.position.x = -0.5; damperCyl.position.y = 0.3; damperCyl.rotation.z = Math.PI/2;
        damperGroup.add(damperCyl);
        scene.add(damperGroup);

        const wireframe = new THREE.LineSegments(
            new THREE.EdgesGeometry(new THREE.BoxGeometry(5,3,3)),
            new THREE.LineBasicMaterial({ color:0x888888 })
        );
        wireframe.position.y = 1.5;
        scene.add(wireframe);

        // --- Plotly setup ---
        function makeLayout(title, yaxis) {
            return {
                title:{ text:title, font:{ size:12 } },
                margin:{ l:45, r:15, b:30, t:35 },
                xaxis:{ range:[0,tMax], title:"time (s)", font:{ size:10 } },
                yaxis:{ range:[-3,3], title:yaxis, font:{ size:10 } },
                paper_bgcolor:"#fff",
                plot_bgcolor:"#fff",
            };
        }
        Plotly.newPlot("plot-displacement", [{ mode:"lines", name:"x(t)", line:{ color:"#007bff" } }, { mode:"markers", marker:{ color:"#007bff", size:10 } }],
                     makeLayout("Displacement vs Time","x (m)"));
        Plotly.newPlot("plot-velocity", [{ mode:"lines", name:"v(t)", line:{ color:"#008000" } }, { mode:"markers", marker:{ color:"#008000", size:10 } }],
                       makeLayout("Velocity vs Time","v (m/s)"));

        // --- Solve dynamics ---
        function solveODE() {
            const k = parseFloat(sliderIds.stiffness.value);
            const m = parseFloat(sliderIds.mass.value);
            const c = parseFloat(sliderIds.damping.value);
            const kp = parseFloat(sliderIds.proportionalGain.value);
            const kd = parseFloat(sliderIds.derivativeGain.value);
            const x0 = parseFloat(sliderIds.displacement.value);
            const v0 = parseFloat(sliderIds.velocity.value);

            const keff = k + kp;
            const ceff = c + kd;

            const omega_n = Math.sqrt(keff / m);
            const zeta = ceff / (2 * Math.sqrt(m * keff));
            const wd = omega_n * Math.sqrt(Math.max(0, 1 - zeta*zeta));
            const tValues = Array.from({length:N+1}, (_,i)=>i*tMax/N);

            solutionX = [];
            solutionV = [];

            if (zeta < 1) { // underdamped
                const amp = Math.sqrt( (v0 + zeta*omega_n*x0)**2 + (x0*wd)**2 ) / wd;
                const phi = Math.atan2(x0*wd, v0 + zeta*omega_n*x0);
                tValues.forEach(t=>{
                    const x = amp * Math.exp(-zeta*omega_n*t) * Math.sin(wd*t + phi);
                    solutionX.push(x);
                    const v = amp * -zeta*omega_n*Math.exp(-zeta*omega_n*t)*Math.sin(wd*t+phi)
                             + amp * Math.exp(-zeta*omega_n*t)*wd*Math.cos(wd*t+phi);
                    solutionV.push(v);
                });
            } else if (zeta === 1) { // critically damped
                const A = x0;
                const B = v0 + omega_n*x0;
                tValues.forEach(t=>{
                    const x = (A + B*t) * Math.exp(-omega_n*t);
                    solutionX.push(x);
                    const v = B*Math.exp(-omega_n*t) - omega_n*(A+B*t)*Math.exp(-omega_n*t);
                    solutionV.push(v);
                });
            } else { // overdamped
                const r1 = -zeta*omega_n + omega_n*Math.sqrt(zeta*zeta-1);
                const r2 = -zeta*omega_n - omega_n*Math.sqrt(zeta*zeta-1);
                const denom = 1/(r1 - r2);
                const A = (r2*x0 - v0) * denom;
                const B = (-r1*x0 + v0) * denom;
                tValues.forEach(t=>{
                    const x = A*Math.exp(r1*t) + B*Math.exp(r2*t);
                    solutionX.push(x);
                    const v = A*r1*Math.exp(r1*t) + B*r2*Math.exp(r2*t);
                    solutionV.push(v);
                });
            }
            updatePlots();
            resetAnimation(true);
        }

        function updatePlots() {
            const t = Array.from({length:N+1}, (_,i)=>i*tMax/N);
            Plotly.react("plot-displacement", [
                { x:t, y:solutionX, mode:"lines", line:{ color:"#007bff" } },
                { x:[time], y:[solutionX[Math.round(time/(tMax/N))]], mode:"markers", marker:{ color:"#007bff", size:10 } }
            ], makeLayout("Displacement vs Time","x (m)"));
            Plotly.react("plot-velocity", [
                { x:t, y:solutionV, mode:"lines", line:{ color:"#008000" } },
                { x:[time], y:[solutionV[Math.round(time/(tMax/N))]], mode:"markers", marker:{ color:"#008000", size:10 } }
            ], makeLayout("Velocity vs Time","v (m/s)"));
        }

        function updateVisuals() {
            const idx = Math.round(time/(tMax/N));
            const x = solutionX[idx];
            mass.position.x = x;
            spring.scale.set(1,1,1);
            spring.position.x = x/2 - 1.5;
            spring.scale.x = 1 + x*0.5;
            damperGroup.position.x = x*0.5;
            const idxSafe = Math.min(idx, N);
            Plotly.restyle("plot-displacement", { x:[[time]], y:[[solutionX[idxSafe]]] }, [1]);
            Plotly.restyle("plot-velocity", { x:[[time]], y:[[solutionV[idxSafe]]] }, [1]);
        }

        // --- Animation loop ---
        function animate() {
            if (!isRunning) return;
            time += 0.016;
            if (time >= tMax) { pause(); time = tMax; }
            updateVisuals();
            renderer.setAnimationLoop(animate);
            requestAnimationFrame(animate);
        }

        function play()  { if (!isRunning) { isRunning = true; animate(); } }
        function pause() { isRunning = false; if (animationFrameId) cancelAnimationFrame(animationFrameId); renderer.setAnimationLoop(null); }
        function resetAnimation(resettingSlider=false){
            pause();
            time = 0;
            updateVisuals();
        }

        // --- Bind UI ---
        const sliderIds = {
            displacement:   document.getElementById("slider-displacement"),
            velocity:       document.getElementById("slider-velocity"),
            stiffness:      document.getElementById("slider-stiffness"),
            mass:           document.getElementById("slider-mass"),
            damping:        document.getElementById("slider-damping"),
            proportionalGain: document.getElementById("slider-proportional-gain"),
            derivativeGain: document.getElementById("slider-derivative-gain"),
        };
        Object.entries(sliderIds).forEach(([name, el])=>{
            el.addEventListener("input", e=>{
                document.getElementById("value-"+name.replace(/([A-Z])/g,'-$1').toLowerCase()).textContent = Number(e.target.value).toFixed(name.includes('stiffness') || name.includes('mass') ? 2 : 1);
                solveODE();
            });
        });
        document.getElementById("btn-play").onclick   = play;
        document.getElementById("btn-pause").onclick  = pause;
        document.getElementById("btn-reset").onclick  = resetAnimation;

        // --- Initial call ---
        solveODE();
        window.addEventListener('resize', () => {
            const w = document.getElementById('scene-container').clientWidth;
            const h = document.getElementById('scene-container').clientHeight;
            renderer.setSize(w, h);
            camera.aspect = w / h;
            camera.updateProjectionMatrix();
        });
    </script>
</body>
</html>
