</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>tension T</h4>
            <input type="range" id="slider-tension" min="1" max="100" value="50" step="1">

            <h4>displacement</h4>
            <label for="slider-x1">x₁</label>
            <input type="range" id="slider-x1" min="-0.5" max="0.5" value="0.1" step="0.01">
            <label for="slider-x2">x₂</label>
            <input type="range" id="slider-x2" min="-0.5" max="0.5" value="0.1" step="0.01">
            <label for="slider-x3">x₃</label>
            <input type="range" id="slider-x3" min="-0.5" max="0.5" value="0.0" step="0.01">

            <h4>mass</h4>
            <label for="slider-m1">m₁</label>
            <input type="range" id="slider-m1" min="0.1" max="2.0" value="1.0" step="0.05">
            <label for="slider-m2">m₂</label>
            <input type="range" id="slider-m2" min="0.1" max="2.0" value="1.0" step="0.05">
            <label for="slider-m3">m₃</label>
            <input type="range" id="slider-m3" min="0.1" max="2.0" value="1.0" step="0.05">

            <h4>release system</h4>
            <div id="animation-controls">
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">⏮</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="main-animation-container"></div>
            <div id="displacement-plot-container"></div>
            <div id="velocity-plot-container"></div>
        </div>
    </div>

    <script>
        // Global variables
        let isAnimating = false;
        let t = 0;
        let dt = 0.02;

        // Physics parameters
        let T = 50;
        let masses = [1, 1, 1];
        let initialDisplacements = [0.1, 0.1, 0];

        // Simulation data
        let omega = [];
        let modes = [];
        let coefficients = [];
        let timeData = [];
        let displacementData = [[], [], []];
        let velocityData = [[], [], []];

        // P5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                let container = document.getElementById('main-animation-container');
                p.createCanvas(container.offsetWidth, container.offsetHeight);
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                // Draw grid
                p.stroke(200);
                p.strokeWeight(1);
                for (let x = 0; x <= 4; x += 0.5) {
                    let px = p.map(x, 0, 4, 50, p.width - 50);
                    p.line(px, 50, px, p.height - 50);
                }
                for (let y = -1; y <= 1; y += 0.25) {
                    let py = p.map(y, -1, 1, p.height - 50, 50);
                    p.line(50, py, p.width - 50, py);
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(50, p.height/2, p.width - 50, p.height/2);
                p.line(50, 50, 50, p.height - 50);

                // Calculate current positions
                let y = [0, 0, 0];
                for (let i = 0; i < 3; i++) {
                    y[i] = 0;
                    for (let j = 0; j < 3; j++) {
                        y[i] += coefficients[j] * modes[j][i] * Math.cos(omega[j] * t);
                    }
                }

                // Draw string
                p.stroke(0);
                p.strokeWeight(2);
                let x0 = p.map(0, 0, 4, 50, p.width - 50);
                let y0 = p.map(0, -1, 1, p.height - 50, 50);
                let x1 = p.map(1, 0, 4, 50, p.width - 50);
                let y1 = p.map(y[0], -1, 1, p.height - 50, 50);
                let x2 = p.map(2, 0, 4, 50, p.width - 50);
                let y2 = p.map(y[1], -1, 1, p.height - 50, 50);
                let x3 = p.map(3, 0, 4, 50, p.width - 50);
                let y3 = p.map(y[2], -1, 1, p.height - 50, 50);
                let x4 = p.map(4, 0, 4, 50, p.width - 50);
                let y4 = p.map(0, -1, 1, p.height - 50, 50);

                p.line(x0, y0, x1, y1);
                p.line(x1, y1, x2, y2);
                p.line(x2, y2, x3, y3);
                p.line(x3, y3, x4, y4);

                // Draw masses
                p.fill(255, 0, 0);
                p.noStroke();
                let radius = 20;
                p.circle(x1, y1, radius * Math.sqrt(masses[0]));
                p.circle(x2, y2, radius * Math.sqrt(masses[1]));
                p.circle(x3, y3, radius * Math.sqrt(masses[2]));

                // Update time and plots
                t += dt;
                updatePlotLines();
            };

            p.windowResized = function() {
                let container = document.getElementById('main-animation-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
            };
        };

        let p5Instance = new p5(sketch, 'main-animation-container');

        // Initialize plots
        function initializePlots() {
            let displacementLayout = {
                title: 'displacement',
                xaxis: { title: 'time t', range: [0, 5] },
                yaxis: { title: 'displacement' },
                shapes: [{
                    type: 'line',
                    x0: 0,
                    y0: -1,
                    x1: 0,
                    y1: 1,
                    line: { color: 'red', width: 2 }
                }]
            };

            let velocityLayout = {
                title: 'velocity',
                xaxis: { title: 'time t', range: [0, 5] },
                yaxis: { title: 'velocity' },
                shapes: [{
                    type: 'line',
                    x0: 0,
                    y0: -5,
                    x1: 0,
                    y1: 5,
                    line: { color: 'red', width: 2 }
                }]
            };

            let displacementData = [
                { x: timeData, y: displacementData[0], name: 'm1', line: { color: 'blue' } },
                { x: timeData, y: displacementData[1], name: 'm2', line: { color: 'orange' } },
                { x: timeData, y: displacementData[2], name: 'm3', line: { color: 'green' } }
            ];

            let velocityData = [
                { x: timeData, y: velocityData[0], name: 'm1', line: { color: 'blue' } },
                { x: timeData, y: velocityData[1], name: 'm2', line: { color: 'orange' } },
                { x: timeData, y: velocityData[2], name: 'm3', line: { color: 'green' } }
            ];

            Plotly.newPlot('displacement-plot-container', displacementData, displacementLayout);
            Plotly.newPlot('velocity-plot-container', velocityData, velocityLayout);
        }

        // Update plot lines
        function updatePlotLines() {
            let update = {
                'shapes[0].x0': t,
                'shapes[0].x1': t
            };

            Plotly.relayout('displacement-plot-container', update);
            Plotly.relayout('velocity-plot-container', update);
        }

        // Physics calculation
        function calculateSystem() {
            // Mass matrix
            let M = [[masses[0], 0, 0], [0, masses[1], 0], [0, 0, masses[2]]];

            // Stiffness matrix
            let K = [
                [2*T, -T, 0],
                [-T, 2*T, -T],
                [0, -T, 2*T]
            ];

            // Solve eigenvalue problem
            let M_inv = numeric.inv(M);
            let C = numeric.dot(numeric.dot(M_inv, K), numeric.identity(3));

            // Use numeric.eig for eigenvalue problem
            let result = numeric.eig(C);
            let eigenvalues = result.lambda.x;
            let eigenvectors = result.E.x;

            // Sort by eigenvalue
            let indices = Array.from({length: 3}, (_, i) => i);
            indices.sort((a, b) => eigenvalues[a] - eigenvalues[b]);

            omega = indices.map(i => Math.sqrt(eigenvalues[i]));
            modes = indices.map(i => eigenvectors[i]);

            // Calculate coefficients
            coefficients = [];
            for (let i = 0; i < 3; i++) {
                let sum = 0;
                for (let j = 0; j < 3; j++) {
                    sum += modes[i][j] * initialDisplacements[j];
                }
                coefficients.push(sum);
            }

            // Generate time data
            timeData = [];
            for (let i = 0; i <= 500; i++) {
                timeData.push(i * 0.01);
            }

            // Calculate displacement and velocity data
            displacementData = [[], [], []];
            velocityData = [[], [], []];

            for (let time of timeData) {
                let y = [0, 0, 0];
                let v = [0, 0, 0];

                for (let i = 0; i < 3; i++) {
                    for (let j = 0; j < 3; j++) {
                        y[i] += coefficients[j] * modes[j][i] * Math.cos(omega[j] * time);
                        v[i] -= coefficients[j] * modes[j][i] * omega[j] * Math.sin(omega[j] * time);
                    }
                }

                for (let i = 0; i < 3; i++) {
                    displacementData[i].push(y[i]);
                    velocityData[i].push(v[i]);
                }
            }

            initializePlots();
        }

        // Event listeners
        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isAnimating = !isAnimating;
            this.textContent = isAnimating ? '❚❚' : '▶';
            if (isAnimating) {
                p5Instance.loop();
            } else {
                p5Instance.noLoop();
            }
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            p5Instance.noLoop();
            t = 0;

            T = parseFloat(document.getElementById('slider-tension').value);
            masses = [
                parseFloat(document.getElementById('slider-m1').value),
                parseFloat(document.getElementById('slider-m2').value),
                parseFloat(document.getElementById('slider-m3').value)
            ];
            initialDisplacements = [
                parseFloat(document.getElementById('slider-x1').value),
                parseFloat(document.getElementById('slider-x2').value),
                parseFloat(document.getElementById('slider-x3').value)
            ];

            calculateSystem();
            p5Instance.redraw();
        });

        // Slider change listeners
        ['tension', 'x1', 'x2', 'x3', 'm1', 'm2', 'm3'].forEach(id => {
            document.getElementById(`slider-${id}`).addEventListener('input', function() {
                document.getElementById('btn-reset').click();
            });
        });

        // Initial setup
        window.addEventListener('load', function() {
            calculateSystem();
        });
    </script>
</body>
</html>
