            background: #bde0ff;
            border-color: #0078d7;
        }
        #canvas-container {
            border: 1px solid #ccc;
            background: #fff;
        }
        #explanation-panel {
            max-width: 700px;
            width: 100%;
            min-height: 60px;
            background: #fff;
            border: 1px solid #ccc;
            border-radius: 4px;
            padding: 10px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="header-controls">
            <button id="btn-move" class="active">move the points</button>
            <button id="btn-start-algo">start the algorithm</button>
        </div>

        <div id="step-controls">
            <div>
                <button disabled>step 1</button>
                <button id="btn-step1-explain" disabled>explain</button>
                <button id="btn-step1-do" disabled>do it</button>
            </div>
            <div>
                <button disabled>step 2</button>
                <button id="btn-step2-explain" disabled>explain</button>
                <button id="btn-step2-do" disabled>do it</button>
            </div>
            <div id="step3-controls-container">
                <button disabled>step 3</button>
                <button id="btn-step3-explain" disabled>explain</button>
                <div id="step3-actions" style="display: inline-block;"></div>
            </div>
        </div>

        <div id="canvas-container"></div>

        <div id="explanation-panel">
            <p id="explanation-text"></p>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let mode = 'move';
        let points = [];
        let draggedPoint = null;
        const algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        function setup() {
            const canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');
            // Initialize points
            points = [
                createVector(150, 350),
                createVector(250, 150),
                createVector(350, 250),
                createVector(550, 180),
                createVector(480, 320),
                createVector(420, 400)
            ];
        }

        function draw() {
            background(255);
            stroke(220);
            noFill();
            rect(0, 0, width, height);

            // Draw candidate lines
            stroke(60, 150, 140);
            strokeWeight(2);
            for (let [from, to] of algorithmState.candidateLines) {
                line(points[from].x, points[from].y, points[to].x, points[to].y);
            }

            // Draw hull edges
            stroke(45, 60, 110);
            strokeWeight(3);
            for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                let a = algorithmState.hullPointsIndices[i];
                let b = algorithmState.hullPointsIndices[i + 1];
                line(points[a].x, points[a].y, points[b].x, points[b].y);
            }
            // Close hull if complete
            if (algorithmState.fullHull.length && algorithmState.hullPointsIndices.length === algorithmState.fullHull.length) {
                let a = algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length - 1];
                let b = algorithmState.hullPointsIndices[0];
                line(points[a].x, points[a].y, points[b].x, points[b].y);
            }

            // Draw points
            stroke(0);
            for (let i = 0; i < points.length; i++) {
                if (algorithmState.hullPointsIndices.includes(i)) {
                    fill(45, 60, 110);
                } else {
                    fill(200, 220, 100);
                }
                ellipse(points[i].x, points[i].y, 12, 12);
                line(points[i].x - 4, points[i].y, points[i].x + 4, points[i].y);
                line(points[i].x, points[i].y - 4, points[i].x, points[i].y + 4);
            }
        }

        function mousePressed() {
            if (mode === 'move') {
                for (let i = 0; i < points.length; i++) {
                    if (dist(mouseX, mouseY, points[i].x, points[i].y) < 12) {
                        draggedPoint = i;
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (mode === 'move' && draggedPoint !== null) {
                points[draggedPoint].x = mouseX;
                points[draggedPoint].y = mouseY;
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }

        function jarvisMarch() {
            const n = points.length;
            if (n < 3) return [];

            const hull = [];
            let leftmost = 0;
            for (let i = 1; i < n; i++) {
                if (points[i].x < points[leftmost].x) leftmost = i;
            }

            let p = leftmost;
            let q;
            do {
                hull.push(p);
                q = (p + 1) % n;
                for (let i = 0; i < n; i++) {
                    const cross = (points[i].x - points[p].x) * (points[q].y - points[p].y) -
                                  (points[i].y - points[p].y) * (points[q].x - points[p].x);
                    if (cross < 0 || (cross === 0 && distSq(points[i], points[p]) > distSq(points[q], points[p]))) {
                        q = i;
                    }
                }
                p = q;
            } while (p !== leftmost);

            return hull;
        }

        function distSq(a, b) {
            return (a.x - b.x) ** 2 + (a.y - b.y) ** 2;
        }

        // UI handlers
        document.getElementById('btn-move').addEventListener('click', () => {
            mode = 'move';
            document.getElementById('btn-move').classList.add('active');
            document.getElementById('btn-start-algo').classList.remove('active');
            document.getElementById('step-controls').style.display = 'none';
            resetAlgorithmState();
        });

        document.getElementById('btn-start-algo').addEventListener('click', () => {
            mode = 'algorithm';
            document.getElementById('btn-start-algo').classList.add('active');
            document.getElementById('btn-move').classList.remove('active');
            document.getElementById('step-controls').style.display = 'flex';
            resetAlgorithmState();
            algorithmState.fullHull = jarvisMarch();
            enableStep1();
            populateStep3Buttons();
        });

        function resetAlgorithmState() {
            algorithmState.step = 0;
            algorithmState.hullPointsIndices = [];
            algorithmState.fullHull = [];
            algorithmState.leftmostPointIndex = null;
            algorithmState.currentPointIndex = null;
            algorithmState.candidateLines = [];
            algorithmState.explanationSteps = [];
            document.getElementById('explanation-text').textContent = '';
            disableAllStepButtons();
        }

        function enableStep1() {
            document.getElementById('btn-step1-explain').disabled = false;
            document.getElementById('btn-step1-do').disabled = false;
        }

        function enableStep2() {
            document.getElementById('btn-step2-explain').disabled = false;
            document.getElementById('btn-step2-do').disabled = false;
        }

        function enableStep3() {
            document.getElementById('btn-step3-explain').disabled = false;
            const firstBtn = document.getElementById('btn-step3-do');
            if (firstBtn) firstBtn.disabled = false;
        }

        function disableAllStepButtons() {
            document.getElementById('btn-step1-explain').disabled = true;
            document.getElementById('btn-step1-do').disabled = true;
            document.getElementById('btn-step2-explain').disabled = true;
            document.getElementById('btn-step2-do').disabled = true;
            document.getElementById('btn-step3-explain').disabled = true;
            document.getElementById('step3-actions').innerHTML = '';
        }

        function populateStep3Buttons() {
            const container = document.getElementById('step3-actions');
            container.innerHTML = '';
            const doBtn = document.createElement('button');
            doBtn.id = 'btn-step3-do';
            doBtn.textContent = 'do it';
            doBtn.addEventListener('click', handleStep3Do);
            container.appendChild(doBtn);

            for (let i = 0; i < algorithmState.fullHull.length - 2; i++) {
                const repeatBtn = document.createElement('button');
                repeatBtn.id = `btn-step3-repeat-${i}`;
                repeatBtn.textContent = 'repeat';
                repeatBtn.disabled = true;
                repeatBtn.addEventListener('click', () => handleStep3Repeat(i));
                container.appendChild(repeatBtn);
            }
        }

        // Step 1
        document.getElementById('btn-step1-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 1. Find the leftmost point.");
            updateExplanation();
        });

        document.getElementById('btn-step1-do').addEventListener('click', () => {
            let minIdx = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[minIdx].x) minIdx = i;
            }
            algorithmState.leftmostPointIndex = minIdx;
            algorithmState.currentPointIndex = minIdx;
            algorithmState.hullPointsIndices.push(minIdx);
            disableStep1Buttons();
            enableStep2();
        });

        function disableStep1Buttons() {
            document.getElementById('btn-step1-explain').disabled = true;
            document.getElementById('btn-step1-do').disabled = true;
        }

        // Step 2
        document.getElementById('btn-step2-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            updateExplanation();
        });

        document.getElementById('btn-step2-do').addEventListener('click', () => {
            const p = algorithmState.currentPointIndex;
            const nextIdx = algorithmState.fullHull[1];
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (i !== p) algorithmState.candidateLines.push([p, i]);
            }
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextIdx);
                algorithmState.currentPointIndex = nextIdx;
                disableStep2Buttons();
                enableStep3();
            }, 500);
        });

        function disableStep2Buttons() {
            document.getElementById('btn-step2-explain').disabled = true;
            document.getElementById('btn-step2-do').disabled = true;
        }

        // Step 3
        document.getElementById('btn-step3-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
            updateExplanation();
        });

        function handleStep3Do() {
            const nextIdx = algorithmState.fullHull[2];
            const p = algorithmState.currentPointIndex;
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (!algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([p, i]);
                }
            }
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextIdx);
                algorithmState.currentPointIndex = nextIdx;
                document.getElementById('btn-step3-do').disabled = true;
                const firstRepeat = document.getElementById('btn-step3-repeat-0');
                if (firstRepeat) firstRepeat.disabled = false;
            }, 500);
        }

        function handleStep3Repeat(index) {
            const nextIdx = algorithmState.fullHull[index + 3];
            const p = algorithmState.currentPointIndex;
            algorithmState.candidateLines = [];
            for (let i = 0; i < points.length; i++) {
                if (!algorithmState.hullPointsIndices.includes(i)) {
                    algorithmState.candidateLines.push([p, i]);
                }
            }
            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextIdx);
                algorithmState.currentPointIndex = nextIdx;
                document.getElementById(`btn-step3-repeat-${index}`).disabled = true;
                const nextRepeat = document.getElementById(`btn-step3-repeat-${index + 1}`);
                if (nextRepeat) nextRepeat.disabled = false;
            }, 500);
        }

        function updateExplanation() {
            document.getElementById('explanation-text').textContent = algorithmState.explanationSteps.join('\n');
        }
    </script>
</body>
</html>
