</head>
<body>
    <div id="container">
        <div id="controls">
            <details>
                <summary>initial conditions</summary>
                <p style="font-size: 12px; color: #666; margin-top: 10px;">
                    Initial values used in the simulation:<br>
                    • X(0) = 0.1 mol/m³<br>
                    • Glu(0) = 60 mol/m³<br>
                    • Eta(0) = 0 mol/m³<br>
                    • E0(0) = 1.0 mol/m³<br>
                    • E1(0) = 0.0 mol/m³<br>
                    • E2(0) = 0.0 mol/m³<br>
                    • V(0) = 1.0 m³
                </p>
            </details>

            <div class="slider-container">
                <label for="slider-alpha-max">maximum fraction of respiring biomass</label>
                <input type="range" id="slider-alpha-max" min="0.1" max="1.0" step="0.01" value="0.3">
                <div class="value-display" id="value-alpha-max">0.3</div>
            </div>

            <div class="slider-container">
                <label for="slider-t-min">run time minimum (h)</label>
                <input type="range" id="slider-t-min" min="0" max="0" step="1" value="0" disabled>
                <div class="value-display" id="value-t-min">0</div>
            </div>

            <div class="slider-container">
                <label for="slider-t-max">run time maximum (h)</label>
                <input type="range" id="slider-t-max" min="10" max="100" step="1" value="60">
                <div class="value-display" id="value-t-max">60</div>
            </div>

            <div class="slider-container">
                <label for="slider-enzyme-control">intermediate enzyme control [mol/m³]</label>
                <input type="range" id="slider-enzyme-control" min="0.1" max="5.0" step="0.01" value="1">
                <div class="value-display" id="value-enzyme-control">1.0</div>
            </div>

            <div class="slider-container">
                <label for="slider-feed-rate">feed flow rate (m³/h)</label>
                <input type="range" id="slider-feed-rate" min="0.0" max="2.0" step="0.01" value="0.2">
                <div class="value-display" id="value-feed-rate">0.2</div>
            </div>

            <div class="slider-container">
                <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³]</label>
                <input type="range" id="slider-substrate-feed-conc" min="10" max="200" step="1" value="100">
                <div class="value-display" id="value-substrate-feed-conc">100</div>
            </div>

            <div class="slider-container">
                <label for="slider-feed-time">feed time (h)</label>
                <input type="range" id="slider-feed-time" min="0" max="60" step="1" value="35">
                <div class="value-display" id="value-feed-time">35</div>
            </div>

            <div class="slider-container">
                <label for="select-plot">plot</label>
                <select id="select-plot">
                    <option value="fed batch" selected>fed batch</option>
                    <option value="α">α</option>
                </select>
            </div>
        </div>

        <div id="visualization">
            <div id="plot-top"></div>
            <div id="plot-bottom"></div>
        </div>
    </div>

    <script>
        // State variables to store simulation data
        let simulationData = null;
        let currentPlotType = 'fed batch';

        // Model parameters
        const n = 4; // Hill coefficient for sharp switching
        const mu_max = 0.4; // 1/h
        const Y_x = 0.5; // mol_X/mol_G
        const Y_e = 1.0; // mol_Eta/mol_G

        // Initial conditions
        const initialConditions = {
            X: 0.1,
            Glu: 60,
            Eta: 0,
            E0: 1.0,
            E1: 0.0,
            E2: 0.0,
            V: 1.0
        };

        // RK4 ODE solver
        function rk4(func, y0, t, params) {
            const h = t[1] - t[0];
            const yout = new Array(t.length);
            yout[0] = y0.slice();

            for (let i = 1; i < t.length; i++) {
                const k1 = func(yout[i-1], t[i-1], params);
                const k2 = func(addArrays(yout[i-1], scaleArray(k1, h/2)), t[i-1] + h/2, params);
                const k3 = func(addArrays(yout[i-1], scaleArray(k2, h/2)), t[i-1] + h/2, params);
                const k4 = func(addArrays(yout[i-1], scaleArray(k3, h)), t[i-1] + h, params);

                yout[i] = addArrays(yout[i-1], scaleArray(addArrays(...[
                    k1,
                    scaleArray(k2, 2),
                    scaleArray(k3, 2),
                    k4
                ]), h/6));
            }
            return yout;
        }

        function addArrays(...arrays) {
            const n = arrays[0].length;
            const result = new Array(n);
            for (let i = 0; i < n; i++) {
                result[i] = 0;
                for (const arr of arrays) {
                    result[i] += arr[i];
                }
            }
            return result;
        }

        function scaleArray(arr, k) {
            return arr.map(x => x * k);
        }

        // ODE system
        function diauxicModel(y, t, params) {
            const [X, Glu, Eta, E0, E1, E2, V] = y;
            const { alpha_max, K_I, F, G_in, t_feed } = params;

            // Calculate alpha (fraction of respiring biomass)
            const alpha = alpha_max * Math.pow(K_I, n) / (Math.pow(K_I, n) + Math.pow(Glu, n));

            // Check if feed is active
            const isFeeding = t <= t_feed && t > 0;

            // Growth rates
            const mu_rx = alpha * mu_max;
            const mu_fx = (1 - alpha) * mu_max * 2; // Fermentative multiplier
            const mu_e = (1 - alpha) * mu_max;

            // Enzyme dynamics
            const dE0 = 0; // Constant
            const dE1 = isFeeding ? G_in - 0.1 * E1 : 0.5 * Glu - 0.1 * E1; // Induced by glucose
            const dE2 = 0.05 * alpha - 0.1 * E2; // Repressed by glucose

            // Mass balances
            const dX = (mu_rx + mu_fx) * X - (isFeeding ? F/V : 0) * X;
            const dGlu = -mu_rx * X / Y_x - isFeeding * F * G_in / V + (isFeeding ? 0 : 0);
            const dEta = mu_e * X - (isFeeding ? F/V : 0) * Eta;

            return [dX, dGlu, dEta, dE0, dE1, dE2, isFeeding ? F : 0];
        }

        // Run simulation
        function runSimulation() {
            const t_min = parseFloat(document.getElementById('slider-t-min').value);
            const t_max = parseFloat(document.getElementById('slider-t-max').value);
            const alpha_max = parseFloat(document.getElementById('slider-alpha-max').value);
            const K_I = parseFloat(document.getElementById('slider-enzyme-control').value);
            const F = parseFloat(document.getElementById('slider-feed-rate').value);
            const G_in = parseFloat(document.getElementById('slider-substrate-feed-conc').value);
            const t_feed = parseFloat(document.getElementById('slider-feed-time').value);

            const dt = 0.1;
            const numSteps = Math.floor((t_max - t_min) / dt);
            const time = Array.from({length: numSteps + 1}, (_, i) => t_min + i * dt);

            const y0 = [
                initialConditions.X,
                initialConditions.Glu,
                initialConditions.Eta,
                initialConditions.E0,
                initialConditions.E1,
                initialConditions.E2,
                initialConditions.V
            ];

            const params = { alpha_max, K_I, F, G_in, t_feed };
            const result = rk4(diauxicModel, y0, time, params);

            // Calculate alpha values
            const alphaValues = result.map(y => {
                const Glu = y[1];
                return alpha_max * Math.pow(K_I, n) / (Math.pow(K_I, n) + Math.pow(Glu, n));
            });

            simulationData = {
                time,
                X: result.map(y => y[0]),
                Glu: result.map(y => y[1]),
                Eta: result.map(y => y[2]),
                E0: result.map(y => y[3]),
                E1: result.map(y => y[4]),
                E2: result.map(y => y[5]),
                V: result.map(y => y[6]),
                alpha: alphaValues
            };

            drawPlots();
        }

        // Draw plots
        function drawPlots() {
            if (!simulationData) return;

            const plotType = document.getElementById('select-plot').value;

            if (plotType === 'fed batch') {
                drawDiauxicBehavior();
                drawEnzymeConcentration();
            } else if (plotType === 'α') {
                drawGlucoseConcentration();
                drawAlphaPlot();
            }
        }

        function drawDiauxicBehavior() {
            const topLayout = {
                title: 'diauxic behavior',
                xaxis: { title: 'time (h)' },
                yaxis: { title: 'concentration [mol/m³]' },
                showlegend: true
            };

            const topData = [
                {
                    x: simulationData.time,
                    y: simulationData.X,
                    mode: 'lines',
                    name: 'X(t)',
                    line: { color: 'blue' }
                },
                {
                    x: simulationData.time,
                    y: simulationData.Glu,
                    mode: 'lines',
                    name: 'Glu(t)',
                    line: { color: 'red' }
                },
                {
                    x: simulationData.time,
                    y: simulationData.Eta,
                    mode: 'lines',
                    name: 'Eta(t)',
                    line: { color: 'green' }
                }
            ];

            Plotly.react('plot-top', topData, topLayout);
        }

        function drawEnzymeConcentration() {
            const bottomLayout = {
                title: 'enzyme concentration',
                xaxis: { title: 'time (h)' },
                yaxis: { title: 'enzyme concentration [mol/m³]' },
                showlegend: true
            };

            const bottomData = [
                {
                    x: simulationData.time,
                    y: simulationData.E2,
                    mode: 'lines',
                    name: 'E₂(t)',
                    line: { color: 'black' }
                },
                {
                    x: simulationData.time,
                    y: simulationData.E1,
                    mode: 'lines',
                    name: 'E₁(t)',
                    line: { color: 'magenta' }
                },
                {
                    x: simulationData.time,
                    y: simulationData.E0,
                    mode: 'lines',
                    name: 'E₀(t)',
                    line: { color: 'orange' }
                }
            ];

            Plotly.react('plot-bottom', bottomData, bottomLayout);
        }

        function drawGlucoseConcentration() {
            const topLayout = {
                title: 'glucose concentration',
                xaxis: { title: 'time (h)' },
                yaxis: { title: 'glucose concentration [mol/m³]' },
                showlegend: true
            };

            const topData = [{
                x: simulationData.time,
                y: simulationData.Glu,
                mode: 'lines',
                name: 'Glu(t)',
                line: { color: 'red' }
            }];

            Plotly.react('plot-top', topData, topLayout);
        }

        function drawAlphaPlot() {
            const bottomLayout = {
                title: 'fraction of respiring biomass',
                xaxis: { title: 'time (h)' },
                yaxis: { title: 'α' },
                showlegend: true
            };

            const bottomData = [{
                x: simulationData.time,
                y: simulationData.alpha,
                mode: 'lines',
                name: 'α',
                line: { color: 'blue' }
            }];

            Plotly.react('plot-bottom', bottomData, bottomLayout);
        }

        // Update slider values and dynamic constraints
        function updateSliderValues() {
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                const valueDisplay = document.getElementById('value-' + slider.id.slice(7));
                valueDisplay.textContent = parseFloat(slider.value).toFixed(slider.step < 1 ? 2 : 0);
            });

            // Update feed-time slider max based on t-max
            const tMaxSlider = document.getElementById('slider-t-max');
            const feedTimeSlider = document.getElementById('slider-feed-time');
            feedTimeSlider.max = tMaxSlider.value;

            if (parseFloat(feedTimeSlider.value) > parseFloat(feedTimeSlider.max)) {
                feedTimeSlider.value = feedTimeSlider.max;
                document.getElementById('value-feed-time').textContent = feedTimeSlider.max;
            }
        }

        // Event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', () => {
                updateSliderValues();
                runSimulation();
            });
        });

        document.getElementById('select-plot').addEventListener('change', drawPlots);

        // Initialization
        updateSliderValues();
        runSimulation();
    </script>
</body>
</html>
