    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let sun, earth, celestialSphere, sunLight;
        let sunPathLine, terminator, latitudeParallel, dayNightParallel;
        let constellationsGroup, sunPathGroup, groundGroup, spaceGroup;
        let isDirty = true;

        // State variables
        let currentDate = 79;
        let currentLatitude = 45;
        let currentTime = 12;
        let currentView = 'ground';

        // Astronomical constants
        const obliquity = 23.45 * Math.PI / 180;
        const dayOfYear = 365;

        // Initialize Three.js
        function initThree() {
            // Scene
            scene = new THREE.Scene();

            // Renderer
            renderer = new THREE.WebGLRenderer({ 
                canvas: document.getElementById('main-canvas'),
                antialias: true 
            });
            renderer.setSize(
                document.getElementById('visualization-container').clientWidth,
                document.getElementById('visualization-container').clientHeight
            );
            renderer.setClearColor(0x000011);

            // Camera
            camera = new THREE.PerspectiveCamera(
                75,
                document.getElementById('visualization-container').clientWidth / 
                document.getElementById('visualization-container').clientHeight,
                0.1,
                1000
            );
            camera.position.set(0, 1, 5);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            sunLight = new THREE.DirectionalLight(0xffffff, 1);
            sunLight.position.set(10, 0, 0);
            scene.add(sunLight);

            // Create scene objects
            createSun();
            createEarth();
            createConstellations();
            createGroundView();
            createSpaceView();

            // Hide space objects initially
            earth.visible = false;
            terminator.visible = false;
            latitudeParallel.visible = false;
            dayNightParallel.visible = false;
        }

        function createSun() {
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xffff00,
                emissive: 0xffff00 
            });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            sun.position.set(10, 0, 0);
            scene.add(sun);
        }

        function createEarth() {
            const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
            const earthMaterial = new THREE.MeshPhongMaterial({ 
                color: 0x2233ff,
                emissive: 0x112244,
                shininess: 10
            });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            earth.rotation.z = obliquity;
            earth.visible = false;
            scene.add(earth);

            // Create axis
            const axisGeometry = new THREE.CylinderGeometry(0.02, 0.02, 3);
            const axisMaterial = new THREE.MeshBasicMaterial({ color: 0x888888 });
            const axis = new THREE.Mesh(axisGeometry, axisMaterial);
            earth.add(axis);
        }

        function createConstellations() {
            constellationsGroup = new THREE.Group();

            // Sample constellation data (simplified - Big Dipper)
            const constellationPoints = [
                new THREE.Vector3(-10, 5, -5),
                new THREE.Vector3(-8, 6, -4),
                new THREE.Vector3(-6, 5, -3),
                new THREE.Vector3(-4, 6, -2),
                new THREE.Vector3(-2, 5, -1),
                new THREE.Vector3(0, 7, 0),
                new THREE.Vector3(2, 6, 1)
            ];

            const geometry = new THREE.BufferGeometry().setFromPoints(constellationPoints);
            const material = new THREE.LineBasicMaterial({ color: 0xffffff, opacity: 0.6, transparent: true });
            const dipper = new THREE.Line(geometry, material);
            constellationsGroup.add(dipper);
            scene.add(constellationsGroup);
        }

        function createGroundView() {
            const groundGeometry = new THREE.CylinderGeometry(3, 3, 0.1, 32);
            const groundMaterial = new THREE.MeshPhongMaterial({ color: 0x228B22 });
            const ground = new THREE.Mesh(groundGeometry, groundMaterial);
            ground.position.y = -0.05;
            scene.add(ground);

            // Celestial dome
            const domeGeometry = new THREE.SphereGeometry(8, 32, 32);
            const domeMaterial = new THREE.MeshPhongMaterial({ 
                color: 0x87CEEB, 
                transparent: true, 
                opacity: 0.3, 
                side: THREE.BackSide 
            });
            const dome = new THREE.Mesh(domeGeometry, domeMaterial);
            scene.add(dome);
        }

        function createSpaceView() {
            // Terminator
            const terminatorGeometry = new THREE.PlaneGeometry(10, 10);
            const terminatorMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x444444, 
                transparent: true, 
                opacity: 0.3, 
                side: THREE.DoubleSide 
            });
            terminator = new THREE.Mesh(terminatorGeometry, terminatorMaterial);
            scene.add(terminator);

            // Latitude parallel
            const parallelCurve = new THREE.EllipseCurve(0, 0, 
                1 * Math.cos(currentLatitude * Math.PI / 180), 
                1 * Math.cos(currentLatitude * Math.PI / 180), 
                0, 2 * Math.PI, false, 0);
            const parallelPoints = parallelCurve.getPoints(50);
            const parallelGeometry = new THREE.BufferGeometry().setFromPoints(parallelPoints);
            const parallelMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
            latitudeParallel = new THREE.Line(parallelGeometry, parallelMaterial);
            latitudeParallel.rotation.x = (90 - currentLatitude) * Math.PI / 180;
            scene.add(latitudeParallel);

            // Day/night on parallel (will be updated)
            const dnPoints = parallelPoints.slice(0, 25);
            const dnGeometry = new THREE.BufferGeometry().setFromPoints(dnPoints);
            const dnMaterial = new THREE.LineBasicMaterial({ color: 0xffff00 });
            dayNightParallel = new THREE.Line(dnGeometry, dnMaterial);
            dayNightParallel.rotation.x = (90 - currentLatitude) * Math.PI / 180;
            dayNightParallel.visible = false;
            scene.add(dayNightParallel);
        }

        // Utility functions
        function getDateString(day) {
            const date = new Date(2024, 0, day);
            const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 
                           'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
            return months[date.getMonth()] + ' ' + date.getDate();
        }

        function getTimeString(hours) {
            const h = Math.floor(hours);
            const m = Math.round((hours - h) * 60);
            return h.toString().padStart(2, '0') + ':' + m.toString().padStart(2, '0');
        }

        function calculateSunPosition(date, latitude, time) {
            // Simplified solar calculation
            const dayAngle = (date - 1) * 2 * Math.PI / dayOfYear;
            const solarDeclination = obliquity * Math.sin(dayAngle);

            const hourAngle = (time - 12) * 15 * Math.PI / 180;
            const latRad = latitude * Math.PI / 180;

            const elevation = Math.asin(
                Math.sin(latRad) * Math.sin(solarDeclination) + 
                Math.cos(latRad) * Math.cos(solarDeclination) * Math.cos(hourAngle)
            );

            const azimuth = Math.atan2(
                -Math.cos(solarDeclination) * Math.sin(hourAngle),
                Math.cos(latRad) * Math.sin(solarDeclination) - 
                Math.sin(latRad) * Math.cos(solarDeclination) * Math.cos(hourAngle)
            );

            return { declination: solarDeclination, elevation, azimuth };
        }

        function updatereadouts() {
            const day = currentDate;
            const lat = currentLatitude;

            // Simplified calculations for demo
            const dayAngle = (day - 1) * 2 * Math.PI / dayOfYear;
            const solarDeclination = obliquity * Math.sin(dayAngle);

            const cosHour = -Math.tan(lat * Math.PI / 180) * Math.tan(solarDeclination);
            const hourAngle = Math.acos(Math.min(1, Math.max(-1, cosHour)));

            const sunrise = 12 - (hourAngle * 180 / Math.PI) / 15;
            const sunset = 12 + (hourAngle * 180 / Math.PI) / 15;
            const dayLength = sunset - sunrise;

            document.getElementById('readout-day-length').textContent = getTimeString(dayLength);
            document.getElementById('readout-night-length').textContent = getTimeString(24 - dayLength);
            document.getElementById('readout-sunrise-time').textContent = getTimeString(sunrise);
            document.getElementById('readout-sunset-time').textContent = getTimeString(sunset);
            document.getElementById('readout-sunrise-direction').textContent = '90°';
            document.getElementById('readout-sunset-direction').textContent = '270°';
        }

        function updateSunPath() {
            if (sunPathLine) scene.remove(sunPathLine);

            const points = [];
            const steps = 50;
            const day = currentDate;
            const lat = currentLatitude;

            // Simplified calculations for visible sun path
            const dayAngle = (day - 1) * 2 * Math.PI / dayOfYear;
            const solarDeclination = obliquity * Math.sin(dayAngle);

            const cosHour = -Math.tan(lat * Math.PI / 180) * Math.tan(solarDeclination);
            const hourAngle = Math.acos(Math.min(1, Math.max(-1, cosHour)));

            for (let t = sunrise - 1; t <= sunset + 1; t += (sunset - sunrise + 2) / steps) {
                const hourAng = (t - 12) * 15 * Math.PI / 180;
                const elev = Math.asin(
                    Math.sin(lat * Math.PI / 180) * Math.sin(solarDeclination) + 
                    Math.cos(lat * Math.PI / 180) * Math.cos(solarDeclination) * Math.cos(hourAng)
                );
                const az = Math.atan2(
                    -Math.cos(solarDeclination) * Math.sin(hourAng),
                    Math.cos(lat * Math.PI / 180) * Math.sin(solarDeclination) - 
                    Math.sin(lat * Math.PI / 180) * Math.cos(solarDeclination) * Math.cos(hourAng)
                );

                const x = 2 * Math.cos(elev) * Math.sin(az);
                const y = 2 * Math.sin(elev);
                const z = 2 * Math.cos(elev) * Math.cos(az);
                points.push(new THREE.Vector3(x, y, z));
            }

            if (points.length > 1) {
                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0xffaa00 });
                sunPathLine = new THREE.Line(geometry, material);
                scene.add(sunPathLine);
            }
        }

        function updateScene() {
            const sunPos = calculateSunPosition(currentDate, currentLatitude, currentTime);

            if (currentView === 'ground') {
                // Ground view updates
                camera.position.set(0, 1, 5);
                controls.target.set(0, 0, 0);

                sun.position.x = 3 * Math.cos(sunPos.elevation) * Math.cos(sunPos.azimuth);
                sun.position.y = 3 * Math.sin(sunPos.elevation);
                sun.position.z = 3 * Math.cos(sunPos.elevation) * Math.sin(sunPos.azimuth);

                updateSunPath();
                sunPathLine.visible = document.getElementById('check-sun-path').checked;
                constellationsGroup.visible = document.getElementById('check-constellations-day').checked;

                earth.visible = false;
                terminator.visible = false;
                latitudeParallel.visible = false;
                dayNightParallel.visible = false;

            } else {
                // Space view updates
                camera.position.set(0, 15, 30);
                controls.target.set(0, 0, 0);

                // Earth rotation
                if (!document.getElementById('check-stationary-earth').checked) {
                    earth.rotation.y = (currentTime / 24) * 2 * Math.PI;
                }

                // Update terminator
                const earthSunDir = new THREE.Vector3(1, 0, 0);
                terminator.position.copy(earth.position);
                terminator.lookAt(earth.position.clone().add(earthSunDir));

                // Update latitude parallel
                if (latitudeParallel) {
                    scene.remove(latitudeParallel);
                }
                const parallelRadius = 1 * Math.cos(currentLatitude * Math.PI / 180);
                const parallelGeometry = new THREE.TorusGeometry(parallelRadius, 0.02, 8, 50);
                const parallelMaterial = new THREE.LineBasicMaterial({ color: 0xffffff });
                latitudeParallel = new THREE.Mesh(parallelGeometry, parallelMaterial);
                latitudeParallel.rotation.x = (90 - currentLatitude) * Math.PI / 180;
                earth.add(latitudeParallel);

                // Update day/night on parallel
                dayNightParallel.visible = document.getElementById('check-day-night-parallel').checked;

                // Show/hide objects
                earth.visible = true;
                terminator.visible = document.getElementById('check-day-night-division').checked;
                constellationsGroup.visible = document.getElementById('check-constellations').checked;
                if (sunPathLine) sunPathLine.visible = false;
            }

            isDirty = true;
        }

        // Event listeners
        document.getElementById('slider-date').addEventListener('input', function(e) {
            currentDate = parseInt(e.target.value);
            document.getElementById('label-date').textContent = getDateString(currentDate);
            updatereadouts();
            updateScene();
        });

        document.getElementById('slider-latitude').addEventListener('input', function(e) {
            currentLatitude = parseInt(e.target.value);
            document.getElementById('label-latitude').textContent = currentLatitude + '°';
            updatereadouts();
            updateScene();
        });

        document.getElementById('slider-time').addEventListener('input', function(e) {
            currentTime = parseFloat(e.target.value);
            document.getElementById('label-time').textContent = getTimeString(currentTime);
            updateScene();
        });

        document.getElementById('select-view').addEventListener('change', function(e) {
            currentView = e.target.value;

            document.getElementById('options-ground').style.display = 
                currentView === 'ground' ? 'block' : 'none';
            document.getElementById('options-space').style.display = 
                currentView === 'space' ? 'block' : 'none';

            updateScene();
        });

        // Checkbox listeners
        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', updateScene);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            if (isDirty) {
                renderer.render(scene, camera);
                isDirty = false;
            }
        }

        // Responsive design
        window.addEventListener('resize', function() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
            isDirty = true;
        });

        // Initialize
        initThree();
        updatereadouts();
        updateScene();
        animate();

        // Fix sun visibility
        let sunrise = 6;
        let sunset = 18;
        updateScene();
    </script>
</body>
</html>
