<body>
  <div id="main-container">
    <!-- Header -->
    <h1>Unsteady-State Diffusion in a Slab</h1>

    <p>
      This demonstration shows unsteady-state unidirectional diffusion of a solute into a slab of thickness 0.4 cm (0.004 m). The slab is initially at a linear concentration profile: <em>c</em>(<em>x</em>, 0) = 1×10<sup>–3</sup> + 250<em>x</em> (mol m<sup>–3</sup>). At time <em>t</em> = 0 the surfaces at <em>x</em> = 0 and <em>x</em> = 0.004 m are suddenly raised and held at constant concentration 4×10<sup>–3</sup> (mol m<sup>–3</sup>). The governing equation is Fick’s second law:
      ∂<em>c</em>/∂<em>t</em> = <em>D</em>∂²<em>c</em>/∂<em>x</em>² with boundary conditions <em>c</em>(0,<em>t</em>) = <em>c</em>(<em>L</em>,<em>t</em>) = 4×10<sup>–3</sup>.
    </p>

    <!-- Control Panel -->
    <div id="control-panel">
      <div class="control-row">
        <label for="time-slider">time (s)</label>
        <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
        <span id="time-value">100</span>
      </div>

      <div class="control-row">
        <label for="diffusivity-slider">diffusivity (m<sup>2</sup> s<sup>–1</sup>)</label>
        <input type="range" id="diffusivity-slider" min="-10" max="-8" step="0.01" value="-8.398">
        <span id="diffusivity-value">4.0 × 10<sup>–9</sup></span>
      </div>
    </div>

    <!-- Visualization Area -->
    <div id="plot-div"></div>
  </div>


  <script>
    // --- Constants & parameters ------------------------------------------------

    const L = 0.004;                 // slab thickness, m
    const c_s = 4.0e-3;              // surface concentration, mol/m³

    // --- DOM References -------------------------------------------------------

    const timeSlider = document.getElementById('time-slider');
    const timeValue  = document.getElementById('time-value');
    const diffSlider = document.getElementById('diffusivity-slider');
    const diffValue  = document.getElementById('diffusivity-value');
    const plotDiv    = document.getElementById('plot-div');

    let datarevision = 0;            // counter for efficient Plotly.react updates

    // --- Utility functions -----------------------------------------------------

    // Map slider 0-100 scale to scientific scale
    // Slider self-range becomes log scale
    function getDiffusivity() {
      const power = parseFloat(diffSlider.value);
      return Math.pow(10, power);
    }

    function formatDiffText(D) {
      const exp = Math.round(Math.log10(D));
      const mant = (D / Math.pow(10, exp)).toFixed(1);
      return `${mant} × 10<sup>${exp}</sup>`;
    }

    // --- Series solution -------------------------------------------------------

    // Pre-calculate wave numbers and coefficients
    const nMax = 100;
    const k_n  = new Array(nMax);
    const B_n  = new Array(nMax);

    for (let n = 0; n < nMax; n++) {
      k_n[n] = ((n + 0.5) * Math.PI) / L;
      // B_n of (c - c_s)
      B_n[n] = (2 / L) * 1.0e-3 *
               ( (250 * Math.pow(-1, n))  / (k_n[n] * k_n[n]) - 3.0 / k_n[n] );
    }

    function computeConcentration(xArray, t, D) {
      const c = [];
      for (let x of xArray) {
        let sum = 0;
        // Limit expansion length for performance
        const termsToUse = Math.min(nMax, t === 0 ? 0 : nMax);
        for (let n = 0; n < termsToUse; n++) {
          sum += B_n[n] * Math.sin(k_n[n] * x) *
                 Math.exp(-k_n[n] * k_n[n] * D * t);
        }
        c.push(sum + c_s);
      }
      return c;
    }

    // --- Plot routines ---------------------------------------------------------

    const layout = {
      margin: { l: 80, r: 20, t: 20, b: 50 },
      xaxis: {
        title: { text: 'position (m)' },
        range: [0, L],
        tickformat: '.4f',
        dtick: 0.001
      },
      yaxis: {
        title: { text: 'concentration (mol m⁻³)' },
        range: [0.0005, 0.0045],
        tickformat: '.4f',
        dtick: 0.0005
      },
      datarevision: datarevision
    };

    const trace = {
      x: [],
      y: [],
      mode: 'lines',
      name: 'concentration',
      line: { color: 'royalblue', width: 2.5 }
    };

    function updatePlot() {
      const t = parseFloat(timeSlider.value);
      const D = getDiffusivity();

      // Format displays
      timeValue.textContent = t.toFixed(0);
      diffValue.innerHTML  = formatDiffText(D);

      // Create x array
      const Nx = 200;
      const x = [];
      for (let i = 0; i < Nx; i++) {
        x.push((i * L) / (Nx - 1));
      }

      // Compute concentrations
      const y = computeConcentration(x, t, D);

      trace.x = x;
      trace.y = y;

      datarevision += 1;
      layout.datarevision = datarevision;

      Plotly.react(plotDiv, [trace], layout, {responsive: true});
    }

    // --- Event listeners -------------------------------------------------------

    timeSlider.addEventListener('input', updatePlot);
    diffSlider.addEventListener('input', updatePlot);

    // --- Initial render -------------------------------------------------------
    updatePlot();
  </script>
</body>
</html>
