<body>
    <div id="app-container">
        <div id="controls-container">
            <div id="function-controls">
                <span>function</span>
                <div class="btn-group">
                    <button class="function-btn active" data-function="x">x</button>
                    <button class="function-btn" data-function="squared">(x-3)²</button>
                    <button class="function-btn" data-function="cubic">(x-3)³+27</button>
                    <button class="function-btn" data-function="sqrt">√x</button>
                    <button class="function-btn" data-function="sine">sin(x)+1</button>
                    <button class="function-btn" data-function="exp">eˣ/³</button>
                </div>
            </div>
            <div id="type-controls">
                <span>type</span>
                <div class="btn-group">
                    <button class="type-btn" data-type="left">left</button>
                    <button class="type-btn active" data-type="right">right</button>
                    <button class="type-btn" data-type="midpoint">midpoint</button>
                    <button class="type-btn" data-type="overestimate">overestimate</button>
                    <button class="type-btn" data-type="underestimate">underestimate</button>
                    <button class="type-btn" data-type="trapezoidal">trapezoidal</button>
                </div>
            </div>
            <div id="slider-a-controls">
                <label for="slider-a">upper limit a</label>
                <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
                <span id="value-a">5.00</span>
            </div>
            <div id="slider-n-controls">
                <label for="slider-n">number of quadrilaterals</label>
                <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
                <span id="value-n">3</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Global state
        let state = {
            funcKey: 'x',
            type: 'right',
            a: 5,
            n: 3
        };

        // Function definitions
        const functions = {
            x: x => x,
            squared: x => Math.pow(x - 3, 2),
            cubic: x => Math.pow(x - 3, 3) + 27,
            sqrt: x => Math.sqrt(Math.max(x, 0)),
            sine: x => Math.sin(x) + 1,
            exp: x => Math.pow(Math.E, x / 3)
        };

        // Anti-derivatives (exact integrals)
        const antiderivatives = {
            x: x => x * x / 2,
            squared: x => (1/3) * Math.pow(x - 3, 3),
            cubic: x => Math.pow(x - 3, 4) / 4 + 27 * x,
            sqrt: x => (2/3) * Math.pow(x, 1.5),
            sine: x => -Math.cos(x) + x,
            exp: x => 3 * Math.exp(x / 3)
        };

        // p5.js sketch
        let sketch = function(p) {
            let canvasSize, margin, plotHeight, plotWidth;

            p.setup = function() {
                let canvas = p.createCanvas(p.min(800, p.windowWidth-20), p.min(600, p.windowHeight-200));
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = function() {
                p.background(248);
                margin = 70;
                plotWidth = p.width - 2 * margin;
                plotHeight = p.height - 2 * margin;

                // Calculate y range
                let xMin = -1, xMax = 8.5;
                let yMin = 0, yMax = 0;
                for (let x = xMin; x <= xMax; x += 0.1) {
                    let y = functions[state.funcKey](x);
                    yMin = Math.min(yMin, y);
                    yMax = Math.max(yMax, y);
                }
                let Y_PADDING = (yMax - yMin) * 0.1 || 1;
                yMin = Math.max(yMin - Y_PADDING, -5);
                yMax = yMax + Y_PADDING;

                // Mapping functions
                let mapX = x => margin + ((x - xMin) / (xMax - xMin)) * plotWidth;
                let mapY = y => p.height - margin - ((y - yMin) / (yMax - yMin)) * plotHeight;

                // Axes and grid
                p.strokeWeight(1);
                p.stroke(200);
                p.line(mapX(0), margin, mapX(0), p.height - margin);
                p.line(mapX(-1), mapY(0), mapX(8.5), mapY(0));
                // Tick marks
                for (let x = Math.ceil(xMin); x <= Math.floor(xMax); x++) {
                    p.line(mapX(x), mapY(0), mapX(x), mapY(0) + 5);
                    p.noStroke();
                    p.fill(100);
                    p.textAlign(p.CENTER);
                    p.text(x, mapX(x), mapY(0) + 20);
                }
                for (let y = Math.ceil(yMin); y <= Math.floor(yMax); y += (yMax - yMin) > 10 ? 1 : 0.5) {
                    p.line(mapX(0) - 5, mapY(y), mapX(0), mapY(y));
                    p.noStroke();
                    p.fill(100);
                    p.textAlign(p.RIGHT);
                    p.text(y.toFixed(1), mapX(0) - 10, mapY(y));
                }

                // Function curve
                p.strokeWeight(2);
                p.stroke(0);
                p.noFill();
                p.beginShape();
                for (let x = xMin; x <= xMax; x += 0.01) {
                    let y = functions[state.funcKey](x);
                    if (y >= yMin && y <= yMax) {
                        p.vertex(mapX(x), mapY(y));
                    }
                }
                p.endShape();

                // Area estimation shapes
                let deltaX = state.a / state.n;
                let estimatedArea = 0;
                let trueArea = antiderivatives[state.funcKey](state.a) - antiderivatives[state.funcKey](0);

                p.strokeWeight(0.5);
                p.stroke(80);
                for (let i = 0; i < state.n; i++) {
                    let leftX = i * deltaX;
                    let rightX = (i + 1) * deltaX;
                    let midX = (leftX + rightX) / 2;

                    let h_left = Math.max(functions[state.funcKey](leftX), 0);
                    let h_right = Math.max(functions[state.funcKey](rightX), 0);
                    let h_mid = Math.max(functions[state.funcKey](midX), 0);

                    let shapeArea = 0;
                    let fillColor;

                    switch (state.type) {
                        case 'left':
                            shapeArea = h_left * deltaX;
                            p.fill(shapeArea > deltaX * (antiderivatives[state.funcKey](rightX) - antiderivatives[state.funcKey](leftX)) / deltaX ? "rgba(173, 216, 230, 0.7)" : "rgba(144, 238, 144, 0.7)");
                            p.rect(mapX(leftX), mapY(h_left), mapX(rightX) - mapX(leftX), mapY(0) - mapY(h_left));
                            break;
                        case 'right':
                            shapeArea = h_right * deltaX;
                            p.fill(shapeArea > deltaX * (antiderivatives[state.funcKey](rightX) - antiderivatives[state.funcKey](leftX)) / deltaX ? "rgba(173, 216, 230, 0.7)" : "rgba(144, 238, 144, 0.7)");
                            p.rect(mapX(leftX), mapY(h_right), mapX(rightX) - mapX(leftX), mapY(0) - mapY(h_right));
                            break;
                        case 'midpoint':
                            shapeArea = h_mid * deltaX;
                            p.fill(shapeArea > deltaX * (antiderivatives[state.funcKey](rightX) - antiderivatives[state.funcKey](leftX)) / deltaX ? "rgba(173, 216, 230, 0.7)" : "rgba(144, 238, 144, 0.7)");
                            p.rect(mapX(leftX), mapY(h_mid), mapX(rightX) - mapX(leftX), mapY(0) - mapY(h_mid));
                            break;
                        case 'overestimate':
                            let h_over = 0;
                            for (let x = leftX; x <= rightX; x += deltaX/10) {
                                h_over = Math.max(h_over, Math.max(functions[state.funcKey](x), 0));
                            }
                            shapeArea = h_over * deltaX;
                            p.fill("rgba(173, 216, 230, 0.7)");
                            p.rect(mapX(leftX), mapY(h_over), mapX(rightX) - mapX(leftX), mapY(0) - mapY(h_over));
                            break;
                        case 'underestimate':
                            let h_under = Infinity;
                            for (let x = leftX; x <= rightX; x += deltaX/10) {
                                let y = Math.max(functions[state.funcKey](x), 0);
                                if (y < h_under) h_under = y;
                            }
                            if (h_under === Infinity) h_under = 0;
                            shapeArea = h_under * deltaX;
                            p.fill("rgba(144, 238, 144, 0.7)");
                            p.rect(mapX(leftX), mapY(h_under), mapX(rightX) - mapX(leftX), mapY(0) - mapY(h_under));
                            break;
                        case 'trapezoidal':
                            let leftY = Math.max(functions[state.funcKey](leftX), 0);
                            let rightY = Math.max(functions[state.funcKey](rightX), 0);
                            shapeArea = (leftY + rightY) * deltaX / 2;
                            p.fill(shapeArea > deltaX * (antiderivatives[state.funcKey](rightX) - antiderivatives[state.funcKey](leftX)) / deltaX ? "rgba(173, 216, 230, 0.7)" : "rgba(144, 238, 144, 0.7)");
                            p.quad(mapX(leftX), mapY(0), mapX(leftX), mapY(leftY), mapX(rightX), mapY(rightY), mapX(rightX), mapY(0));
                            break;
                    }
                    estimatedArea += shapeArea;
                }

                // Vertical line at x=a
                p.strokeWeight(2);
                p.stroke(255, 0, 0);
                p.line(mapX(state.a), margin, mapX(state.a), p.height - margin);

                // Text overlay
                p.noStroke();
                p.textAlign(p.LEFT);
                p.fill(0);
                p.textSize(14);
                p.text(`estimated area: ${estimatedArea.toFixed(5)}`, 20, 30);
                p.text(`integral: ${trueArea.toFixed(5)}`, 20, 50);
            };
        };

        // Initialize p5.js
        let myp5 = new p5(sketch);

        // Event listeners
        document.querySelectorAll('.function-btn').forEach(btn => {
            btn.addEventListener('click', (e) => {
                document.querySelectorAll('.function-btn').forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');
                state.funcKey = e.target.dataset.function;
                myp5.redraw();
            });
        });

        document.querySelectorAll('.type-btn').forEach(btn => {
            btn.addEventListener('click', (e) => {
                document.querySelectorAll('.type-btn').forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');
                state.type = e.target.dataset.type;
                myp5.redraw();
            });
        });

        document.getElementById('slider-a').addEventListener('input', (e) => {
            state.a = parseFloat(e.target.value);
            document.getElementById('value-a').textContent = state.a.toFixed(2);
            myp5.redraw();
        });

        document.getElementById('slider-n').addEventListener('input', (e) => {
            state.n = parseInt(e.target.value);
            document.getElementById('value-n').textContent = state.n;
            myp5.redraw();
        });

        // Initial setup
        document.getElementById('value-a').textContent = state.a.toFixed(2);
    </script>
</body>
</html>
