            <button id="btn-reset" title="Reset">↻</button>
            <div class="slider-group">
                <label for="slider-current">current (A)</label>
                <input type="range" id="slider-current" min="0" max="1" step="0.001" value="0.431">
                <span id="value-current">0.431</span>
            </div>
            <div class="slider-group">
                <label for="slider-earth-field">horizontal Earth field (T)</label>
                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field">1.94e-5</span>
            </div>
            <div class="slider-group">
                <label for="slider-radius">radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius">0.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-turns">number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns">25</span>
            </div>
        </div>
        <div id="viz-container">
            <div id="p5-canvas"></div>
        </div>
    </div>

    <script>
        // Global parameters
        let params = {
            current: 0.431,
            earthField: 0.0000194,
            radius: 0.5,
            turns: 25
        };

        // p5.js sketch
        let sketch = function(p) {
            const mu0 = 4 * Math.PI * 1e-7;

            // Elliptic integrals using AGM
            function ellipticK(m) {
                if (m < 0 || m >= 1) return 0;
                let a = 1, b = Math.sqrt(1 - m), c = m;
                while (Math.abs(a - b) > 1e-12) {
                    let temp = (a + b) / 2;
                    b = Math.sqrt(a * b);
                    a = temp;
                }
                return Math.PI / (2 * a);
            }

            function ellipticE(m) {
                if (m < 0 || m >= 1) return 0;
                let a = 1, b = Math.sqrt(1 - m), c = m;
                let sum = 0;
                while (Math.abs(a - b) > 1e-12) {
                    sum += c * c;
                    let temp = (a + b) / 2;
                    c = (a - b) / 2;
                    b = Math.sqrt(a * b);
                    a = temp;
                }
                return (Math.PI / (2 * a)) * (1 - sum / 2);
            }

            // Magnetic field from a single loop
            function B_loop(y, z, R, I, N, z_offset) {
                const zp = z - z_offset;
                const denom = (R + y) ** 2 + zp ** 2;
                if (denom === 0) return { By: 0, Bz: 0 };
                const m = (4 * R * y) / denom;
                const K = ellipticK(m);
                const E = ellipticE(m);
                const common1 = (mu0 * I * N) / (2 * Math.PI);
                const common2 = 1 / Math.sqrt(denom);

                let By, Bz;
                if (y === 0) {
                    By = 0;
                    Bz = (mu0 * I * N * R ** 2) / (2 * ((R ** 2 + zp ** 2) ** 1.5));
                } else {
                    By = common1 * (zp / y) * common2 * (((R ** 2 + y ** 2 + zp ** 2) / ((R - y) ** 2 + zp ** 2)) * E - K);
                    Bz = common1 * common2 * (K - ((R ** 2 - y ** 2 - zp ** 2) / ((R - y) ** 2 + zp ** 2)) * E);
                }
                return { By, Bz };
            }

            // Total magnetic field
            function B_total(y, z) {
                const B1 = B_loop(y, z, params.radius, params.current, params.turns, -params.radius / 2);
                const B2 = B_loop(y, z, params.radius, params.current, params.turns, params.radius / 2);
                const By = B1.By + B2.By;
                const Bz = B1.Bz + B2.Bz + params.earthField;
                return { By, Bz, mag: Math.sqrt(By ** 2 + Bz ** 2) };
            }

            let minMag = 0, maxMag = 1;

            p.setup = function() {
                const canvas = p.createCanvas(p.windowWidth * 0.8, p.windowHeight * 0.8);
                canvas.parent('p5-canvas');
                p.colorMode(p.HSB, 360, 100, 100, 255);
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);
                p.translate(p.width / 2, p.height / 2);
                p.scale(1, -1);

                // Calculate magnitude range
                const gridSize = 25;
                const worldMin = -0.5, worldMax = 0.5;
                const step = (worldMax - worldMin) / gridSize;
                let magnitudes = [];
                for (let i = 0; i <= gridSize; i++) {
                    for (let j = 0; j <= gridSize; j++) {
                        const y = worldMin + i * step;
                        const z = worldMin + j * step;
                        const B = B_total(y, z);
                        magnitudes.push(B.mag);
                    }
                }
                minMag = Math.min(...magnitudes);
                maxMag = Math.max(...magnitudes);

                // Draw streamlines
                const pixelScale = p.width / (worldMax - worldMin);
                for (let i = 0; i <= gridSize; i++) {
                    for (let j = 0; j <= gridSize; j++) {
                        const y0 = worldMin + i * step;
                        const z0 = worldMin + j * step;
                        drawStreamline(y0, z0, pixelScale, worldMin, worldMax);
                    }
                }

                // Draw axes
                p.stroke(255, 0, 0);
                p.strokeWeight(1);
                p.line(-p.width / 2, 0, p.width / 2, 0); // Y axis
                p.line(0, -p.height / 2, 0, p.height / 2); // Z axis

                // Ticks and labels
                p.fill(255, 0, 0);
                p.stroke(255, 0, 0);
                p.textSize(10);
                p.textAlign(p.CENTER, p.CENTER);
                for (let t = -0.5; t <= 0.5; t += 0.2) {
                    const x = t * pixelScale;
                    const y = t * pixelScale;
                    p.line(x, -5, x, 5);
                    p.line(-5, y, 5, y);
                    p.push();
                    p.scale(1, -1);
                    p.text(t.toFixed(1), x, 15);
                    p.text(t.toFixed(1), 15, -y);
                    p.pop();
                }
            };

            function drawStreamline(y0, z0, pixelScale, worldMin, worldMax) {
                const stepSize = 0.01;
                const maxSteps = 100;
                const arrowInterval = 10;

                function drawSegment(y, z, dir) {
                    const B = B_total(y, z);
                    if (B.mag === 0) return null;
                    const hue = p.map(B.mag, minMag, maxMag, 270, 60);
                    p.stroke(hue, 100, 100);
                    p.strokeWeight(1);

                    const dy = dir * stepSize * B.By / B.mag;
                    const dz = dir * stepSize * B.z / B.mag;
                    const y1 = y + dy;
                    const z1 = z + dz;

                    if (y1 < worldMin || y1 > worldMax || z1 < worldMin || z1 > worldMax) return null;

                    p.line(y * pixelScale, z * pixelScale, y1 * pixelScale, z1 * pixelScale);
                    return { y: y1, z: z1 };
                }

                // Forward
                let y = y0, z = z0;
                for (let k = 0; k < maxSteps; k++) {
                    const next = drawSegment(y, z, 1);
                    if (!next) break;
                    y = next.y;
                    z = next.z;
                    if (k % arrowInterval === 0) drawArrow(y, z, 1, pixelScale);
                }

                // Backward
                y = y0; z = z0;
                for (let k = 0; k < maxSteps; k++) {
                    const next = drawSegment(y, z, -1);
                    if (!next) break;
                    y = next.y;
                    z = next.z;
                    if (k % arrowInterval === 0) drawArrow(y, z, -1, pixelScale);
                }
            }

            function drawArrow(y, z, dir, pixelScale) {
                const B = B_total(y, z);
                if (B.mag === 0) return;
                const angle = Math.atan2(B.z, B.By);
                const arrowSize = 0.005 * pixelScale;
                const x = y * pixelScale;
                const yp = z * pixelScale;

                p.push();
                p.translate(x, yp);
                p.rotate(angle);
                p.line(0, 0, dir * arrowSize, -arrowSize / 2);
                p.line(0, 0, dir * arrowSize, arrowSize / 2);
                p.pop();
            }

            p.windowResized = function() {
                p.resizeCanvas(p.windowWidth * 0.8, p.windowHeight * 0.8);
                p.redraw();
            };
        };

        new p5(sketch);

        // Slider event listeners
        function updateSlider(id, valueId, value, format = x => x) {
            document.getElementById(valueId).textContent = format(value);
            params[id.replace('slider-', '')] = parseFloat(value);
            window.p5Instance.redraw();
        }

        document.getElementById('slider-current').addEventListener('input', e => {
            updateSlider('slider-current', 'value-current', e.target.value, x => parseFloat(x).toFixed(3));
        });

        document.getElementById('slider-earth-field').addEventListener('input', e => {
            updateSlider('slider-earth-field', 'value-earth-field', e.target.value, x => parseFloat(x).toExponential(2));
        });

        document.getElementById('slider-radius').addEventListener('input', e => {
            updateSlider('slider-radius', 'value-radius', e.target.value, x => parseFloat(x).toFixed(2));
        });

        document.getElementById('slider-turns').addEventListener('input', e => {
            updateSlider('slider-turns', 'value-turns', e.target.value, x => parseInt(x));
        });

        // Reset button
        document.getElementById('btn-reset').addEventListener('click', () => {
            const initial = {
                current: 0.431,
                earthField: 0.0000194,
                radius: 0.5,
                turns: 25
            };
            document.getElementById('slider-current').value = initial.current;
            document.getElementById('value-current').textContent = initial.current.toFixed(3);
            document.getElementById('slider-earth-field').value = initial.earthField;
            document.getElementById('value-earth-field').textContent = initial.earthField.toExponential(2);
            document.getElementById('slider-radius').value = initial.radius;
            document.getElementById('value-radius').textContent = initial.radius.toFixed(2);
            document.getElementById('slider-turns').value = initial.turns;
            document.getElementById('value-turns').textContent = initial.turns;
            params = { ...initial };
            window.p5Instance.redraw();
        });
    </script>
</body>
</html>
