<body>
    <h1>Applying Graphics Filters Using Convolution</h1>
    <p class="description">Demo of image processing using convolution kernels. Adjust the controls to see different effects on the letter image.</p>

    <div id="control-panel">
        <div class="control-group">
            <label for="slider-convolver">convolver</label>
            <input type="range" id="slider-convolver" min="1" max="5" value="2" step="1">
            <span id="convolver-value">2</span>
        </div>

        <div class="control-group">
            <label for="slider-image-size">image size</label>
            <input type="range" id="slider-image-size" min="10" max="40" value="24" step="1">
            <span id="image-size-value">24</span>
        </div>

        <div class="control-group">
            <label for="input-letter">pick a letter:</label>
            <input type="text" id="input-letter" maxlength="1" value="a">
        </div>

        <div class="control-group">
            <label>filter</label>
            <div class="filter-group">
                <input type="radio" name="filter" id="radio-shadow" value="shadow" checked>
                <label for="radio-shadow">shadow</label>
                <input type="radio" name="filter" id="radio-blur" value="blur">
                <label for="radio-blur">blur</label>
            </div>
        </div>
    </div>

    <div id="visualization-area">
        <div id="canvas-container"></div>
    </div>

    <script>
        let sketch = function(p) {
            let convolverSlider = document.getElementById('slider-convolver');
            let imageSizeSlider = document.getElementById('slider-image-size');
            let inputLetter = document.getElementById('input-letter');
            let shadowRadio = document.getElementById('radio-shadow');
            let blurRadio = document.getElementById('radio-blur');

            let beforeImg = [];
            let afterImg = [];
            let pixelSize = 0;
            let letter = 'a';
            let filterType = 'shadow';

            p.setup = function() {
                p.createCanvas(420, 210).parent('canvas-container');
                p.textAlign(p.CENTER, p.CENTER);

                // Initialize controls
                updateValues();

                // Add event listeners
                convolverSlider.addEventListener('input', updateValues);
                imageSizeSlider.addEventListener('input', updateValues);
                inputLetter.addEventListener('input', updateValues);
                shadowRadio.addEventListener('change', updateValues);
                blurRadio.addEventListener('change', updateValues);
            };

            p.draw = function() {
                p.background(255);

                let gridSize = parseInt(imageSizeSlider.value);
                pixelSize = 200 / gridSize;

                // Clear arrays
                beforeImg = [];
                afterImg = [];

                // Create before image
                for (let i = 0; i < gridSize; i++) {
                    beforeImg[i] = [];
                    for (let j = 0; j < gridSize; j++) {
                        beforeImg[i][j] = 0; // White background
                    }
                }

                // Render letter
                p.push();
                p.translate(100, 105);
                p.fill(0);
                p.noStroke();
                p.textSize(pixelSize * gridSize * 0.8);
                p.text(letter, 0, 0);
                p.pop();

                // Read pixels from canvas to beforeImg
                p.loadPixels();
                for (let i = 0; i < gridSize; i++) {
                    for (let j = 0; j < gridSize; j++) {
                        let x = int(i * pixelSize) + 100 - gridSize/2;
                        let y = int(j * pixelSize) + 105 - gridSize/2;
                        let index = (y * p.width + x) * 4;
                        let brightness = (p.pixels[index] + p.pixels[index + 1] + p.pixels[index + 2]) / 3;
                        beforeImg[j][i] = brightness < 128 ? 1 : 0;
                    }
                }

                // Generate kernel
                let k = parseInt(convolverSlider.value);
                let kernel = [];
                let kernelSize = 2 * k + 1;

                if (filterType === 'blur') {
                    // Blur kernel
                    let val = 1.0 / (kernelSize * kernelSize);
                    for (let i = 0; i < kernelSize; i++) {
                        kernel[i] = [];
                        for (let j = 0; j < kernelSize; j++) {
                            kernel[i][j] = val;
                        }
                    }
                } else {
                    // Shadow kernel
                    for (let i = 0; i < kernelSize; i++) {
                        kernel[i] = [];
                        for (let j = 0; j < kernelSize; j++) {
                            kernel[i][j] = 0;
                        }
                    }

                    // Create shadow offset
                    let offset = 1;
                    let shadowK = Math.max(1, k - offset);
                    for (let i = offset; i < offset + shadowK; i++) {
                        for (let j = offset; j < offset + shadowK; j++) {
                            if (i < kernelSize && j < kernelSize) {
                                kernel[i][j] = 1.0 / (shadowK * shadowK);
                            }
                        }
                    }
                }

                // Apply convolution
                for (let i = 0; i < gridSize; i++) {
                    afterImg[i] = [];
                    for (let j = 0; j < gridSize; j++) {
                        let sum = 0.0;
                        for (let ki = -k; ki <= k; ki++) {
                            for (let kj = -k; kj <= k; kj++) {
                                let src = 0.0;
                                let x = i + ki;
                                let y = j + kj;
                                if (x >= 0 && x < gridSize && y >= 0 && y < gridSize) {
                                    src = beforeImg[x][y];
                                }
                                sum += src * kernel[ki + k][kj + k];
                            }
                        }
                        afterImg[i][j] = p.constrain(sum, 0, 1);
                    }
                }

                // Draw before image
                p.push();
                p.translate(50, 5);
                p.noStroke();
                for (let i = 0; i < gridSize; i++) {
                    for (let j = 0; j < gridSize; j++) {
                        let brightness = 1 - beforeImg[i][j];
                        p.fill(brightness * 255);
                        p.rect(i * pixelSize, j * pixelSize, pixelSize, pixelSize);
                    }
                }
                p.pop();

                // Draw after image
                p.push();
                p.translate(260, 5);
                p.noStroke();
                for (let i = 0; i < gridSize; i++) {
                    for (let j = 0; j < gridSize; j++) {
                        let brightness = 1 - afterImg[i][j];
                        p.fill(brightness * 255);
                        p.rect(i * pixelSize, j * pixelSize, pixelSize, pixelSize);
                    }
                }
                p.pop();

                // Labels
                p.fill(0);
                p.textAlign(p.LEFT, p.TOP);
                p.textSize(14);
                p.text("before", 50, 5);
                p.text("after", 260, 5);
            };

            function updateValues() {
                let val = convolverSlider.value;
                document.getElementById('convolver-value').textContent = val;

                val = imageSizeSlider.value;
                document.getElementById('image-size-value').textContent = val;

                letter = inputLetter.value.toLowerCase();
                if (letter.length === 0) letter = 'a';

                filterType = document.querySelector('input[name="filter"]:checked').value;

                p.redraw();
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
