                <select id="select-residue"></select>
            </div>

            <div id="shared-controls" class="control-group">
                <label for="slider-residue-x">jump to atom x</label>
                <input type="range" id="slider-residue-x" min="1" max="400" step="1" value="50">
                <span id="residue-x-value" class="value-display">50</span>

                <label for="slider-residue-y">jump to atom y</label>
                <input type="range" id="slider-residue-y" min="1" max="400" step="1" value="52">
                <span id="residue-y-value" class="value-display">52</span>

                <label for="slider-rmsd">RMSD</label>
