                <input type="range" id="slider-plot-size" min="200" max="800" step="1" value="400">
                <span id="plot-size-value" class="value-display">400</span>
            </div>
        </div>

        <div id="visualization-area">
            <button id="btn-reset">+</button>
            <p id="info-display">Res A: ASN1 | Res X: CYS50 | Res Y: PHE52</p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/controls/OrbitControls.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // Global variables
        let proteinData = [];
        let currentView = 'crystallography';
        let scene, camera, renderer, controls;
        let atomMeshes = [];
        let p5Instance;
        let distanceMatrix = [];

        // Amino acid codes
        const aminoAcids = ['A', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'Y'];

        // Generate protein data
        function generateProteinData(numResidues) {
            const data = [];
            let x = 0, y = 0, z = 0;

            for (let i = 1; i <= numResidues; i++) {
                const resName = aminoAcids[Math.floor(Math.random() * aminoAcids.length)];
                const atoms = [];

                // CA atom
                if (i > 1) {
                    x += (Math.random() - 0.5) * 4;
                    y += (Math.random() - 0.5) * 4;
                    z += (Math.random() - 0.5) * 4;
                }
                atoms.push({ atomName: 'CA', x, y, z });

                // Other atoms
                atoms.push({ atomName: 'N', x: x - 1.5, y: y - 1.5, z: z });
                atoms.push({ atomName: 'C', x: x + 1.5, y: y - 1.5, z: z });
                atoms.push({ atomName: 'O', x: x + 2.5, y: y - 0.5, z: z });
                atoms.push({ atomName: 'CB', x: x, y: y + 1.5, z: z + 1.5 });

                data.push({ resName, resSeq: i, atoms });
            }

            return data;
        }

        // Initialize Three.js scene
        function initThreeJS() {
            const container = document.getElementById('canvas-container');
            const size = parseInt(document.getElementById('slider-plot-size').value);

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(50, 50, 100);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(size, size);
            renderer.setClearColor(0xffffff);
            container.innerHTML = '';
            container.appendChild(renderer.domElement);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            drawProteinStructure();
            animate();
        }

        // Draw protein structure in 3D
        function drawProteinStructure() {
            // Clear existing meshes
            atomMeshes.forEach(mesh => scene.remove(mesh));
            atomMeshes = [];

            const showLateralChains = document.getElementById('checkbox-lateral-chains').checked;
            const grayLevel = parseFloat(document.getElementById('slider-gray-level').value);
            const resA = parseInt(document.getElementById('slider-browse-sequence').value);
            const resX = parseInt(document.getElementById('slider-residue-x').value);
            const resY = parseInt(document.getElementById('slider-residue-y').value);

            proteinData.forEach((residue, index) => {
                const resIndex = index + 1;

                residue.atoms.forEach(atom => {
                    if (!showLateralChains && atom.atomName !== 'CA') return;

                    const geometry = new THREE.SphereGeometry(0.5, 16, 16);
                    let color = new THREE.Color(0, 0, grayLevel);

                    if (resIndex === resA) {
                        color = new THREE.Color(0xffff00);
                    } else if ((resIndex === resX || resIndex === resY) && atom.atomName === 'CA') {
                        color = new THREE.Color(0xff0000);
                    }

                    const material = new THREE.MeshLambertMaterial({ color });
                    const sphere = new THREE.Mesh(geometry, material);
                    sphere.position.set(atom.x, atom.y, atom.z);

                    scene.add(sphere);
                    atomMeshes.push(sphere);
                });
            });

            // Add bounding box
            const box = new THREE.Box3().setFromObject(scene);
            const boxHelper = new THREE.BoxHelper(scene, 0x888888);
            scene.add(boxHelper);
        }

        // Initialize p5.js for contact map
        function initP5() {
            const container = document.getElementById('canvas-container');
            const size = parseInt(document.getElementById('slider-plot-size').value);

            if (p5Instance) {
                p5Instance.remove();
            }

            p5Instance = new p5(sketch => {
                sketch.setup = function() {
                    const canvas = sketch.createCanvas(size, size);
                    canvas.parent('canvas-container');
                    sketch.noLoop();
                };

                sketch.draw = function() {
                    sketch.background(255);

                    const resX = parseInt(document.getElementById('slider-residue-x').value);
                    const resY = parseInt(document.getElementById('slider-residue-y').value);
                    const rmsd = parseFloat(document.getElementById('slider-rmsd').value);

                    // Draw axes
                    sketch.stroke(200);
                    sketch.line(50, size - 50, size - 50, size - 50);
                    sketch.line(50, 50, 50, size - 50);

                    // Draw ticks and labels
                    sketch.fill(0);
                    sketch.textSize(10);
                    for (let i = 1; i <= 4; i++) {
                        const pos = 50 + (i - 1) * (size - 100) / 3;
                        sketch.text(i * 100, pos - 5, size - 35);
                        sketch.text(i * 100, 35, size - pos + 5);
                    }

                    // Draw contact map
                    sketch.stroke(0);
                    sketch.strokeWeight(2);

                    for (let i = 0; i < proteinData.length; i++) {
                        for (let j = 0; j < proteinData.length; j++) {
                            const ca1 = proteinData[i].atoms.find(a => a.atomName === 'CA');
                            const ca2 = proteinData[j].atoms.find(a => a.atomName === 'CA');

                            const dist = Math.sqrt(
                                Math.pow(ca1.x - ca2.x, 2) +
                                Math.pow(ca1.y - ca2.y, 2) +
                                Math.pow(ca1.z - ca2.z, 2)
                            );

                            if (dist <= rmsd) {
                                const x = 50 + (i / 399) * (size - 100);
                                const y = size - 50 - (j / 399) * (size - 100);
                                sketch.point(x, y);
                            }
                        }
                    }

                    // Draw red lines
                    sketch.stroke(255, 0, 0);
                    sketch.strokeWeight(1);
                    const xLine = 50 + (resX - 1) / 399 * (size - 100);
                    const yLine = size - 50 - (resY - 1) / 399 * (size - 100);
                    sketch.line(xLine, 50, xLine, size - 50);
                    sketch.line(50, yLine, size - 50, yLine);
                };
            });
        }

        // Animation loop for Three.js
        function animate() {
            if (currentView === 'crystallography') {
                requestAnimationFrame(animate);
                controls.update();
                renderer.render(scene, camera);
            }
        }

        // Update info display
        function updateInfoDisplay() {
            const resA = parseInt(document.getElementById('slider-browse-sequence').value);
            const resX = parseInt(document.getElementById('slider-residue-x').value);
            const resY = parseInt(document.getElementById('slider-residue-y').value);

            const typeA = proteinData[resA - 1].resName;
            const typeX = proteinData[resX - 1].resName;
            const typeY = proteinData[resY - 1].resName;

            document.getElementById('info-display').textContent = 
                `Res A: ${typeA}${resA} | Res X: ${typeX}${resX} | Res Y: ${typeY}${resY}`;
        }

        // Switch view
        function switchView(view) {
            currentView = view;

            document.getElementById('btn-crystallography').classList.toggle('active', view === 'crystallography');
            document.getElementById('btn-contact-map').classList.toggle('active', view === 'contact-map');

            if (view === 'crystallography') {
                if (p5Instance) p5Instance.remove();
                initThreeJS();
            } else {
                if (renderer) {
                    renderer.domElement.remove();
                }
                initP5();
                p5Instance.redraw();
            }
        }

        // Initialize
        document.addEventListener('DOMContentLoaded', function() {
            proteinData = generateProteinData(400);

            // Populate select residue dropdown
            const selectResidue = document.getElementById('select-residue');
            aminoAcids.forEach(aa => {
                const option = document.createElement('option');
                option.value = aa;
                option.textContent = aa;
                selectResidue.appendChild(option);
            });

            // Set initial view
            switchView('crystallography');
            updateInfoDisplay();

            // Event listeners
            document.getElementById('btn-crystallography').addEventListener('click', () => switchView('crystallography'));
            document.getElementById('btn-contact-map').addEventListener('click', () => switchView('contact-map'));

            // Slider value updates
            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', function() {
                    const valueSpan = document.getElementById(this.id + '-value');
                    if (valueSpan) {
                        valueSpan.textContent = this.value;
                    }

                    if (this.id === 'slider-plot-size') {
                        if (currentView === 'crystallography') {
                            initThreeJS();
                        } else {
                            initP5();
                        }
                    } else if (currentView === 'crystallography') {
                        drawProteinStructure();
                        updateInfoDisplay();
                    } else {
                        p5Instance.redraw();
                        updateInfoDisplay();
                    }
                });
            });

            // Checkbox and select updates
            document.getElementById('checkbox-lateral-chains').addEventListener('change', () => {
                if (currentView === 'crystallography') {
                    drawProteinStructure();
                }
            });

            document.getElementById('select-residue').addEventListener('change', function() {
                const targetAA = this.value;
                const currentRes = parseInt(document.getElementById('slider-browse-sequence').value);

                // Find next occurrence
                for (let i = currentRes; i < proteinData.length; i++) {
                    if (proteinData[i].resName === targetAA) {
                        document.getElementById('slider-browse-sequence').value = i + 1;
                        document.getElementById('browse-sequence-value').textContent = i + 1;
                        break;
                    }
                }

                if (currentView === 'crystallography') {
                    drawProteinStructure();
                }
                updateInfoDisplay();
            });

            document.getElementById('slider-residue-x').addEventListener('input', updateInfoDisplay);
            document.getElementById('slider-residue-y').addEventListener('input', updateInfoDisplay);
            document.getElementById('slider-browse-sequence').addEventListener('input', updateInfoDisplay);

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', () => {
                if (currentView === 'crystallography' && controls) {
                    controls.reset();
                }
            });
        });
    </script>
</body>
</html>
