        let a = 7;
        let b = 8;
        let c = 24;

        const checkboxMode = document.getElementById('checkbox-mode');
        const checkboxDisplay = document.getElementById('checkbox-display');
        const inputA = document.getElementById('input-a');
        const inputB = document.getElementById('input-b');
        const sliderC = document.getElementById('slider-c');
        const displayC = document.getElementById('display-c');
        const equationText = document.getElementById('equation-text');
        const infoToggle = document.getElementById('info-toggle');
        const infoContent = document.getElementById('info-content');

        function gcd(x, y) {
            return y === 0 ? x : gcd(y, x % y);
        }

        infoToggle.addEventListener('click', () => {
            const isHidden = infoContent.style.display === 'none';
            infoContent.style.display = isHidden ? 'block' : 'none';
            infoToggle.textContent = isHidden ? '-' : '+';
        });

        function updateVisualization() {
            if (p5Inst && p5Inst.redraw) {
                p5Inst.redraw();
            }
        }

        [inputA, inputB, sliderC].forEach(input => {
            input.addEventListener('input', () => {
                a = parseInt(inputA.value);
                b = parseInt(inputB.value);
                c = parseInt(sliderC.value);

                displayC.textContent = c;
                sliderC.max = a * b * 2;

                if (gcd(a, b) !== 1) {
                    equationText.textContent = "Numbers a and b should be coprime!";
                } else {
                    updateEquationText();
                }
                updateVisualization();
            });
        });

        [checkboxMode, checkboxDisplay].forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                updateEquationText();
                updateVisualization();
            });
        });

        function updateEquationText() {
            if (gcd(a, b) !== 1) {
                equationText.textContent = "Numbers a and b should be coprime!";
            } else if (checkboxDisplay.checked) {
                const frobenius = a * b - a - b;
                equationText.textContent = `F = ${a}×${b} - ${a} - ${b} = ${frobenius}`;
            } else {
                equationText.textContent = `Current equation: ${a}x + ${b}y = ${c}`;
            }

            sliderC.disabled = checkboxDisplay.checked;
            displayC.style.opacity = checkboxDisplay.checked ? '0.5' : '1';
        }

        let p5Inst;

        function sketch(p) {
            const canvasSize = 550;
            const margin = 50;
            const drawingSize = canvasSize - 2 * margin;

            p.setup = function() {
                p.createCanvas(canvasSize, canvasSize).parent('canvas-container');
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                if (gcd(a, b) !== 1) {
                    return;
                }

                p.push();
                p.translate(margin, canvasSize - margin);
                p.scale(1, -1);

                const stepsX = b + 1;
                const stepsY = a + 1;
                const stepSize = drawingSize / Math.max(stepsX - 1, stepsY - 1);
                const xMax = stepsX - 1;
                const yMax = stepsY - 1;

                // Draw axes
                p.stroke(200);
                p.strokeWeight(1);
                for (let i = 0; i <= xMax; i++) {
                    const x = i * stepSize;
                    p.line(x, 0, x, yMax * stepSize);
                }
                for (let j = 0; j <= yMax; j++) {
                    const y = j * stepSize;
                    p.line(0, y, xMax * stepSize, y);
                }

                // Draw axis labels
                p.push();
                p.scale(1, -1);
                p.fill(0, 0, 255);
                p.textAlign(p.CENTER, p.CENTER);
                for (let i = 0; i <= xMax; i++) {
                    const x = i * stepSize;
                    p.text(i, x, -stepSize/2);
                }
                for (let j = 0; j <= yMax; j++) {
                    const y = j * stepSize;
                    p.text(j, -stepSize/2, -y);
                }
                p.pop();

                const currentC = checkboxDisplay.checked ? a * b - a - b : c;

                if (checkboxMode.checked) {
                    // Dots mode
                    p.fill(0);
                    p.noStroke();
                    for (let x = 0; x <= xMax; x++) {
                        for (let y = 0; y <= yMax; y++) {
                            p.circle(x * stepSize, y * stepSize, 5);
                        }
                    }

                    // Draw line ax + by = c
                    p.stroke(255, 0, 0);
                    p.strokeWeight(2);
                    const x1 = currentC / a;
                    const y2 = currentC / b;

                    if (x1 >= 0 && x1 <= xMax) {
                        p.line(x1 * stepSize, 0, x1 * stepSize, yMax * stepSize);
                    }
                    if (y2 >= 0 && y2 <= yMax) {
                        p.line(0, y2 * stepSize, xMax * stepSize, y2 * stepSize);
                    }

                    // Highlight solutions
                    p.fill(0, 255, 0);
                    p.noStroke();
                    if (!checkboxDisplay.checked) {
                        for (let x = 0; x <= xMax; x++) {
                            for (let y = 0; y <= yMax; y++) {
                                if (a * x + b * y === currentC) {
                                    p.circle(x * stepSize, y * stepSize, 8);
                                }
                            }
                        }
                    }
                } else {
                    // Numbers mode
                    p.textAlign(p.CENTER, p.CENTER);
                    p.textSize(12);

                    for (let x = 0; x <= xMax; x++) {
                        for (let y = 0; y <= yMax; y++) {
                            const value = a * x + b * y;

                            if (checkboxDisplay.checked) {
                                // Check if representable in non-negative integers (x >= 0, y >= 0)
                                let representable = false;
                                for (let xi = 0; xi <= xMax; xi++) {
                                    for (let yi = 0; yi <= yMax; yi++) {
                                        if (a * xi + b * yi === value && xi >= 0 && yi >= 0) {
                                            representable = true;
                                            break;
                                        }
                                    }
                                    if (representable) break;
                                }

                                if (!representable) {
                                    p.fill(255, 0, 0);
                                } else {
                                    p.fill(0);
                                }
                            } else {
                                p.fill(value === currentC ? [0, 255, 0] : [0]);
                            }

                            p.push();
                            p.scale(1, -1);
                            p.text(value, x * stepSize, -y * stepSize);
                            p.pop();
                        }
                    }
                }

                p.pop();
            };
        }

        new p5(sketch);
    </script>
</body>
</html>
