            <table id="table-quiz"></table>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // Data arrays
        const riversData = [
            { name: 'Nile', lon: 31.2, lat: 30.2, found: false },
            { name: 'Saint Lawrence', lon: -64.5, lat: 49.5, found: false },
            { name: 'Nelson', lon: -96.0, lat: 57.0, found: false },
            { name: 'Mekong', lon: 105.5, lat: 10.2, found: false },
            { name: 'Araguaia', lon: -50.0, lat: -5.5, found: false },
            { name: 'Yangtze', lon: 112.0, lat: 31.0, found: false },
            { name: 'Brahmaputra', lon: 89.6, lat: 25.2, found: false },
            { name: 'Yukon', lon: -164.0, lat: 62.8, found: false },
            { name: 'Mackenzie', lon: -134.0, lat: 69.0, found: false },
            // 31 more rivers
            ...Array(31).fill().map((_, i) => ({ name: `River ${i + 10}`, lon: Math.random() * 360 - 180, lat: Math.random() * 180 - 90, found: false }))
        ];

        const lakesData = [
            { name: 'Onega', lon: 35.5, lat: 61.7, found: false },
            { name: 'Erie', lon: -81.2, lat: 42.2, found: false },
            { name: 'Huron', lon: -82.0, lat: 45.0, found: false },
            { name: 'Issyk-Kul', lon: 77.5, lat: 42.2, found: false },
            { name: 'Michigan', lon: -87.0, lat: 43.7, found: false },
            { name: 'Ladoga', lon: 31.0, lat: 60.8, found: false },
            { name: 'Winnipeg', lon: -97.0, lat: 52.0, found: false },
            { name: 'Baikal', lon: 108.0, lat: 53.6, found: false },
            { name: 'Tanganyika', lon: 29.5, lat: -6.0, found: false },
            // 18 more lakes
            ...Array(18).fill().map((_, i) => ({ name: `Lake ${i + 10}`, lon: Math.random() * 360 - 180, lat: Math.random() * 180 - 90, found: false }))
        ];

        let currentQuiz = riversData.slice();
        let img;

        // DOM helpers
        const positionP = document.getElementById('p-position');
        const quizTable = document.getElementById('table-quiz');

        function populateTable(quizItems) {
            quizTable.innerHTML = '';

            const header = document.createElement('tr');
            const statusRow = document.createElement('tr');

            quizItems.forEach(item => {
                const th = document.createElement('th');
                th.textContent = item.name;
                header.appendChild(th);

                const td = document.createElement('td');
                td.dataset.name = item.name;
                td.textContent = item.found ? 'True' : 'False';
                if (item.found) td.classList.add('found');
                statusRow.appendChild(td);
            });

            quizTable.appendChild(header);
            quizTable.appendChild(statusRow);
        }

        function updateTable(quizItems) {
            const cells = quizTable.querySelectorAll('td');
            cells.forEach(cell => {
                const item = quizItems.find(d => d.name === cell.dataset.name);
                if (item) {
                    cell.textContent = item.found ? 'True' : 'False';
                    if (item.found) cell.classList.add('found');
                    else cell.classList.remove('found');
                }
            });
        }

        populateTable(currentQuiz);

        function resetQuiz() {
            const selected = document.querySelector('input[name="quiz-type"]:checked').value;
            if (selected === 'rivers') currentQuiz = riversData;
            else currentQuiz = lakesData;

            currentQuiz.forEach(d => d.found = false);
            populateTable(currentQuiz);
            redraw();
        }

        // Event listeners
        document.getElementById('radio-rivers').addEventListener('change', resetQuiz);
        document.getElementById('radio-lakes').addEventListener('change', resetQuiz);
        document.getElementById('btn-reset').addEventListener('click', resetQuiz);

        // P5 sketch
        const sketch = p => {
            p.preload = () => {
                img = p.loadImage('https://upload.wikimedia.org/wikipedia/commons/c/c5/Blue_Marble_Next_Generation_%2B_topography_%2B_bathymetry.jpg');
            };

            p.setup = () => {
                const canvas = p.createCanvas(800, 400);
                canvas.parent('canvas-container');
                p.noLoop();
                resetQuiz();
            };

            p.draw = () => {
                p.image(img, 0, 0, p.width, p.height);

                const showLocs = document.getElementById('check-show-locations').checked;
                const showSome = document.getElementById('check-show-some').checked;

                let unfound = currentQuiz.filter(d => !d.found);
                if (showSome && unfound.length > 0) {
                    let sample = unfound.slice(0, 3);
                    for (const place of sample) {
                        const { x, y } = mapToPixels(place.lon, place.lat);
                        p.fill('white');
                        p.textSize(12);
                        p.text(place.name, x + 12, y);
                    }
                }

                if (showLocs) {
                    p.noStroke();
                    p.fill('red');
                    for (const place of unfound) {
                        const { x, y } = mapToPixels(place.lon, place.lat);
                        p.circle(x, y, 10);
                    }
                }

                p.fill(173, 216, 230, 100);
                p.noStroke();
                p.circle(p.mouseX, p.mouseY, 30);

                // Update mouse position text
                const geo = mapToGeo(p.mouseX, p.mouseY);
                positionP.textContent = `Current position {${Math.round(geo.lon)}, ${Math.round(geo.lat)}}`;
            };

            p.mouseMoved = () => p.redraw();

            p.mouseClicked = () => {
                function distSq(x1, y1, x2, y2) {
                    const dx = x2 - x1;
                    const dy = y2 - y1;
                    return dx * dx + dy * dy;
                }
                const toleranceSq = 15 * 15;
                let hit = null;
                for (const place of currentQuiz) {
                    if (!place.found) {
                        const { x, y } = mapToPixels(place.lon, place.lat);
                        if (distSq(p.mouseX, p.mouseY, x, y) < toleranceSq) {
                            hit = place;
                            break;
                        }
                    }
                }
                if (hit) {
                    hit.found = true;
                    updateTable(currentQuiz);
                    p.redraw();
                }
            };

            // Mapping helpers using p5 instance
            function mapToPixels(lon, lat) {
                return {
                    x: p.map(lon, -180, 180, 0, p.width),
                    y: p.map(lat, 90, -90, 0, p.height)
                };
            }

            function mapToGeo(x, y) {
                return {
                    lon: p.map(x, 0, p.width, -180, 180),
                    lat: p.map(y, 0, p.height, 90, -90)
                };
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
