            border-bottom: 1px solid #eee;
        }
        #header-section p {
            margin: 4px 0;
        }
        #main-content {
            flex: 1;
            display: flex;
            min-height: 0; /* critical for FF */
        }
        #info-panel {
            width: 300px;
            padding: 15px;
            border-right: 1px solid #eee;
            overflow-y: auto;
        }
        #encoding-table {
            margin-top: 15px;
        }
        #canvas-area {
            flex: 1;
            min-width: 0;
        }
