            display: block;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label>tiers</label>
            <div id="tiers-container">
                <button id="btn-tier-1" class="tier-btn">1</button>
                <button id="btn-tier-2" class="tier-btn">2</button>
                <button id="btn-tier-3" class="tier-btn">3</button>
                <button id="btn-tier-4" class="tier-btn">4</button>
                <button id="btn-tier-5" class="tier-btn active">5</button>
            </div>
            <label>show binary</label>
            <input type="checkbox" id="checkbox-binary">
            <label>characters</label>
            <input type="radio" id="radio-chars-no" name="chars-toggle" value="no"><label for="radio-chars-no">no</label>
            <input type="radio" id="radio-chars-yes" name="chars-toggle" value="yes" checked><label for="radio-chars-yes">yes</label>
        </div>

        <div id="viz-container">
            <div id="header-section">
                <p id="original-text">MISSISSIPPI_RIVER</p>
                <p id="encoded-text">1100000101000101001001000011111010011011110101</p>
            </div>
            <div id="main-content">
                <div id="info-panel">
                    <p id="explanation-text">Once at the top, the total frequency becomes 17, which is the total number of characters.</p>
                    <div id="encoding-table">
                        <p>I = 00</p>
                        <p>S = 01</p>
                        <p>P = 100</p>
                        <p>R = 101</p>
                        <p>M = 1100</p>
                        <p>V = 1101</p>
                        <p>E = 1110</p>
                        <p>_ = 1111</p>
                    </div>
                </div>
                <div id="canvas-area"></div>
            </div>
        </div>
    </div>

    <script>
        // Global state variables
        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;

        // Hard-coded tree data
        const nodes = {
            'I':     { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            'S':     { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            'P':     { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            'R':     { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            'M':     { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            'V':     { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            'E':     { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            '_':     { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            'IS':    { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            'PR':    { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            'MV':    { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            'E_':    { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            'MVE_':  { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            'PRMVE': { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            'ROOT':  { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        };

        function setup() {
            const canvasDiv = document.getElementById('canvas-area');
            const canvas = createCanvas(800, 500);
            canvas.parent(canvasDiv);
            noLoop();
        }

        function draw() {
            clear();
            stroke('#C70000');
            strokeWeight(2);

            // Draw edges & binary labels
            Object.values(nodes).forEach(node => {
                const parentKey = findParentKey(node.id);
                if (parentKey && nodes[parentKey].tier <= currentTier && node.tier <= currentTier) {
                    const parent = nodes[parentKey];
                    line(parent.pos.x, parent.pos.y + 20, node.pos.x, node.pos.y - 20);
                    if (showBinary) {
                        const midX = (parent.pos.x + node.pos.x) / 2;
                        const midY = (parent.pos.y + 20 + node.pos.y - 20) / 2;
                        noStroke();
                        fill('#000');
                        textSize(14);
                        textAlign(CENTER, CENTER);
                        const bit = parent.children[0] === node.id ? '0' : '1';
                        text(bit, midX, midY);
                    }
                }
            });

            // Draw nodes
            Object.values(nodes).forEach(node => {
                if (node.tier > currentTier) return;

                noStroke();
                fill('#FEFDD5');
                rect(node.pos.x - 40, node.pos.y - 20, 80, 40, 6);
                stroke('#C70000');
                noFill();
                rect(node.pos.x - 40, node.pos.y - 20, 80, 40, 6);

                noStroke();
                fill('#000');
                textSize(12);
                textAlign(CENTER, CENTER);
                let label = node.label;
                let textLabel;
                if (node.tier === 1) {
                    // Leaf: Character(Frequency)
                    textLabel = `${label}(${node.freq})`;
                } else {
                    // Internal: (Show chars vs only freq)
                    if (showNodeChars) {
                        textLabel = `${label}(${node.freq})`;
                    } else {
                        textLabel = `(${node.freq})`;
                    }
                }
                text(textLabel, node.pos.x, node.pos.y);
            });
        }

        // Helper: find parent key for a given child node id
        function findParentKey(childId) {
            for (const [key, node] of Object.entries(nodes)) {
                if (node.children && node.children.includes(childId)) {
                    return key;
                }
            }
            return null;
        }

        // --- Controller wiring ---
        function updateTier(tier) {
            currentTier = tier;
            document.querySelectorAll('.tier-btn').forEach(b => b.classList.remove('active'));
            document.getElementById(`btn-tier-${tier}`).classList.add('active');

            // Update explanation text
            const expText = document.getElementById('explanation-text');
            if (tier === 1) {
                expText.textContent = "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.";
            } else if (tier >= 2 && tier <= 4) {
                expText.textContent = "We continue grouping until we get to the highest tier.";
            } else { // 5
                expText.textContent = "Once at the top, the total frequency becomes 17, which is the total number of characters.";
            }

            // Update visibility
            const tableDiv = document.getElementById('encoding-table');
            const encodedDiv = document.getElementById('encoded-text');
            tableDiv.style.display = (currentTier >= 2) ? 'block' : 'none';
            encodedDiv.style.display = (currentTier === 5) ? 'block' : 'none';

            redraw();
        }

        // Attach events
        document.getElementById('btn-tier-1').addEventListener('click', () => updateTier(1));
        document.getElementById('btn-tier-2').addEventListener('click', () => updateTier(2));
        document.getElementById('btn-tier-3').addEventListener('click', () => updateTier(3));
        document.getElementById('btn-tier-4').addEventListener('click', () => updateTier(4));
        document.getElementById('btn-tier-5').addEventListener('click', () => updateTier(5));

        document.getElementById('checkbox-binary').addEventListener('change', (e) => {
            showBinary = e.target.checked;
            redraw();
        });

        document.getElementById('radio-chars-no').addEventListener('change', () => {
            showNodeChars = false;
            redraw();
        });
        document.getElementById('radio-chars-yes').addEventListener('change', () => {
            showNodeChars = true;
            redraw();
        });

        // Initial password render update for tier 5
        updateTier(5);
    </script>
</body>
</html>
