            overflow: hidden;
        }

        #formula-label {
            position: absolute;
            top: 10px;
            right: 10px;
            background-color: rgba(255, 255, 255, 0.9);
            padding: 5px 10px;
            border-radius: 4px;
            font-size: 18px;
        }

        canvas {
            display: block;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <label for="slider-n">n</label>
        <input type="range" id="slider-n" min="1" max="20" value="1" step="1">
        <input type="number" id="input-n" min="1" max="20" value="1" step="1">
    </div>

    <div id="visualization-area">
        <div id="function-plot-container">
            <div id="formula-label">$$y = \frac{2}{x}$$</div>
        </div>
        <div id="sum-plot-container"></div>
    </div>

    <script>
        // Color array for rectangles
        const colors = [
            '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa',
            '#6495ed', '#9370db', '#8a2be2', '#ff69b4', '#ff6347',
            '#ffa500', '#ffd700', '#adff2f', '#32cd32', '#00ff00',
            '#00fa9a', '#40e0d0', '#48d1cc', '#1e90ff', '#0000ff'
        ];

        // Function plot sketch
        const functionSketch = (p) => {
            let canvas;

            p.setup = () => {
                canvas = p.createCanvas(
                    document.getElementById('function-plot-container').offsetWidth,
                    document.getElementById('function-plot-container').offsetHeight
                );
                canvas.parent('function-plot-container');
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                const n = parseInt(document.getElementById('input-n').value);
                const margin = 50;
                const plotWidth = p.width - 2 * margin;
                const plotHeight = p.height - 2 * margin;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(margin, p.height - margin, p.width - margin, p.height - margin);
                p.line(margin, p.height - margin, margin, margin);

                // Draw tick marks and labels
                p.textAlign('center', 'center');
                p.textSize(12);

                // X-axis ticks
                for (let x = 0; x <= 10; x++) {
                    const xPos = margin + (x / 10) * plotWidth;
                    p.line(xPos, p.height - margin, xPos, p.height - margin + 5);
                    p.text(x, xPos, p.height - margin + 15);
                }

                // Y-axis ticks
                for (let y = 0; y <= 2; y += 0.5) {
                    const yPos = p.height - margin - (y / 2.2) * plotHeight;
                    p.line(margin - 5, yPos, margin, yPos);
                    p.text(y.toFixed(1), margin - 20, yPos);
                }

                // Draw function y = 2/x
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let x = 0.5; x <= 10; x += 0.01) {
                    const y = 2 / x;
                    if (y <= 2.2) {
                        const xPos = margin + (x / 10.5) * plotWidth;
                        const yPos = p.height - margin - (y / 2.2) * plotHeight;
                        p.vertex(xPos, yPos);
                    }
                }
                p.endShape();

                // Draw rectangles
                for (let k = 1; k <= n; k++) {
                    p.fill(colors[k - 1]);
                    p.stroke(0);
                    p.strokeWeight(1);

                    const x1 = margin + (k / 10.5) * plotWidth;
                    const y1 = p.height - margin - (2 / (k + 1) / 2.2) * plotHeight;
                    const x2 = margin + ((k + 1) / 10.5) * plotWidth;
                    const y2 = p.height - margin - (2 / k / 2.2) * plotHeight;

                    p.rect(x1, y1, x2 - x1, y2 - y1);

                    // Draw dashed line for k <= 3
                    if (k <= 3) {
                        p.stroke(128);
                        p.strokeWeight(1);
                        p.drawingContext.setLineDash([5, 5]);
                        p.line(
                            margin + (k / 10.5) * plotWidth,
                            p.height - margin,
                            margin + (k / 10.5) * plotWidth,
                            p.height - margin - (2 / k / 2.2) * plotHeight
                        );
                        p.drawingContext.setLineDash([]);
                    }
                }
            };

            p.windowResized = () => {
                p.resizeCanvas(
                    document.getElementById('function-plot-container').offsetWidth,
                    document.getElementById('function-plot-container').offsetHeight
                );
                p.redraw();
            };
        };

        // Summation plot sketch
        const sumSketch = (p) => {
            let canvas;

            p.setup = () => {
                canvas = p.createCanvas(
                    document.getElementById('sum-plot-container').offsetWidth,
                    document.getElementById('sum-plot-container').offsetHeight
                );
                canvas.parent('sum-plot-container');
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                const n = parseInt(document.getElementById('input-n').value);
                const margin = 50;
                const plotWidth = p.width - 2 * margin;
                const plotHeight = p.height - 2 * margin;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(margin, p.height - margin, p.width - margin, p.height - margin);
                p.line(margin, p.height - margin, margin, margin);

                // Draw tick marks and labels
                p.textAlign('center', 'center');
                p.textSize(12);

                // X-axis ticks
                p.line(margin + plotWidth * (1 / 1.5), p.height - margin, 
                       margin + plotWidth * (1 / 1.5), p.height - margin + 5);
                p.text('1', margin + plotWidth * (1 / 1.5), p.height - margin + 15);

                // Y-axis ticks
                for (let y = 0; y <= 2; y += 0.5) {
                    const yPos = p.height - margin - (y / 2.2) * plotHeight;
                    p.line(margin - 5, yPos, margin, yPos);
                    p.text(y.toFixed(1), margin - 20, yPos);
                }

                // Draw stacked rectangles
                let yStack = 0;
                for (let k = n; k >= 1; k--) {
                    const h = 2 / k - 2 / (k + 1);
                    const rectHeight = (h / 2.2) * plotHeight;

                    p.fill(colors[k - 1]);
                    p.stroke(0);
                    p.strokeWeight(1);

                    p.rect(
                        margin,
                        p.height - margin - (yStack / 2.2) * plotHeight - rectHeight,
                        plotWidth * (1 / 1.5),
                        rectHeight
                    );

                    yStack += h;
                }
            };

            p.windowResized = () => {
                p.resizeCanvas(
                    document.getElementById('sum-plot-container').offsetWidth,
                    document.getElementById('sum-plot-container').offsetHeight
                );
                p.redraw();
            };
        };

        // Initialize sketches
        new p5(functionSketch);
        new p5(sumSketch);

        // Control synchronization
        const slider = document.getElementById('slider-n');
        const input = document.getElementById('input-n');

        function updateValue(newValue) {
            const value = Math.max(1, Math.min(20, parseInt(newValue) || 1));
            slider.value = value;
            input.value = value;

            // Trigger redraw for both sketches
            document.querySelectorAll('canvas').forEach(canvas => {
                const sketch = canvas.__p5;
                if (sketch && sketch.redraw) {
                    sketch.redraw();
                }
            });
        }

        slider.addEventListener('input', (e) => updateValue(e.target.value));
        input.addEventListener('input', (e) => updateValue(e.target.value));

        // Handle window resize
        window.addEventListener('resize', () => {
            setTimeout(() => {
                document.querySelectorAll('canvas').forEach(canvas => {
                    const sketch = canvas.__p5;
                    if (sketch && sketch.windowResized) {
                        sketch.windowResized();
                    }
                });
            }, 100);
        });
    </script>
</body>
</html>
