            font-family: monospace;
        }

        #visualization-area {
            flex: 1;
            padding: 20px;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        #plot-container {
            width: 100%;
            height: 100%;
            max-width: 800px;
            max-height: 600px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>Model Selection</h3>
            <div class="button-group">
                <button id="btn-monod" class="active">Monod</button>
                <button id="btn-si">substrate inhibition</button>
            </div>

            <h3>View Selection</h3>
            <div class="button-group">
                <button id="btn-bifurcation" class="active">bifurcation diagram</button>
                <button id="btn-nontrivial">nontrivial steady state</button>
            </div>

            <h3>Parameters</h3>
            <div class="slider-container">
                <label for="slider-x2f">x<sub>2f</sub></label>
                <input type="range" id="slider-x2f" min="1" max="10" step="0.1" value="4">
                <span id="value-x2f">4.0</span>
            </div>

            <div class="slider-container">
                <label for="slider-umax">μ<sub>max</sub></label>
                <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                <span id="value-umax">0.60</span>
            </div>

            <div class="slider-container">
                <label for="slider-km">k<sub>m</sub></label>
                <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                <span id="value-km">0.12</span>
            </div>

            <div class="slider-container" id="k1-container" style="display: none;">
                <label for="slider-k1">k<sub>1</sub></label>
                <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
                <span id="value-k1">0.4545</span>
            </div>

            <div class="slider-container">
                <label for="slider-ds">D<sub>s</sub></label>
                <input type="range" id="slider-ds" min="0.01" max="1" step="0.01" value="0.6">
                <span id="value-ds">0.60</span>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Global state
        let currentModel = 'monod';
        let currentView = 'bifurcation';
        let params = {
            x2f: 4.0,
            umax: 0.6,
            km: 0.12,
            k1: 0.4545,
            ds: 0.6
        };

        // Initialize UI
        function initializeUI() {
            updateSliderDisplays();
            document.getElementById('btn-monod').classList.add('active');
            document.getElementById('btn-bifurcation').classList.add('active');
            if (currentModel === 'monod') {
                document.getElementById('k1-container').style.display = 'none';
            } else {
                document.getElementById('k1-container').style.display = 'block';
            }
        }

        // Update slider value displays
        function updateSliderDisplays() {
            document.getElementById('value-x2f').textContent = params.x2f.toFixed(1);
            document.getElementById('value-umax').textContent = params.umax.toFixed(2);
            document.getElementById('value-km').textContent = params.km.toFixed(2);
            document.getElementById('value-k1').textContent = params.k1.toFixed(4);
            document.getElementById('value-ds').textContent = params.ds.toFixed(2);
        }

        // Update plot based on current state
        function updatePlot() {
            if (currentView === 'bifurcation') {
                drawBifurcationDiagram();
            } else {
                drawNontrivialStatePlot();
            }
        }

        // Calculate D for given x2 based on model
        function calculateD(x2, model) {
            if (model === 'monod') {
                return params.umax * x2 / (params.km + x2);
            } else {
                return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
            }
        }

        // Calculate mu for SI model
        function calculateMu(x2) {
            return params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
        }

        // Draw bifurcation diagram
        function drawBifurcationDiagram() {
            const x2Vals = [];
            const dValsStable = [];
            const dValsUnstable = [];

            const x2Step = 0.01;
            const maxX2 = parseFloat(params.x2f);

            if (currentModel === 'si') {
                const x2Crit = Math.sqrt(params.km / params.k1);
                for (let x2 = 0; x2 < maxX2; x2 += x2Step) {
                    const d = calculateD(x2, 'si');
                    if (x2 <= x2Crit) {
                        x2Vals.push(x2);
                        dValsStable.push(d);
                    }
                }

                const x2ValsUn = [];
                const dValsUn = [];
                for (let x2 = 0; x2 < maxX2; x2 += x2Step) {
                    const d = calculateD(x2, 'si');
                    if (x2 > x2Crit) {
                        x2ValsUn.push(x2);
                        dValsUn.push(d);
                    }
                }

                var traceMonodStable = {
                    x: dValsStable,
                    y: x2Vals,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'nontrivial stable',
                    line: { color: 'blue' }
                };

                var traceMonodUnstable = {
                    x: dValsUn,
                    y: x2ValsUn,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'nontrivial unstable',
                    line: { color: 'red', dash: 'dash' }
                };
            } else {
                for (let x2 = 0; x2 < maxX2; x2 += x2Step) {
                    x2Vals.push(x2);
                    dValsStable.push(calculateD(x2, 'monod'));
                }

                var traceMonodStable = {
                    x: dValsStable,
                    y: x2Vals,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'nontrivial',
                    line: { color: 'blue' }
                };
            }

            // Trivial steady state line
            const dBif = calculateD(params.x2f, currentModel);
            const traceTrivialUnstable = {
                x: [0, dBif],
                y: [params.x2f, params.x2f],
                type: 'scatter',
                mode: 'lines',
                name: 'trivial unstable',
                line: { color: 'red', dash: 'dash' },
                showlegend: true
            };

            const traceTrivialStable = {
                x: [dBif, 1.0],
                y: [params.x2f, params.x2f],
                type: 'scatter',
                mode: 'lines',
                name: 'trivial stable',
                line: { color: 'blue' },
                showlegend: true
            };

            // Intersection points
            const solutions = [];
            for (let x2 = 0.01; x2 < maxX2; x2 += 0.001) {
                const d = calculateD(x2, currentModel);
                if (Math.abs(d - params.ds) < 0.005) {
                    const dDx2 = (calculateD(x2 + 0.01, currentModel) - calculateD(x2 - 0.01, currentModel)) / 0.02;
                    solutions.push({ x2, stable: dDx2 > 0 });
                }
            }

            const stablePoints = {
                x: solutions.filter(s => s.stable).map(s => params.ds),
                y: solutions.filter(s => s.stable).map(s => s.x2),
                type: 'scatter',
                mode: 'markers',
                name: 'stable steady states',
                marker: { color: 'magenta', size: 10 }
            };

            const unstablePoints = {
                x: solutions.filter(s => !s.stable).map(s => params.ds),
                y: solutions.filter(s => !s.stable).map(s => s.x2),
                type: 'scatter',
                mode: 'markers',
                name: 'unstable steady states',
                marker: { color: 'cyan', size: 10 }
            };

            const trivialPoint = {
                x: [params.ds],
                y: [params.x2f],
                type: 'scatter',
                mode: 'markers',
                name: 'trivial solution',
                marker: { color: 'green', size: 10 }
            };

            const data = [traceMonodStable, traceMonodUnstable, traceTrivialUnstable, traceTrivialStable, stablePoints, unstablePoints, trivialPoint].filter(t => t !== undefined);

            const layout = {
                title: 'Bifurcation Diagram',
                xaxis: { title: 'dilution rate', range: [0, 1] },
                yaxis: { title: 'X₂', range: [0, 6] },
                shapes: [{
                    type: 'line',
                    x0: params.ds,
                    x1: params.ds,
                    y0: 0,
                    y1: 6,
                    line: { color: 'gray', width: 1, dash: 'dot' }
                }]
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        // Draw nontrivial state plot
        function drawNontrivialStatePlot() {
            if (currentModel === 'monod') return; // Not applicable for Monod model

            const x2Vals = [];
            const muVals = [];

            for (let x2 = 0; x2 <= 7; x2 += 0.01) {
                x2Vals.push(x2);
                muVals.push(calculateMu(x2));
            }

            const traceMuCurve = {
                x: x2Vals,
                y: muVals,
                type: 'scatter',
                mode: 'lines',
                name: 'μ(x₂)',
                line: { color: 'blue' }
            };

            const traceDs = {
                x: [0, 7],
                y: [params.ds, params.ds],
                type: 'scatter',
                mode: 'lines',
                name: 'Dₛ',
                line: { color: 'gray', width: 2 }
            };

            // Bistability region
            const dUpper = params.umax / (1 + 2 * Math.sqrt(params.k1 * params.km));
            const dLower = calculateMu(params.x2f);

            const solutions = [];
            for (let x2 = 0.01; x2 <= 7; x2 += 0.001) {
                const mu = calculateMu(x2);
                if (Math.abs(mu - params.ds) < 0.0005) {
                    const dMudx2 = (calculateMu(x2 + 0.001) - calculateMu(x2 - 0.001)) / 0.002;
                    solutions.push({ x2, mu, stable: dMudx2 > 0 });
                }
            }

            // Bistability region
            const upperBound = {
                x: [0, 7],
                y: [dUpper, dUpper],
                type: 'scatter',
                mode: 'lines',
                name: 'D upper',
                line: { color: 'red', dash: 'dash' },
                showlegend: false
            };

            const lowerBound = {
                x: [0, 7],
                y: [dLower, dLower],
                type: 'scatter',
                mode: 'lines',
                name: 'D lower',
                line: { color: 'red', dash: 'dash' },
                fill: 'tonexty',
                fillcolor: 'rgba(255, 200, 200, 0.3)',
                showlegend: false
            };

            const stablePoints = {
                x: solutions.filter(s => s.stable).map(s => s.x2),
                y: solutions.filter(s => s.stable).map(s => s.mu),
                type: 'scatter',
                mode: 'markers',
                name: 'stable steady states',
                marker: { color: 'magenta', size: 10 }
            };

            const unstablePoints = {
                x: solutions.filter(s => !s.stable).map(s => s.x2),
                y: solutions.filter(s => !s.stable).map(s => s.mu),
                type: 'scatter',
                mode: 'markers',
                name: 'unstable steady states',
                marker: { color: 'cyan', size: 10 }
            };

            const data = [traceMuCurve, traceDs, upperBound, lowerBound, stablePoints, unstablePoints];

            const layout = {
                title: 'Nontrivial Steady State',
                xaxis: { title: 'substrate concentration', range: [0, 7] },
                yaxis: { title: 'specific growth rate coefficient', range: [0, 1] }
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        // Event listeners
        document.getElementById('btn-monod').addEventListener('click', function() {
            currentModel = 'monod';
            document.getElementById('btn-monod').classList.add('active');
            document.getElementById('btn-si').classList.remove('active');
            document.getElementById('k1-container').style.display = 'none';
            updatePlot();
        });

        document.getElementById('btn-si').addEventListener('click', function() {
            currentModel = 'si';
            document.getElementById('btn-si').classList.add('active');
            document.getElementById('btn-monod').classList.remove('active');
            document.getElementById('k1-container').style.display = 'block';
            updatePlot();
        });

        document.getElementById('btn-bifurcation').addEventListener('click', function() {
            currentView = 'bifurcation';
            document.getElementById('btn-bifurcation').classList.add('active');
            document.getElementById('btn-nontrivial').classList.remove('active');
            updatePlot();
        });

        document.getElementById('btn-nontrivial').addEventListener('click', function() {
            currentView = 'nontrivial';
            document.getElementById('btn-nontrivial').classList.add('active');
            document.getElementById('btn-bifurcation').classList.remove('active');
            if (currentModel === 'monod') {
                document.getElementById('btn-si').click();
            }
            updatePlot();
        });

        // Parameter sliders
        ['x2f', 'umax', 'km', 'k1', 'ds'].forEach(param => {
            document.getElementById(`slider-${param}`).addEventListener('input', function(e) {
                params[param] = parseFloat(e.target.value);
                updateSliderDisplays();
                updatePlot();
            });
        });

        // Initialize
        initializeUI();
        updatePlot();
    </script>
</body>
</html>
