    <script>
        // Three.js setup
        let scene, camera, renderer, controls;
        let earthMesh, sunlight;
        let selectedLatitudeLine, surfaceNormalVector, sunVector;
        const earthRadius = 1;

        function initThreeJS() {
            const container = document.getElementById('globe-container');
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            camera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.set(3, 2, 3);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            container.appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;

            // Earth texture
            const canvas = document.createElement('canvas');
            canvas.width = 512;
            canvas.height = 256;
            const ctx = canvas.getContext('2d');

            // Blue ocean background
            ctx.fillStyle = '#4a90e2';
            ctx.fillRect(0, 0, 512, 256);

            // Simple continents
            ctx.fillStyle = '#2d5016';
            ctx.beginPath();
            ctx.ellipse(100, 100, 60, 40, 0, 0, 2 * Math.PI);
            ctx.fill();
            ctx.beginPath();
            ctx.ellipse(200, 50, 80, 60, 0, 0, 2 * Math.PI);
            ctx.fill();
            ctx.beginPath();
            ctx.ellipse(300, 150, 70, 50, 0, 0, 2 * Math.PI);
            ctx.fill();
            ctx.beginPath();
            ctx.ellipse(450, 80, 60, 70, 0, 0, 2 * Math.PI);
            ctx.fill();

            const texture = new THREE.CanvasTexture(canvas);

            // Earth mesh
            const geometry = new THREE.SphereGeometry(earthRadius, 32, 16);
            const material = new THREE.MeshPhongMaterial({ map: texture });
            earthMesh = new THREE.Mesh(geometry, material);
            scene.add(earthMesh);

            // Grid
            const wireframeMaterial = new THREE.MeshBasicMaterial({ 
                wireframe: true, 
                color: 0xffffff, 
                opacity: 0.3, 
                transparent: true 
            });
            const wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);
            scene.add(wireframeMesh);

            // Lighting
            sunlight = new THREE.DirectionalLight(0xffffff, 1);
            scene.add(sunlight);
            scene.add(new THREE.AmbientLight(0x404040));

            // Vectors
            surfaceNormalVector = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0),
                new THREE.Vector3(0, 1, 0),
                0.3,
                0x000000
            );
            scene.add(surfaceNormalVector);

            sunVector = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, -1),
                new THREE.Vector3(0, 1, 0),
                0.3,
                0xff0000
            );
            scene.add(sunVector);

            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function updateThreeJS(season, latitude, time) {
            const solarDeclination = 23.5 * Math.cos(2 * Math.PI * season);

            // Rotate earth
            earthMesh.rotation.y = (time / 24) * 2 * Math.PI;

            // Update sunlight direction
            const solarDeclinationRad = solarDeclination * Math.PI / 180;
            sunlight.position.set(0, Math.sin(solarDeclinationRad), -Math.cos(solarDeclinationRad));
            sunlight.lookAt(0, 0, 0);

            // Update selected latitude line
            if (selectedLatitudeLine) scene.remove(selectedLatitudeLine);
            const latitudeRad = latitude * Math.PI / 180;
            const circleGeometry = new THREE.CircleGeometry(Math.sin(latitudeRad), 64);
            const lineGeometry = new THREE.BufferGeometry().setFromPoints(
                circleGeometry.getAttribute('position').array.reduce((acc, val, idx) => {
                    if (idx % 3 === 0) acc.push(new THREE.Vector3(val, Math.cos(latitudeRad), 0));
                    return acc;
                }, [])
            );
            selectedLatitudeLine = new THREE.Line(
                lineGeometry,
                new THREE.LineDashedMaterial({ color: 0x0000ff, dashSize: 0.1, gapSize: 0.05 })
            );
            selectedLatitudeLine.rotation.y = Math.PI / 2;
            selectedLatitudeLine.computeLineDistances();
            scene.add(selectedLatitudeLine);

            // Update surface normal and sun vectors
            const x = Math.sin(latitudeRad);
            const y = Math.cos(latitudeRad);
            surfaceNormalVector.position.set(0, y, x);
            surfaceNormalVector.setDirection(new THREE.Vector3(0, y, x));

            sunVector.position.set(0, y, x);
            sunVector.setDirection(sunlight.position.clone().normalize());
        }

        // Plotly setup
        let instantPlot, cumulativePlot;

        function initPlotly() {
            const layout1 = {
                title: 'energy',
                xaxis: { title: 'time (hr)', range: [0, 24] },
                yaxis: { title: 'watts/m^2', range: [0, 1400] },
                margin: { l: 50, r: 20, t: 40, b: 50 }
            };

            const layout2 = {
                title: 'energy',
                xaxis: { title: 'time (hr)', range: [0, 24] },
                yaxis: { title: 'watts/m^2', range: [0, 14000] },
                margin: { l: 50, r: 20, t: 40, b: 50 }
            };

            const data1 = [
                { x: [], y: [], name: 'equator', mode: 'lines', line: { color: 'rgb(214, 39, 40)' } },
                { x: [], y: [], name: 'latitude', mode: 'lines', line: { color: 'black' } },
                { x: [], y: [], name: 'north pole', mode: 'lines', line: { color: 'rgb(31, 119, 180)' } },
                { x: [12], y: [0], mode: 'markers', marker: { color: 'black', size: 8 }, showlegend: false }
            ];

            const data2 = [
                { x: [], y: [], name: 'equator', mode: 'lines', line: { color: 'rgb(214, 39, 40)' } },
                { x: [], y: [], name: 'latitude', mode: 'lines', line: { color: 'black' } },
                { x: [], y: [], name: 'north pole', mode: 'lines', line: { color: 'rgb(31, 119, 180)' } }
            ];

            Plotly.newPlot('plot-instant', data1, layout1, {responsive: true});
            Plotly.newPlot('plot-cumulative', data2, layout2, {responsive: true});
        }

        function updatePlots(season, latitude, time) {
            const solarDeclination = 23.5 * Math.cos(2 * Math.PI * season);
            const solarDeclinationRad = solarDeclination * Math.PI / 180;

            const hours = Array.from({length: 241}, (_, i) => i / 10);

            const latitudes = [0, latitude, 90];
            const colors = ['rgb(214, 39, 40)', 'black', 'rgb(31, 119, 180)'];
            const names = ['equator', 'latitude', 'north pole'];

            const instantData = [[], [], []];
            const cumulativeData = [[], [], []];

            latitudes.forEach((lat, idx) => {
                let cumulative = 0;
                const latRad = lat * Math.PI / 180;

                hours.forEach(hour => {
                    const hra = 15 * (hour - 12) * Math.PI / 180;
                    const hourRad = hour * Math.PI / 24;

                    const energy = 1361 * Math.max(0, 
                        Math.sin(latRad) * Math.sin(solarDeclinationRad) + 
                        Math.cos(latRad) * Math.cos(solarDeclinationRad) * Math.cos(hra)
                    );

                    instantData[idx].push(energy);
                    cumulative += energy * 0.1; // 0.1 hour steps
                    cumulativeData[idx].push(cumulative);
                });
            });

            // Update instantaneous plot
            const instantUpdate = {
                'x[0]': hours,
                'y[0]': instantData[0],
                'x[1]': hours,
                'y[1]': instantData[1],
                'x[2]': hours,
                'y[2]': instantData[2],
                'x[3]': [time],
                'y[3]': [instantData[1][Math.round(time * 10)]]
            };
            Plotly.restyle('plot-instant', instantUpdate);

            // Update cumulative plot
            const cumulativeUpdate = {
                'x[0]': hours,
                'y[0]': cumulativeData[0],
                'x[1]': hours,
                'y[1]': cumulativeData[1],
                'x[2]': hours,
                'y[2]': cumulativeData[2]
            };
            Plotly.restyle('plot-cumulative', cumulativeUpdate);
        }

        // Main update function
        function update() {
            const season = parseFloat(document.getElementById('slider-season').value);
            const latitude = parseFloat(document.getElementById('slider-latitude').value);
            const time = parseFloat(document.getElementById('slider-time').value);

            document.getElementById('value-season').textContent = season.toFixed(3);
            document.getElementById('value-latitude').textContent = latitude.toFixed(2);
            document.getElementById('value-time').textContent = time.toFixed(2);

            updateThreeJS(season, latitude, time);
            updatePlots(season, latitude, time);
        }

        // Initialize
        window.addEventListener('load', () => {
            initThreeJS();
            initPlotly();
            update();

            // Add slider listeners
            ['slider-season', 'slider-latitude', 'slider-time'].forEach(id => {
                document.getElementById(id).addEventListener('input', update);
            });

            // Handle resize
            window.addEventListener('resize', () => {
                const container = document.getElementById('globe-container');
                if (camera && renderer) {
                    camera.aspect = container.clientWidth / container.clientHeight;
                    camera.updateProjectionMatrix();
                    renderer.setSize(container.clientWidth, container.clientHeight);
                }

                Plotly.Plots.resize('plot-instant');
                Plotly.Plots.resize('plot-cumulative');
            });
        });
    </script>
</body>
</html>
