            101: { a: {n: 101, d: 6}, b: {n: 10200, d: 101}, c: {n: 10201, d: 606} }
        };

        let slider, input;
        let currentArea = 13;

        function setup() {
            const canvas = createCanvas(500, 400);
            canvas.parent('canvas-container');

            slider = document.getElementById('slider-area');
            input = document.getElementById('input-area');

            slider.addEventListener('input', handleSliderChange);
            input.addEventListener('input', handleInputChange);

            drawVisualization();
        }

        function handleSliderChange(e) {
            currentArea = parseInt(e.target.value);
            input.value = currentArea;
            drawVisualization();
        }

        function handleInputChange(e) {
            let value = parseInt(e.target.value);
            if (value < 1) value = 1;
            if (value > 101) value = 101;
            currentArea = value;
            slider.value = currentArea;
            input.value = currentArea;
            drawVisualization();
        }

        function drawVisualization() {
            clear();

            if (congruentData[currentArea]) {
                drawTriangle(currentArea);
            } else {
                drawNotCongruentMessage();
            }
        }

        function drawTriangle(area) {
            const data = congruentData[area];
            const a = data.a.n / data.a.d;
            const b = data.b.n / data.b.d;
            const c = data.c.n / data.c.d;

            const padding = 60;
            const scale = min((width - padding) / a, (height - padding) / b);

            const triangleWidth = a * scale;
            const triangleHeight = b * scale;

            const startX = (width - triangleWidth) / 2;
            const startY = height - (height - triangleHeight) / 2;

            // Draw triangle
            fill('#FFF2E5');
            stroke('#000000');
            strokeWeight(2);

            beginShape();
            vertex(startX, startY);
            vertex(startX + triangleWidth, startY);
            vertex(startX, startY - triangleHeight);
            endShape(CLOSE);

            // Draw labels
            fill('#8B5A2B');
            noStroke();
            textAlign(CENTER, CENTER);

            // Base label (a)
            drawFraction(data.a, startX + triangleWidth/2, startY + 20, 0, 14);

            // Height label (b)
            drawFraction(data.b, startX - 30, startY - triangleHeight/2, 0, 14);

            // Hypotenuse label (c)
            const midX = startX + triangleWidth/2;
            const midY = startY - triangleHeight/2;
            const angle = atan2(-triangleHeight, -triangleWidth);
            drawFraction(data.c, midX - 20 * cos(angle), midY - 20 * sin(angle), angle, 14);
        }

        function drawFraction(fraction, x, y, rotation, fontSize) {
            push();
            translate(x, y);
            rotate(rotation);

            const numStr = fraction.n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");
            const denStr = fraction.d.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");

            textSize(fontSize);
            textAlign(CENTER, CENTER);

            const numWidth = textWidth(numStr);
            const denWidth = textWidth(denStr);
            const maxWidth = max(numWidth, denWidth);

    const spacing = fontSize * 0.6;

    text(numStr, 0, -spacing/2);
    text(denStr, 0, spacing/2);

    stroke('#8B5A2B');
    strokeWeight(1);
    line(-maxWidth/2 - 2, 0, maxWidth/2 + 2, 0);

    pop();
        }

        function drawNotCongruentMessage() {
            fill('#FF0000');
            textAlign(CENTER, CENTER);
            textSize(20);
            text(`${currentArea} is not a congruent number`, width/2, height/2);
        }
    </script>
</body>
</html>
