                </div>
                <div class="control-item">
                    <label for="slider-beta">β</label>
                    <input type="range" id="slider-beta" min="0.1" max="1.0" step="0.01" value="0.5">
                    <span id="label-beta">0.5</span>
                </div>
                <div class="control-item">
                    <label for="slider-w">w</label>
                    <input type="range" id="slider-w" min="0.1" max="2.0" step="0.01" value="1.0">
                    <span id="label-w">1.0</span>
                </div>
                <div class="control-item">
                    <label for="slider-r">r</label>
                    <input type="range" id="slider-r" min="0.1" max="2.0" step="0.01" value="1.0">
                    <span id="label-r">1.0</span>
                </div>
            </div>
        </div>

        <!-- 2. Problem Control Panel -->
        <div id="problem-controls-panel">
            <div class="problem-column">
                <p>primal problem: minimize costs C given volume Q</p>
                <div class="control-item">
                    <label for="slider-Q">Q</label>
                    <input type="range" id="slider-Q" min="1" max="10" step="0.1" value="3">
                    <span id="label-Q">3</span>
                </div>
                <button id="btn-Q-from-dual">Q from dual</button>
            </div>
            <div class="problem-column">
                <p>dual problem: maximize volume Q given costs C</p>
                <div class="control-item">
                    <label for="slider-C">C</label>
                    <input type="range" id="slider-C" min="1" max="10" step="0.1" value="4">
                    <span id="label-C">4</span>
                </div>
                <button id="btn-C-from-primal">C from primal</button>
            </div>
        </div>

        <!-- 3. Visualization Area -->
        <div id="visualization-area">
            <div id="plot-primal" class="plot-container"></div>
            <div id="plot-dual" class="plot-container"></div>
        </div>

        <!-- 4. Output Display -->
        <div id="output-area">
            <div id="output-primal" class="output-box">x = 3.00, y = 3.00, C = 6.00</div>
            <div id="output-dual" class="output-box">x = 2.00, y = 2.00, Q = 2.00</div>
        </div>
    </div>

    <script>
        // Global variables
        let alpha, beta, w, r, Q, C;
        let primalScene, dualScene, primalRenderer, dualRenderer, primalCamera, dualCamera;
        let primalControls, dualControls;

        // Initialize three.js scenes
        function initThreeJS() {
            // Primal plot setup
            const primalContainer = document.getElementById('plot-primal');
            primalScene = new THREE.Scene();
            primalCamera = new THREE.PerspectiveCamera(75, primalContainer.clientWidth / primalContainer.clientHeight, 0.1, 1000);
            primalRenderer = new THREE.WebGLRenderer({ antialias: true });
            primalRenderer.setSize(primalContainer.clientWidth, primalContainer.clientHeight);
            primalRenderer.setClearColor(0xf0f0f0);
            primalContainer.appendChild(primalRenderer.domElement);

            // Dual plot setup
            const dualContainer = document.getElementById('plot-dual');
            dualScene = new THREE.Scene();
            dualCamera = new THREE.PerspectiveCamera(75, dualContainer.clientWidth / dualContainer.clientHeight, 0.1, 1000);
            dualRenderer = new THREE.WebGLRenderer({ antialias: true });
            dualRenderer.setSize(dualContainer.clientWidth, dualContainer.clientHeight);
            dualRenderer.setClearColor(0xf0f0f0);
            dualContainer.appendChild(dualRenderer.domElement);

            // Controls
            primalControls = new THREE.OrbitControls(primalCamera, primalRenderer.domElement);
            dualControls = new THREE.OrbitControls(dualCamera, dualRenderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            primalScene.add(ambientLight.clone());
            dualScene.add(ambientLight.clone());

            // Setup cameras
            primalCamera.position.set(6, 6, 6);
            dualCamera.position.set(6, 6, 6);

            // Add bounding box and axes
            const boxGeometry = new THREE.BoxGeometry(10, 10, 10);
            const boxHelper = new THREE.BoxHelper(new THREE.Box3().setFromCenterAndSize(new THREE.Vector3(0, 0, 0), new THREE.Vector3(10, 10, 10)));
            boxHelper.material.color.setHex(0x888888);
            primalScene.add(boxHelper.clone());
            dualScene.add(boxHelper.clone());

            // Axes
            const axesHelper = new THREE.AxesHelper(10);
            primalScene.add(axesHelper.clone());
            dualScene.add(axesHelper.clone());
        }

        // Update visualization
        function updateVisualization() {
            // Clear previous objects
            while (primalScene.children.length > 3) {
                primalScene.remove(primalScene.children[primalScene.children.length - 1]);
            }
            while (dualScene.children.length > 3) {
                dualScene.remove(dualScene.children[dualScene.children.length - 1]);
            }

            // Primal problem visualization
            // Orange isoquant curtain
            const primalPoints = [];
            const step = 0.1;
            for (let x = 0.1; x <= 10; x += step) {
                const y = Math.pow(Q / Math.pow(x, alpha), 1/beta);
                if (y <= 10) {
                    primalPoints.push(new THREE.Vector3(x, y, 0));
                    primalPoints.push(new THREE.Vector3(x, y, 10));
                }
            }
            const primalGeometry = new THREE.BufferGeometry().setFromPoints(primalPoints);
            const primalMaterial = new THREE.LineBasicMaterial({ color: 0xff6f00 });
            const primalLine = new THREE.LineSegments(primalGeometry, primalMaterial);
            primalScene.add(primalLine);

            // Gray isocost plane
            const primalSol = primalSolution();
            const planeGeometry = new THREE.PlaneGeometry(20, 20);
            const planeMaterial = new THREE.MeshLambertMaterial({ 
                color: 0x808080, 
                transparent: true, 
                opacity: 0.3,
                side: THREE.DoubleSide 
            });
            const isocostPlane = new THREE.Mesh(planeGeometry, planeMaterial);
            isocostPlane.position.set(5, 5, primalSol.c);
            isocostPlane.rotation.x = Math.atan(-w/r);
            isocostPlane.rotation.z = Math.atan(-w/r);
            primalScene.add(isocostPlane);

            // Blue solution vector
            const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(primalSol.x, primalSol.y, primalSol.c)
            ]);
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 3 });
            const solutionLine = new THREE.Line(lineGeometry, lineMaterial);
            primalScene.add(solutionLine);

            // Dual problem visualization
            // Orange production surface
            const plotPoints = [];
            for (let x = 0.1; x <= 10; x += 0.5) {
                for (let y = 0.1; y <= 10; y += 0.5) {
                    const z = Math.pow(x, alpha) * Math.pow(y, beta);
                    if (z <= 10) {
                        plotPoints.push(new THREE.Vector3(x, y, z));
                    }
                }
            }
            const surfaceGeometry = new THREE.BufferGeometry().setFromPoints(plotPoints);
            const surfaceMaterial = new THREE.PointsMaterial({ color: 0xff9800, size: 0.1 });
            const surface = new THREE.Points(surfaceGeometry, surfaceMaterial);
            dualScene.add(surface);

            // Gray budget curtain
            const dualPoints = [];
            for (let x = 0.1; x <= 10; x += step) {
                const y = (C - w * x) / r;
                if (y > 0 && y <= 10) {
                    dualPoints.push(new THREE.Vector3(x, y, 0));
                    dualPoints.push(new THREE.Vector3(x, y, 10));
                }
            }
            const dualGeometry = new THREE.BufferGeometry().setFromPoints(dualPoints);
            const dualMaterial = new THREE.LineBasicMaterial({ color: 0x808080 });
            const dualLine = new THREE.LineSegments(dualGeometry, dualMaterial);
            dualScene.add(dualLine);

            // Red intersection curve
            const intersectionPoints = [];
            for (let x = 0.1; x <= 10; x += step) {
                const y = (C - w * x) / r;
                if (y > 0) {
                    const z = Math.pow(x, alpha) * Math.pow(y, beta);
                    intersectionPoints.push(new THREE.Vector3(x, y, z));
                }
            }
            const intersectionGeometry = new THREE.BufferGeometry().setFromPoints(intersectionPoints);
            const intersectionMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
            const intersectionCurve = new THREE.Line(intersectionGeometry, intersectionMaterial);
            dualScene.add(intersectionCurve);

            // Gray sphere at optimal point
            const dualSol = dualSolution();
            const sphereGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const sphereMaterial = new THREE.MeshLambertMaterial({ color: 0x808080 });
            const optimalSphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            optimalSphere.position.set(dualSol.x, dualSol.y, dualSol.q);
            dualScene.add(optimalSphere);

        // Update output displays
            document.getElementById('output-primal').textContent = 
                `x = ${primalSol.x.toFixed(2)}, y = ${primalSol.y.toFixed(2)}, C = ${primalSol.c.toFixed(2)}`;
            document.getElementById('output-dual').textContent = 
                `x = ${dualSol.x.toFixed(2)}, y = ${dualSol.y.toFixed(2)}, Q = ${dualSol.q.toFixed(2)}`;
        }

        function primalSolution() {
            const k = (w * beta) / (r * alpha);
            const x = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y = x * k;
            const c = w * x + r * y;
            return { x, y, c };
        }

        function dualSolution() {
            const x = (C * alpha) / (w * (alpha + beta));
            const y = (C * beta) / (r * (alpha + beta));
            const q = Math.pow(x, alpha) * Math.pow(y, beta);
            return { x, y, q };
        }

        // Event listeners
        function setupEventListeners() {
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', (e) => {
                    const value = parseFloat(e.target.value);
                    const id = e.target.id;
                    const label = document.getElementById(`label-${id.split('-')[1]}`);
                    label.textContent = value.toString();

                    // Update global variables
                    switch(id) {
                        case 'slider-alpha': alpha = value; break;
                        case 'slider-beta': beta = value; break;
                        case 'slider-w': w = value; break;
                        case 'slider-r': r = value; break;
                        case 'slider-Q': Q = value; break;
                        case 'slider-C': C = value; break;
                    }

                    update();
                });
            });

            document.getElementById('btn-reset').addEventListener('click', () => {
                alpha = 0.5; beta = 0.5; w = 1.0; r = 1.0; Q = 3.0; C = 4.0;
                document.getElementById('slider-alpha').value = alpha;
                document.getElementById('slider-beta').value = beta;
                document.getElementById('slider-w').value = w;
                document.getElementById('slider-r').value = r;
                document.getElementById('slider-Q').value = Q;
                document.getElementById('slider-C').value = C;
                document.getElementById('label-alpha').textContent = alpha.toString();
                document.getElementById('label-beta').textContent = beta.toString();
                document.getElementById('label-w').textContent = w.toString();
                document.getElementById('label-r').textContent = r.toString();
                document.getElementById('label-Q').textContent = Q.toString();
                document.getElementById('label-C').textContent = C.toString();
                update();
            });

            document.getElementById('btn-C-from-primal').addEventListener('click', () => {
                const primalSol = primalSolution();
                C = primalSol.c;
                document.getElementById('slider-C').value = C;
                document.getElementById('label-C').textContent = C.toFixed(2);
                update();
            });

            document.getElementById('btn-Q-from-dual').addEventListener('click', () => {
                const dualSol = dualSolution();
                Q = dualSol.q;
                document.getElementById('slider-Q').value = Q;
                document.getElementById('label-Q').textContent = Q.toFixed(2);
                update();
            });
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            primalControls.update();
            dualControls.update();

            primalRenderer.render(primalScene, primalCamera);
            dualRenderer.render(dualScene, dualCamera);
        }

        function update() {
            updateVisualization();
        }

        // Initialize
        alpha = 0.5;
        beta = 0.5;
        w = 1.0;
        r = 1.0;
        Q = 3.0;
        C = 4.0;

        initThreeJS();
        setupEventListeners();
        update();
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            const primalContainer = document.getElementById('plot-primal');
            const dualContainer = document.getElementById('plot-dual');

            primalCamera.aspect = primalContainer.clientWidth / primalContainer.clientHeight;
            primalCamera.updateProjectionMatrix();
            primalRenderer.setSize(primalContainer.clientWidth, primalContainer.clientHeight);

            dualCamera.aspect = dualContainer.clientWidth / dualContainer.clientHeight;
            dualCamera.updateProjectionMatrix();
            dualRenderer.setSize(dualContainer.clientWidth, dualContainer.clientHeight);
        });
    </script>
</body>
</html>
