    text("x = 0.25 n-pentane", 50, 290);
    text("x = 0.25 n-heptane", 50, 305);
    text("x = 0.25 n-octane", 50, 320);

    // Vapor info
    text("Vapor", 560, 85);
    text("T = " + flashResults.T_flash.toFixed(1) + " K", 560, 100);
    text("P = " + parseInt(document.getElementById('slider-pressure').value) + " kPa", 560, 115);
    text("y = " + flashResults.y[0].toFixed(3) + " n-butane", 560, 130);
    text("y = " + flashResults.y[1].toFixed(3) + " n-pentane", 560, 145);
    text("y = " + flashResults.y[2].toFixed(3) + " n-heptane", 560, 160);
    text("y = " + flashResults.y[3].toFixed(3) + " n-octane", 560, 175);

    // Liquid info
    text("Liquid", 560, 385);
    text("T = " + flashResults.T_flash.toFixed(1) + " K", 560, 400);
    text("P = " + parseInt(document.getElementById('slider-pressure').value) + " kPa", 560, 415);
    text("x = " + flashResults.x[0].toFixed(3) + " n-butane", 560, 430);
    text("x = " + flashResults.x[1].toFixed(3) + " n-pentane", 560, 445);
    text("x = " + flashResults.x[2].toFixed(3) + " n-heptane", 560, 460);
    text("x = " + flashResults.x[3].toFixed(3) + " n-octane", 560, 475);

    // Process info
    text("Process Outputs", 220, 330);
    text("Vapor fraction = " + flashResults.V_frac.toFixed(3), 220, 345);
    text("Flash temperature = " + flashResults.T_flash.toFixed(1) + " K", 220, 360);
    text("Flash pressure = " + parseInt(document.getElementById('slider-pressure').value) + " kPa", 220, 375);

    // Heat info
    text("Q = " + parseInt(document.getElementById('slider-heat').value) + " kJ/hr", 420, 135);
}

function updateVisualization() {
    let P = parseInt(document.getElementById('slider-pressure').value);
    let Q = parseInt(document.getElementById('slider-heat').value);

    document.getElementById('pressure-value').textContent = P + ' kPa';
    document.getElementById('heat-value').textContent = Q + ' kJ/hr';

    calculateFlash(P, Q);
}
</script>

</body>
