        </div>
        <div id="visualization-container">
            <canvas id="scene-canvas"></canvas>
        </div>
    </div>

    <script>
        const params = {
            time: 0,
            nodal: 0,
            sunEarth: 1.571,
            zodiac: 0,
            constellations: true,
            realistic: false
        };
        let isAnimating = { time: false, nodal: false, sunEarth: false, zodiac: false };
        let animationId = null;
        let lastTime = 0;
        let canvas = document.getElementById('scene-canvas');
        let renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.shadowMap.enabled = true;
        document.getElementById('visualization-container').appendChild(renderer.domElement);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(60, canvas.clientWidth / canvas.clientHeight, 0.1, 5000);
        const light = new THREE.DirectionalLight(0xffffff, 2);
        const ambient = new THREE.AmbientLight(0x404040);
        const earth = new THREE.Mesh(
            new THREE.SphereGeometry(1, 32, 32),
            new THREE.MeshPhongMaterial({ color: 0x4488ff })
        );
        const earthGroup = new THREE.Group();
        const atmosphere = new THREE.Mesh(
            new THREE.SphereGeometry(1.03, 32, 32),
            new THREE.MeshBasicMaterial({ color: 0x4488ff, transparent: true, opacity: 0.3, blending: THREE.AdditiveBlending })
        );
        const starsGeo = new THREE.BufferGeometry();
        const starMat = new THREE.PointsMaterial({ color: 0xffffee, size: 1.5 });
        const earthOrbitGeo = new THREE.BufferGeometry();
        const earthOrbitMat = new THREE.LineBasicMaterial({ color: 0x00ffff });

        function createMoonTexture() {
            const canvasTex = document.createElement('canvas');
            canvasTex.width = canvasTex.height = 512;
            const ctx = canvasTex.getContext('2d');
            ctx.fillStyle = '#aaa';
            ctx.fillRect(0, 0, 512, 512);
            for (let i = 0; i < 500; i++) {
                const x = Math.random() * 512;
                const y = Math.random() * 512;
                const r = 5 + Math.random() * 25;
                const grd = ctx.createRadialGradient(x, y, 0, x, y, r);
                grd.addColorStop(0, '#555');
                grd.addColorStop(1, '#999');
                ctx.fillStyle = grd;
                ctx.beginPath();
                ctx.arc(x, y, r, 0, Math.PI * 2);
                ctx.fill();
            }
            const tex = new THREE.CanvasTexture(canvasTex);
            tex.wrapS = tex.wrapT = THREE.RepeatWrapping;
            tex.repeat.set(4, 2);
            return tex;
        }
        const moonTexture = createMoonTexture();
        const moon = new THREE.Mesh(
            new THREE.SphereGeometry(1, 32, 32),
            new THREE.MeshStandardMaterial({ map: moonTexture, bumpMap: moonTexture, bumpScale: 0.1 })
        );
        const surface = new THREE.Mesh(
            new THREE.PlaneGeometry(100, 100),
            new THREE.MeshStandardMaterial({ map: moonTexture, bumpMap: moonTexture, bumpScale: 0.1 })
        );
        surface.position.set(0, -1.5, 0);
        surface.rotation.x = -Math.PI / 2;

        const moonGroup = new THREE.Group();
        moonGroup.add(moon);
        moonGroup.add(surface);
        scene.add(moonGroup);

        earth.castShadow = true;
        earthGroup.add(earth);
        earthGroup.add(atmosphere);
        moon.castShadow = true;
        moon.receiveShadow = true;
        surface.receiveShadow = true;

        const constellationGroup = new THREE.Group();
        scene.add(constellationGroup);

        light.castShadow = true;
        light.shadow.camera.near = 0.1;
        light.shadow.camera.far = 100;
        scene.add(light);
        scene.add(ambient);

        let viewMode = 'land';
        let earthRadius = 0.014; // Realistic angular diameter ≈ 1.9° as seen from Moon
        let currentSize = 10;
        const targetSizeMap = {
            true: earthRadius,
            false: 1
        };

        // Constellation data (zodiac, simplified)
        const zodiac = {
            stars: [
                { x: 60, y: 0, z: 1 },
                { x: 62, y: 0.5, z: 0 },
                { x: 61, y: 0.3, z: 0.8 },
                { x: 59, y: -0.2, z: 0.5 },
                { x: 58, y: 0.4, z: 0.3 },
                { x: 61, y: 0.8, z: 0.9 },
                { x: 57, y: 0.1, z: 0.2 }
            ],
            lines: [
                [0, 1],
                [1, 2],
                [2, 3],
                [3, 0],
                [3, 4],
                [4, 5],
                [5, 6],
                [6, 2]
            ]
        };

        function initConstellations() {
            const starPositions = [];
            zodiac.stars.forEach(s => starPositions.push(s.x, s.y, s.z));
            starsGeo.setAttribute('position', new THREE.Float32BufferAttribute(starPositions, 3));
            const stars = new THREE.Points(starsGeo, starMat);
            constellationGroup.add(stars);

            const linePositions = [];
            zodiac.lines.forEach(l => {
                const s0 = zodiac.stars[l[0]];
                const s1 = zodiac.stars[l[1]];
                linePositions.push(s0.x, s0.y, s0.z);
                linePositions.push(s1.x, s1.y, s1.z);
            });
            earthOrbitGeo.setAttribute('position', new THREE.Float32BufferAttribute(linePositions, 3));
            const lines = new THREE.LineSegments(earthOrbitGeo, earthOrbitMat);
            constellationGroup.add(lines);
        }
        initConstellations();

        function updateView() {
            if (viewMode === 'land') {
                camera.position.set(0, 1.5, 2);
                camera.lookAt(0, 30, 0);
                surface.visible = true;
                earthGroup.visible = false;
                constellationGroup.visible = false;
            } else {
                camera.position.set(0, 0, 30);
                camera.lookAt(0, 0, 0);
                surface.visible = false;
                earthGroup.visible = true;
                constellationGroup.visible = params.constellations;
            }
            updateScene();
        }

        function animateToSize(target, speed = 0.05) {
            const step = (target - currentSize) * speed;
            if (Math.abs(step) < 0.001) {
                currentSize = target;
                earth.scale.set(currentSize, currentSize, currentSize);
                return;
            }
            currentSize += step;
            earth.scale.set(currentSize, currentSize, currentSize);
            requestAnimationFrame(() => animateToSize(target, speed));
        }

        function setEarthScale() {
            const target = targetSizeMap[params.realistic];
            animateToSize(target);
        }

        function updateScene() {
            // Earth position based on time angle, nodal tilt
            const R = 8.605; // semi-major of selenocentric orbit (~6.5° up to close approach)
            const phaseDeg = params.time * 3600; // day advance
            const nodalRad = params.nodal;
            const sunEarthRad = params.sunEarth;
            const x = R * Math.cos(phaseDeg);
            const y = R * Math.sin(phaseDeg) * Math.cos(nodalRad);
            const z = R * Math.sin(phaseDeg) * Math.sin(nodalRad);

            const earthToMoon = new THREE.Vector3(-x, -y, -z);
            earthGroup.position.copy(earthToMoon);
            earth.rotation.y = phaseDeg * 0.1; // spin
            atmosphere.rotation.y = phaseDeg * 0.1;

            // Sun position based on initial Sun-Earth angle
            const sunDir = new THREE.Vector3(-Math.cos(sunEarthRad), -0.3, -Math.sin(sunEarthRad)).normalize();
            light.position.copy(sunDir.multiplyScalar(50));

            // Constellation rotation
            constellationGroup.rotation.y = params.zodiac;

            // Eclipse check: when Earth lies on Sun-Moon line (roughly within ~1.0 angular radius)
            const dist = Math.sqrt(x * x + y * y);
            const distToShadowCenter = (sunEarthRad < Math.PI / 6 || sunEarthRad > 5 * Math.PI / 6) ? 0 : dist;
            const shadowRadius = 2;
            const isEclipsed = distToShadowCenter < shadowRadius;

            if (isEclipsed) {
                earth.material.color.set(0xff6666);
                atmosphere.material.color.set(0xff6666);
            } else {
                earth.material.color.set(0x4488ff);
                atmosphere.material.color.set(0x4488ff);
            }

            renderer.render(scene, camera);
        }

        function resizeCanvas() {
            const width = canvas.clientWidth;
            const height = canvas.clientHeight;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            updateScene();
        }
        resizeCanvas();
        window.addEventListener('resize', resizeCanvas);

        let selected = 'land';
        document.getElementById('btn-land').addEventListener('click', () => {
            document.getElementById('btn-land').classList.add('active');
            document.getElementById('btn-space').classList.remove('active');
            viewMode = 'land';
            updateView();
        });
        document.getElementById('btn-space').addEventListener('click', () => {
            document.getElementById('btn-space').classList.add('active');
            document.getElementById('btn-land').classList.remove('active');
            viewMode = 'space';
            updateView();
        });

        const sliders = [
            { id: 'time', val: 'time', displayId: 'slider-time-value' },
            { id: 'nodal-angle', val: 'nodal', displayId: 'slider-nodal-angle-value' },
            { id: 'sun-earth-angle', val: 'sunEarth', displayId: 'slider-sun-earth-angle-value' },
            { id: 'zodiac', val: 'zodiac', displayId: 'slider-zodiac-value' }
        ];
        sliders.forEach(s => {
            const el = document.getElementById('slider-' + s.id);
            const sp = document.getElementById(s.displayId);
            el.addEventListener('input', () => {
                params[s.val] = parseFloat(el.value);
                sp.textContent = s.id === 'sun-earth-angle' ? Math.PI / 2 === params[s.val] ? '\\(\\frac{\pi}{2}\\)' : params[s.val].toFixed(2) : params[s.val].toFixed(2);
                if (s.id === 'sun-earth-angle') {
                    sp.textContent = Math.abs(params[s.val] - 1.571) < 0.01 ? '\\(\\frac{\pi}{2}\\)' : params[s.val].toFixed(2);
                    MathJax.typeset([sp]);
                }
                updateScene();
            });
        });

        const checkboxes = [
            { id: 'constellations', val: 'constellations' },
            { id: 'realistic-size', val: 'realistic' }
        ];
        checkboxes.forEach(c => {
            const el = document.getElementById('checkbox-' + c.id);
            params[c.val] = el.checked;
            el.addEventListener('change', () => {
                params[c.val] = el.checked;
                if (c.id === 'constellations') {
                    constellationGroup.visible = params.constellations;
                }
                if (c.id === 'realistic-size') {
                    setEarthScale();
                }
                updateScene();
            });
        });

        const anims = [
            { id: 'time', val: 'time', btn: 'btn-time-play' },
            { id: 'nodal-angle', val: 'nodal', btn: 'btn-nodal-angle-play' },
            { id: 'sun-earth-angle', val: 'sunEarth', btn: 'btn-sun-earth-angle-play' },
            { id: 'zodiac', val: 'zodiac', btn: 'btn-zodiac-play' }
        ];
        anims.forEach(a => {
            const el = document.getElementById(a.btn);
            const slider = document.getElementById('slider-' + a.id);
            el.addEventListener('click', () => {
                isAnimating[a.val] = !isAnimating[a.val];
                el.classList.toggle('paused', !isAnimating[a.val]);
            });
        });

        function mainLoop(ts) {
            if (!lastTime) lastTime = ts;
            const dt = ts - lastTime; // milliseconds
            lastTime = ts;

            if (isAnimating.time) {
                params.time += dt * 0.002;
                if (params.time > 100) params.time = 0;
                document.getElementById('slider-time').value = params.time;
                document.getElementById('slider-time-value').textContent = params.time.toFixed(2);
            }
            if (isAnimating.nodal) {
                params.nodal += dt * 0.003;
                if (params.nodal > 6.283) params.nodal -= 6.283;
                document.getElementById('slider-nodal-angle').value = params.nodal;
                document.getElementById('slider-nodal-angle-value').textContent = params.nodal.toFixed(2);
            }
            if (isAnimating.sunEarth) {
                params.sunEarth += dt * 0.002;
                if (params.sunEarth > 6.283) params.sunEarth -= 6.283;
                document.getElementById('slider-sun-earth-angle').value = params.sunEarth;
                const sp = document.getElementById('slider-sun-earth-angle-value');
                sp.textContent = Math.abs(params.sunEarth - 1.571) < 0.01 ? '\\(\\frac{\pi}{2}\\)' : params.sunEarth.toFixed(2);
                MathJax.typeset([sp]);
            }
            if (isAnimating.zodiac) {
                params.zodiac += dt * 0.001;
                if (params.zodiac > 6.283) params.zodiac -= 6.283;
                document.getElementById('slider-zodiac').value = params.zodiac;
                document.getElementById('slider-zodiac-value').textContent = params.zodiac.toFixed(2);
            }

            updateScene();
            animationId = requestAnimationFrame(mainLoop);
        }

        updateView();
        setEarthScale();
        mainLoop(0);
    </script>
</body>
</html>
