    <script>
        const sliderResidenceTime = document.getElementById('slider-residence-time');
        const valueResidenceTime = document.getElementById('value-residence-time');
        const sliderTransitionPace = document.getElementById('slider-transition-pace');
        const valueTransitionPace = document.getElementById('value-transition-pace');
        const sliderWedges = document.getElementById('slider-wedges');
        const valueWedges = document.getElementById('value-wedges');

        const C0 = 850;      // Initial atmospheric carbon (GtC)
        const E0 = 8.5;      // Initial emission rate (GtC/yr)
        const C_eq = 586;    // Pre-industrial equilibrium
        const E_floor = 1.5; // Emission floor
        const rise_rate = 0.02; // Emission increase rate during transition (GtC/yr^2)
        const time_step = 0.5;  // Time step for integration (years)
        const max_time = 300;   // Simulation duration (years)

        function calculateModel() {
            const t_res = parseFloat(sliderResidenceTime.value);
            const t_trans = parseFloat(sliderTransitionPace.value);
            const N_wedges = parseFloat(sliderWedges.value);

            const E_peak = E0 + rise_rate * t_trans;

            const timeArray = [];
            const emissionsArray = [];
            const concentrationArray = [];

            let C = C0;

            for (let t = 0; t <= max_time; t += time_step) {
                let E;
                if (t < t_trans) {
                    E = E0 + rise_rate * t;
                } else {
                    const reduction_rate = N_wedges / 50;
                    const E_linear = E_peak - reduction_rate * (t - t_trans);
                    E = Math.max(E_floor, E_linear);
                }

                const dC = (E - (C - C_eq) / t_res) * time_step;
                C += dC;

                timeArray.push(t);
                emissionsArray.push(100 * E);  // Scale for plotting
                concentrationArray.push(C);
            }

            return { timeArray, emissionsArray, concentrationArray };
        }

        function updatePlot() {
            const { timeArray, emissionsArray, concentrationArray } = calculateModel();

            const data = [
                {
                    x: timeArray,
                    y: concentrationArray,
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    name: 'Atmospheric Carbon'
                },
                {
                    x: timeArray,
                    y: emissionsArray,
                    mode: 'lines',
                    line: { color: 'purple', width: 2 },
                    name: 'Annual Emissions x100'
                },
                {
                    x: [0, 300],
                    y: [1200, 1200],
                    mode: 'lines',
                    line: { color: 'black', width: 2, dash: 'dash' },
                    name: 'Target Ceiling'
                }
            ];

            const layout = {
                title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                xaxis: { title: 'years', range: [0, 300], zeroline: true },
                yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true },
                showlegend: false,
                margin: { l: 60, r: 20, b: 40, t: 80 },
                paper_bgcolor: '#f9f9f9',
                plot_bgcolor: 'white',
                grid: { color: '#ddd' }
            };

            Plotly.react('plot-div', data, layout);
        }

        // Attach event listeners to sliders
        sliderResidenceTime.addEventListener('input', () => {
            valueResidenceTime.textContent = sliderResidenceTime.value;
            updatePlot();
        });

        sliderTransitionPace.addEventListener('input', () => {
            valueTransitionPace.textContent = sliderTransitionPace.value;
            updatePlot();
        });

        sliderWedges.addEventListener('input', () => {
            valueWedges.textContent = parseFloat(sliderWedges.value).toFixed(2);
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
