            overflow: hidden;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h2 style="text-align: center; margin-top: 0;">Pendulum Controls</h2>

            <div class="slider-container">
                <label for="slider-theta0">θ<sub>0</sub>:</label>
                <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" step="0.01" value="0.5236">
                <span id="value-theta0">π/6</span>
            </div>

            <div class="slider-container">
                <label for="slider-v0">v<sub>0</sub>:</label>
                <input type="range" id="slider-v0" min="-5" max="5" step="0.1" value="0">
                <span id="value-v0">0</span>
            </div>

            <div class="slider-container">
                <label for="slider-length">Connector length:</label>
                <input type="range" id="slider-length" min="0.5" max="4" step="0.1" value="2">
                <span id="value-length">2</span>
            </div>

            <div id="animation-controls">
                <button id="btn-play-pause">▶</button>
                <button id="btn-reset">|<</button>
            </div>
        </div>

        <div id="visualization-area"></div>
    </div>

    <script>
        let isPlaying = false;
        let theta, theta_dot, theta_ddot;
        let x, x_dot;
        let g = 9.8;
        let m = 1, M = 1;
        let dt = 0.016;
        let scale = 50;

        function formatAngle(rad) {
            if (Math.abs(rad) < 0.01) return "0";

            const fractions = [
                {value: Math.PI/6, str: "π/6"},
                {value: Math.PI/4, str: "π/4"},
                {value: Math.PI/3, str: "π/3"},
                {value: Math.PI/2, str: "π/2"},
                {value: 2*Math.PI/3, str: "2π/3"},
                {value: 3*Math.PI/4, str: "3π/4"},
                {value: 5*Math.PI/6, str: "5π/6"},
                {value: Math.PI, str: "π"}
            ];

            for (let frac of fractions) {
                if (Math.abs(rad - frac.value) < 0.01) return frac.str;
                if (Math.abs(rad + frac.value) < 0.01) return "-" + frac.str;
            }

            return rad.toFixed(2) + " rad";
        }

        function resetSimulation() {
            theta = parseFloat(document.getElementById('slider-theta0').value);
            theta_dot = 0;
            x = 0;
            x_dot = parseFloat(document.getElementById('slider-v0').value);

            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';

            if (drawingBuffer) {
                drawPendulum();
            }
        }

        function updateSliderDisplays() {
            const theta0Val = parseFloat(document.getElementById('slider-theta0').value);
            document.getElementById('value-theta0').textContent = formatAngle(theta0Val);
            document.getElementById('value-v0').textContent = document.getElementById('slider-v0').value;
            document.getElementById('value-length').textContent = document.getElementById('slider-length').value;
        }

        // Initialize p5.js sketch
        let drawingBuffer = null;

        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.parent('visualization-area');
            drawingBuffer = true;
            resetSimulation();
            updateSliderDisplays();
        }

        function drawPendulum() {
            background('#F0F0F0');

            const l = parseFloat(document.getElementById('slider-length').value);
            const px = width / 2 + x * scale;
            const py = height / 2;

            const bx = px + (l * scale) * sin(theta);
            const by = py + (l * scale) * cos(theta);

            stroke('black');
            strokeWeight(2);
            line(px, py, bx, by);

            fill('red');
            noStroke();
            rectMode(CENTER);
            rect(px, py, 30, 20);

            fill('orange');
            stroke('black');
            strokeWeight(2);
            circle(bx, by, 20);
        }

        function draw() {
            if (isPlaying) {
                const l = parseFloat(document.getElementById('slider-length').value);

                // Calculate accelerations
                theta_ddot = (-m * l * sin(theta) * cos(theta) * theta_dot * theta_dot - g * (M + m) * sin(theta)) / 
                            (l * (M + m * sin(theta) * sin(theta)));

                theta_dot += theta_ddot * dt;
                theta += theta_dot * dt;

                x_dot = parseFloat(document.getElementById('slider-v0').value) - (m * l * cos(theta) * theta_dot) / (M + m);
                Math.abs(theta - Math.PI) < 0.1 || Math.abs(theta + Math.PI) < 0.1
                x += x_dot * dt;
            }

            drawPendulum();
        }

        // Event listeners
        document.getElementById('slider-theta0').addEventListener('input', () => {
            updateSliderDisplays();
            resetSimulation();
        });

        document.getElementById('slider-v0').addEventListener('input', () => {
            updateSliderDisplays();
            resetSimulation();
        });

        document.getElementById('slider-length').addEventListener('input', () => {
            updateSliderDisplays();
            resetSimulation();
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isPlaying = !isPlaying;
            document.getElementById('btn-play-pause').textContent = isPlaying ? '||' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', resetSimulation);
    </script>
</body>
</html>
