<body>
    <h1>Deformation Pattern in an Earthquake Source Region</h1>

    <div id="control-panel">
        <div class="slider-group">
            <label for="slider-strike">strike angle φ of fault plane clockwise from North (0° ≤ φ &lt; 360°)</label>
            <input type="range" id="slider-strike" min="0" max="360" value="180" step="1">
            <input type="text" id="value-strike" value="180" readonly>
        </div>

        <div class="slider-group">
            <label for="slider-dip">dip angle δ of fault plane down from horizontal (0° ≤ δ ≤ 90°)</label>
            <input type="range" id="slider-dip" min="0" max="90" value="60" step="1">
            <input type="text" id="value-dip" value="60" readonly>
        </div>

        <div class="slider-group">
            <label for="slider-slip-angle">slip angle λ in fault plane between slip direction and strike (−180° ≤ λ &lt; 180°)</label>
            <input type="range" id="slider-slip-angle" min="-180" max="180" value="90" step="1">
            <input type="text" id="value-slip-angle" value="90" readonly>
        </div>

        <div class="slider-group">
            <label for="slider-slip-amount">amount of slip of hanging wall (0 to 1)</label>
            <input type="range" id="slider-slip-amount" min="0" max="1" value="0.5" step="0.01">
            <input type="text" id="value-slip-amount" value="0.5" readonly>
        </div>

        <button id="btn-reset">↺ Reset</button>
    </div>

    <div id="visualization-container">
        <div class="control-item">
            <input type="checkbox" id="checkbox-box">
            <label for="checkbox-box">box</label>
        </div>
        <div id="canvas-container">
            <canvas id="webgl-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let sphere, boxHelper;
        let labelGroup;
        let originalVertices = [];

        // Default values
        const defaults = {
            strike: 180,
            dip: 60,
            slipAngle: 90,
            slipAmount: 0.5,
            showBox: false
        };

        // Initialize the scene
        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera setup
            const canvas = document.getElementById('webgl-canvas');
            const container = document.getElementById('canvas-container');
            const aspect = container.clientWidth / container.clientHeight;
            camera = new THREE.PerspectiveCamera(75, aspect, 0.1, 1000);
            camera.position.set(3, 2, 3);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.1;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Create sphere
            createDeformedSphere();

            // Create compass rose and labels
            createCompassRose();

            // Create bounding box helper
            boxHelper = new THREE.BoxHelper(sphere, 0x888888);
            boxHelper.visible = defaults.showBox;
            scene.add(boxHelper);

            // Event listeners
            setupEventListeners();

            // Initial draw
            updateVisualization();

            // Animation loop
            animate();
        }

        function createDeformedSphere() {
            const geometry = new THREE.SphereGeometry(1, 64, 32);
            originalVertices = geometry.attributes.position.array.slice();

            const material = new THREE.MeshStandardMaterial({
                color: 0xffffff,
                vertexColors: true,
                metalness: 0.1,
                roughness: 0.5
            });

            sphere = new THREE.Mesh(geometry, material);
            scene.add(sphere);

            // Create color attribute
            const colors = new Float32Array(geometry.attributes.position.count * 3);
            geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));
        }

        function createCompassRose() {
            labelGroup = new THREE.Group();
            scene.add(labelGroup);

            // Compass rose
            const roseGeometry = new THREE.RingGeometry(1.5, 1.6, 64);
            const roseMaterial = new THREE.MeshBasicMaterial({ 
                color: 0xcccccc, 
                side: THREE.DoubleSide,
                opacity: 0.5,
                transparent: true
            });
            const rose = new THREE.Mesh(roseGeometry, roseMaterial);
            rose.rotation.x = -Math.PI / 2;
            labelGroup.add(rose);

            // Add labels
            const fontLoader = new THREE.FontLoader();

            // Cardinal directions
            const directions = [
                { text: 'N', pos: [0, 0, 1.7] },
                { text: 'E', pos: [1.7, 0, 0] },
                { text: 'S', pos: [0, 0, -1.7] },
                { text: 'W', pos: [-1.7, 0, 0] }
            ];

            directions.forEach(dir => {
                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = 64;
                canvas.height = 64;
                context.font = 'Bold 48px Arial';
                context.fillStyle = 'black';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(dir.text, 32, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                sprite.position.set(...dir.pos);
                sprite.scale.set(0.2, 0.2, 1);
                labelGroup.add(sprite);
            });
        }

        function updateVisualization() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const slipAngle = parseFloat(document.getElementById('slider-slip-angle').value);
            const slipAmount = parseFloat(document.getElementById('slider-slip-amount').value);

            // Convert to radians
            const φ = (strike * Math.PI) / 180;
            const δ = (dip * Math.PI) / 180;
            const λ = (slipAngle * Math.PI) / 180;

            // Calculate vectors in seismological coordinates
            // Fault normal vector n
            const n = new THREE.Vector3(
                -Math.sin(δ) * Math.sin(φ),
                -Math.sin(δ) * Math.cos(φ),
                -Math.cos(δ)
            );

            // Slip vector u
            const u = new THREE.Vector3(
                Math.cos(λ) * Math.cos(φ) + Math.sin(λ) * Math.cos(δ) * Math.sin(φ),
                -Math.cos(λ) * Math.sin(φ) + Math.sin(λ) * Math.cos(δ) * Math.cos(φ),
                -Math.sin(λ) * Math.sin(δ)
            );

            // P and T axes
            const P = n.clone().add(u).normalize();
            const T = u.clone().sub(n).normalize();

            // Map to three.js coordinates
            const P_th = new THREE.Vector3(-P.y, -P.z, -P.x);
            const T_th = new THREE.Vector3(-T.y, -T.z, -T.x);
            const U_th = new THREE.Vector3(-u.y, -u.z, -u.x);

            // Update colors and deformation
            const colors = sphere.geometry.attributes.color;
            const positions = sphere.geometry.attributes.position;

            for (let i = 0; i < positions.count; i++) {
                const x = originalVertices[i * 3];
                const y = originalVertices[i * 3 + 1];
                const z = originalVertices[i * 3 + 2];

                const vertex = new THREE.Vector3(x, y, z);

                // Determine quadrant color
                const PdotV = vertex.dot(P_th);
                const TdotV = vertex.dot(T_th);

                const isCompressive = PdotV * TdotV > 0;

                if (isCompressive) {
                    colors.setXYZ(i, 1, 1, 1); // white
                } else {
                    colors.setXYZ(i, 1, 0.64, 0); // orange
                }

                // Apply deformation
                const dotP = vertex.dot(P_th);
                const dotT = vertex.dot(T_th);
                const deformation = (dotT * dotT - dotP * dotP) * slipAmount * 0.5;

                positions.setXYZ(
                    i,
                    x * (1 + deformation * x * x),
                    y * (1 + deformation * y * y),
                    z * (1 + deformation * z * z)
                );
            }

            colors.needsUpdate = true;
            positions.needsUpdate = true;
            sphere.geometry.computeVertexNormals();

            // Update bounding box
            boxHelper.update();

            // Add P, T, U labels
            updateAxisLabels(P_th, T_th, U_th);
        }

        function updateAxisLabels(P, T, U) {
            // Remove old labels
            while(labelGroup.children.length > 5) {
                labelGroup.remove(labelGroup.children[labelGroup.children.length - 1]);
            }

            ['P', 'T', 'U'].forEach((axis, i) => {
                const vec = [P, T, U][i];
                const pos = vec.clone().multiplyScalar(1.05);

                const canvas = document.createElement('canvas');
                const context = canvas.getContext('2d');
                canvas.width = 64;
                canvas.height = 64;
                context.font = 'Bold 48px Arial';
                context.fillStyle = 'red';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(axis, 32, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                sprite.position.copy(pos);
                sprite.scale.set(0.3, 0.3, 1);
                labelGroup.add(sprite);
            });
        }

        function setupEventListeners() {
            // Slider events
            ['strike', 'dip', 'slip-angle', 'slip-amount'].forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const value = document.getElementById(`value-${param}`);

                slider.addEventListener('input', (e) => {
                    value.value = e.target.value;
                    updateVisualization();
                });
            });

            // Checkbox event
            document.getElementById('checkbox-box').addEventListener('change', (e) => {
                boxHelper.visible = e.target.checked;
            });

            // Reset button
            document.getElementById('btn-reset').addEventListener('click', () => {
                document.getElementById('slider-strike').value = defaults.strike;
                document.getElementById('value-strike').value = defaults.strike;

                document.getElementById('slider-dip').value = defaults.dip;
                document.getElementById('value-dip').value = defaults.dip;

                document.getElementById('slider-slip-angle').value = defaults.slipAngle;
                document.getElementById('value-slip-angle').value = defaults.slipAngle;

                document.getElementById('slider-slip-amount').value = defaults.slipAmount;
                document.getElementById('value-slip-amount').value = defaults.slipAmount;

                document.getElementById('checkbox-box').checked = defaults.showBox;
                boxHelper.visible = defaults.showBox;

                updateVisualization();
            });

            // Window resize
            window.addEventListener('resize', () => {
                const container = document.getElementById('canvas-container');
                camera.aspect = container.clientWidth / container.clientHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(container.clientWidth, container.clientHeight);
            });
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize the visualization
        init();
    </script>
</body>
</html>
