</head>
<body>
  <div id="app-container">
    <div id="control-panel">
      <div class="control-group">
        <label for="slider-db-size">database size</label>
        <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
        <div class="value-display">+<span id="display-db-size">10</span></div>
      </div>
      <div class="control-group">
        <label for="slider-iterations">number of iterations</label>
        <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
        <div class="value-display">+<span id="display-iterations">2</span></div>
      </div>
    </div>

    <div id="canvas-container"></div>
    <div id="probability-display"></div>
  </div>

  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
  <script>
    // Globals
    const targetIndex = 6;
    let N = 10;
    let k = 2;

    // Sliders
    const sliderDbSize = document.getElementById('slider-db-size');
    const displayDbSize = document.getElementById('display-db-size');
    const sliderIterations = document.getElementById('slider-iterations');
    const displayIterations = document.getElementById('display-iterations');
    const probabilityDisplay = document.getElementById('probability-display');

    // === Functions (utility) ===
    function updateDbSizeDisplay() { displayDbSize.textContent = N; }
    function updateIterationsDisplay() { displayIterations.textContent = k; }

    function calculateProbability(N_, k_) {
      const angle = (2 * k_ + 1) * Math.asin(1 / Math.sqrt(N_));
      return Math.pow(Math.sin(angle), 2);
    }

    function updateProbabilityDisplay() {
      const prob = calculateProbability(N, k) * 100;
      probabilityDisplay.textContent = `${prob.toFixed(1)}% success probability`;
    }

    // === P5.js sketch ===
    const sketch = (p) => {
      let canvasHeight, canvasWidth;

      p.setup = () => {
        canvasWidth = 780;
        canvasHeight = 480;
        const canvas = p.createCanvas(canvasWidth, canvasHeight);
        canvas.parent('canvas-container');
        updateDbSizeDisplay();
        updateIterationsDisplay();
        updateProbabilityDisplay();
      };

      p.draw = () => {
        // Clear
        p.background('white');

        const sectionHeight = canvasHeight / 4;

        // Titles and stages
        const titles = [
          "original amplitudes",
          "after first inversion",
          "after first diffusion",
          `after ${k} iterations`
        ];

        for (let stageIdx = 0; stageIdx < 4; stageIdx++) {
          const y0 = stageIdx * sectionHeight;
          const y1 = y0 + sectionHeight;
          const midY = (y0 + y1) / 2;
          const stageTitle = titles[stageIdx];

          // Title
          p.stroke('black');
          p.fill('black');
          p.textAlign(p.CENTER, p.TOP);
          p.text(stageTitle, canvasWidth / 2, y0 + 5);

          // Baseline
          p.stroke('lightgray');
          p.strokeWeight(1);
          p.line(40, midY, canvasWidth - 40, midY);

          // Prepare amplitudes
          let amps;
          switch (stageIdx) {
            case 0:
              amps = Array(N).fill(1 / Math.sqrt(N));
              break;
            case 1:
              amps = Array(N).fill(1 / Math.sqrt(N));
              amps[targetIndex] *= -1;
              break;
            case 2: {
              amps = Array(N).fill(1 / Math.sqrt(N));
              amps[targetIndex] *= -1;
              const mean = amps.reduce((a, b) => a + b, 0) / N;
              amps = amps.map(a => 2 * mean - a);
              break;
            }
            case 3: {
              // init
              amps = Array(N).fill(1 / Math.sqrt(N));
              for (let iter = 0; iter < k; iter++) {
                // oracle
                amps[targetIndex] *= -1;
                // diffusion
                const mean = amps.reduce((a, b) => a + b, 0) / N;
                amps = amps.map(a => 2 * mean - a);
              }
              break;
            }
          }

          // Draw bars
          const margin = 50;
          const barWidth = (canvasWidth - 2 * margin) / N * 0.6;
          const stepX = (canvasWidth - 2 * margin) / N;

          for (let i = 0; i < N; i++) {
            const x = margin + i * stepX + stepX / 2 - barWidth / 2;
            const a = amps[i];
            const h = a * 40;

            p.noStroke();
            if (i === targetIndex) {
              p.fill('#FFA500');
            } else {
              p.fill('#808080');
            }
            p.rect(x, midY - h / 2, barWidth, h);
          }

          // Parentheses around bars for stage 0
          if (stageIdx === 0) {
            const parenRadius = 5 + N * 0.5;
            const leftX = margin - 5;
            const rightX = margin + N * stepX + 5;

            p.noFill();
            p.stroke('#000');
            p.strokeWeight(2);
            // left arc
            p.arc(leftX, midY, parenRadius * 2, parenRadius * 2, -70, 70, p.OPEN);
            // right arc
            p.arc(rightX, midY, parenRadius * 2, parenRadius * 2, 110, 250, p.OPEN);
          }
        }
      };
    };

    // === Slider event listeners ===
    sliderDbSize.addEventListener('input', () => {
      N = parseInt(sliderDbSize.value, 10);
      updateDbSizeDisplay();
      updateProbabilityDisplay();
    });

    sliderIterations.addEventListener('input', () => {
      k = parseInt(sliderIterations.value, 10);
      updateIterationsDisplay();
      updateProbabilityDisplay();
    });

    // === Initialize p5.js sketch ===
    new p5(sketch);
  </script>
</body>
</html>
