            max-width: 100%;
            max-height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-volatility">relative volatility</label>
                <input type="range" id="slider-volatility" min="1.1" max="10" step="0.1" value="2.5">
                <button id="btn-volatility-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-distillate">distillate specification</label>
                <input type="range" id="slider-distillate" min="0.51" max="0.99" step="0.01" value="0.95">
                <button id="btn-distillate-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-bottom">bottom specification</label>
                <input type="range" id="slider-bottom" min="0.01" max="0.49" step="0.01" value="0.05">
                <button id="btn-bottom-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-comp">feed composition</label>
                <input type="range" id="slider-feed-comp" min="0.02" max="0.98" step="0.01" value="0.50">
                <button id="btn-feed-comp-plus">+</button>
            </div>
            <div class="control-group">
                <label for="slider-feed-quality">feed quality</label>
                <input type="range" id="slider-feed-quality" min="0.0" max="2.0" step="0.05" value="1.0">
                <button id="btn-feed-quality-plus">+</button>
            </div>
        </div>

        <div id="visualization-area">
            <p id="stages-count-display">The number of stages is 8.</p>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        let sketch = function(p) {
            let margin = 60;
            let plotSize = 500;
            let canvas;

            let alpha, xD, xB, xF, q;
            let stageCount = 8;

            p.setup = function() {
                canvas = p.createCanvas(600, 600);
                canvas.parent('canvas-container');
                p.noLoop();

                // Initialize initial values
                updateParams();
                drawDiagram();
            };

            function updateParams() {
                alpha = parseFloat(document.getElementById('slider-volatility').value);
                xD = parseFloat(document.getElementById('slider-distillate').value);
                xB = parseFloat(document.getElementById('slider-bottom').value);
                xF = parseFloat(document.getElementById('slider-feed-comp').value);
                q = parseFloat(document.getElementById('slider-feed-quality').value);

                // Ensure parameter constraints
                enforceConstraints();
            }

            function enforceConstraints() {
                let feedSlider = document.getElementById('slider-feed-comp');
                let xFBtn = document.getElementById('btn-feed-comp-plus');

                if (xB >= xF) {
                    xF = Math.min(xB + 0.01, 0.97);
                    feedSlider.value = xF;
                }
                if (xF >= xD) {
                    xF = Math.max(xD - 0.01, xB + 0.01);
                    feedSlider.value = xF;
                }

                // Update slider ranges
                feedSlider.min = (xB + 0.01).toFixed(2);
                feedSlider.max = (xD - 0.01).toFixed(2);
            }

            function drawDiagram() {
                p.background(255);

                // Draw axes
                p.stroke(128);
                p.strokeWeight(1);
                p.line(margin, margin, margin, margin + plotSize);
                p.line(margin, margin + plotSize, margin + plotSize, margin + plotSize);

                // Draw axis labels
                p.fill(128);
                p.noStroke();
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);

                // Y-axis ticks and labels
                for (let i = 0; i <= 10; i++) {
                    let y = margin + plotSize - (i * plotSize / 10);
                    p.text(i/10, margin - 20, y);
                    p.stroke(220);
                    p.line(margin - 5, y, margin, y);
                }

                // X-axis ticks and labels
                for (let i = 0; i <= 10; i++) {
                    let x = margin + (i * plotSize / 10);
                    p.text(i/10, x, margin + plotSize + 20);
                    p.stroke(220);
                    p.line(x, margin + plotSize, x, margin + plotSize + 5);
                }

                // Axis titles
                p.textSize(14);
                p.text('Mole fraction in liquid, x', margin + plotSize/2, margin + plotSize + 45);
                p.push();
                p.translate(margin - 45, margin + plotSize/2);
                p.rotate(-p.PI/2);
                p.text('Mole fraction in vapor, y', 0, 0);
                p.pop();

                // Draw equilibrium curve
                p.stroke('#FF0000');
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let x = 0; x <= 1; x += 0.01) {
                    let y = (alpha * x) / (1 + (alpha - 1) * x);
                    let px = mapX(x);
                    let py = mapY(y);
                    p.vertex(px, py);
                }
                p.endShape();

                // Draw diagonal line
                p.stroke('#228B22');
                p.line(margin, margin + plotSize, margin + plotSize, margin);

                // Calculate intersection point
                let intersection = calculateIntersection();

                // Draw rectifying line
                p.stroke('#00FFFF');
                p.line(mapX(xD), mapY(xD), mapX(intersection.x), mapY(intersection.y));

                // Draw stripping line
                p.stroke('#FF00FF');
                p.line(mapX(xB), mapY(xB), mapX(intersection.x), mapY(intersection.y));

                // Draw feed line
                p.stroke('#0000CD');
                let feedLineEnd = calculateFeedLineEnd();
                p.line(mapX(xF), mapY(xF), mapX(feedLineEnd.x), mapY(feedLineEnd.y));

                // Draw stages
                stageCount = drawStages(intersection);

                // Update display
                document.getElementById('stages-count-display').textContent = `The number of stages is ${stageCount}.`;
            }

            function calculateIntersection() {
                let tolerance = 0.0001;
                let x = 0.5;
                let y;

                for (let i = 0; i < 100; i++) {
                    y = (alpha * x) / (1 + (alpha - 1) * x);

                    let feedLineY;
                    if (q === 1) {
                        feedLineY = x;
                    } else if (q === 0) {
                        feedLineY = xF;
                    } else {
                        let slope = q / (q - 1);
                        feedLineY = slope * (x - xF) + xF;
                    }

                    if (Math.abs(y - feedLineY) < tolerance) {
                        break;
                    }

                    x = (q * xF - (1 - x)) / (-(1 - 1/alpha) * q + (q - 1));
                }

                return { x: x, y: y };
            }

            function calculateFeedLineEnd() {
                let x = xF;
                let y = xF;

                if (q === 1) {
                    x = 1;
                } else if (q === 0) {
                    x = xF;
                } else {
                    let slope = q / (q - 1);
                    x = xF + 0.3;
                    y = slope * (x - xF) + xF;
                }

                return { x: x, y: y };
            }

            function drawStages(intersection) {
                p.stroke('#000000');
                p.strokeWeight(2);

                let x = xD;
                let y = xD;
                let count = 0;
                let done = false;

                while (x > xB && !done) {
                    // Horizontal to equilibrium curve
                    let y_eq = (alpha * x) / (1 + (alpha - 1) * x);
                    p.line(mapX(x), mapY(y), mapX(x), mapY(y_eq));
                    y = y_eq;

                    if (x <= xB) break;
                    count += 1;

                    // Vertical to operating line
                    let nextX;
                    if (x > intersection.x) {
                        nextX = x;
                    } else {
                        // Stripping line slope
                        let slope = (intersection.y - xB) / (intersection.x - xB);
                        nextX = xB + (y - xB) / slope;
                    }
                    x = nextX;

                    p.line(mapX(x), mapY(y), mapX(nextX), mapY(y));
                }

                return count;
            }

            function mapX(x) {
                return p.map(x, 0, 1, margin, margin + plotSize);
            }

            function mapY(y) {
                return p.map(y, 0, 1, margin + plotSize, margin);
            }

            // Event listeners
            document.getElementById('slider-volatility').addEventListener('input', function() {
                updateParams();
                p.redraw();
            });

            document.getElementById('slider-distillate').addEventListener('input', function() {
                updateParams();
                p.redraw();
            });

            document.getElementById('slider-bottom').addEventListener('input', function() {
                updateParams();
                p.redraw();
            });

            document.getElementById('slider-feed-comp').addEventListener('input', function() {
                updateParams();
                p.redraw();
            });

            document.getElementById('slider-feed-quality').addEventListener('input', function() {
                updateParams();
                p.redraw();
            });

            // Button listeners
            document.querySelectorAll('button[id$="-plus"]').forEach(btn => {
                btn.addEventListener('click', function() {
                    let sliderId = this.id.replace('btn-', '').replace('-plus', '');
                    let slider = document.getElementById('slider-' + sliderId);
                    let step = parseFloat(slider.step);
                    let newVal = parseFloat(slider.value) + step;

                    if (newVal <= slider.max) {
                        slider.value = newVal;
                        updateParams();
                        p.redraw();
                    }
                });
            });
        };

        new p5(sketch);
    </script>
</body>
</html>
