</head>
<body>
    <!-- Main container for the demo -->
    <div id="demo-container">
        <!-- Section for user controls -->
        <div id="control-panel">
            <label for="slider-month">month</label>
            <input type="range" id="slider-month" min="0" max="11" value="0" step="1">
            <span id="month-display">January</span>

            <label for="perspective-buttons">perspective</label>
            <div id="perspective-buttons">
                <button id="btn-2d" class="active">2D</button>
                <button id="btn-3d">3D</button>
            </div>
        </div>

        <!-- Section for the visualization canvas -->
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>

        <!-- Visualization scripts -->
        <script>
            let currentPerspective = '2d';
            let currentMonth = 0;
            let p5Instance = null;
            let threeInstance = null;

            // Month names
            const months = [
                'January', 'February', 'March', 'April', 'May', 'June',
                'July', 'August', 'September', 'October', 'November', 'December'
            ];

            // Control panel event listeners
            document.getElementById('slider-month').addEventListener('input', (e) => {
                currentMonth = parseInt(e.target.value);
                document.getElementById('month-display').textContent = months[currentMonth];
                if (currentPerspective === '2d' && p5Instance) {
                    p5Instance.redraw();
                } else if (currentPerspective === '3d' && threeInstance) {
                    threeInstance.updateScene(currentMonth);
                }
            });

            document.getElementById('btn-2d').addEventListener('click', () => {
                if (currentPerspective !== '2d') {
                    currentPerspective = '2d';
                    switchTo2D();
                    updateButtonStates();
                }
            });

            document.getElementById('btn-3d').addEventListener('click', () => {
                if (currentPerspective !== '3d') {
                    currentPerspective = '3d';
                    switchTo3D();
                    updateButtonStates();
                }
            });

            function updateButtonStates() {
                document.getElementById('btn-2d').classList.toggle('active', currentPerspective === '2d');
                document.getElementById('btn-3d').classList.toggle('active', currentPerspective === '3d');
            }

            // Initialize with 2D view
            switchTo2D();

            function switchTo2D() {
                if (threeInstance) {
                    threeInstance.cleanup();
                    threeInstance = null;
                }
                initP5();
            }

            function switchTo3D() {
                if (p5Instance) {
                    p5Instance.remove();
                    p5Instance = null;
                }
                initThree();
            }

            function initP5() {
                const sketch = (p) => {
                    p.setup = function() {
                        const canvas = p.createCanvas(
                            document.getElementById('visualization-area').offsetWidth,
                            document.getElementById('visualization-area').offsetHeight
                        );
                        canvas.parent('visualization-area');
                        p.noLoop();
                    };

                    p.draw = function() {
                        p.background(0);

                        const centerX = p.width / 2;
                        const centerY = p.height / 2;
                        const earthRadius = 50;

                        // Calculate tilt angle based on month
                        const tilt = 23.5 * p.cos(2 * p.PI * currentMonth / 12);
                        const radians = p.radians(tilt);

                        p.push();
                        p.translate(centerX, centerY);
                        p.rotate(radians);

                        // Draw bow shock
                        p.strokeWeight(3);
                        p.stroke('#FFA500');
                        p.noFill();
                        p.beginShape();
                        for (let x = -200; x <= 200; x += 5) {
                            const y = 150 * (1 - p.sq(x/200)) * p.cos(p.PI * x/400);
                            p.vertex(x, y);
                        }
                        for (let x = 200; x >= -200; x -= 5) {
                            const y = -150 * (1 - p.sq(x/200)) * p.cos(p.PI * x/400);
                            p.vertex(x, y);
                        }
                        p.endShape(p.CLOSE);

                        // Draw magnetopause
                        p.stroke('#00FFFF');
                        p.beginShape();
                        for (let x = -180; x <= 180; x += 5) {
                            const y = 120 * (1 - p.sq(x/180)) * p.cos(p.PI * x/360);
                            p.vertex(x, y);
                        }
                        for (let x = 180; x >= -180; x -= 5) {
                            const y = -120 * (1 - p.sq(x/180)) * p.cos(p.PI * x/360);
                            p.vertex(x, y);
                        }
                        p.endShape(p.CLOSE);

                        // Draw magnetic field lines
                        p.strokeWeight(1);
                        p.stroke('#808080');
                        for (let i = 0; i < 8; i++) {
                            p.beginShape();
                            for (let x = -200; x <= 200; x += 10) {
                                const y = 80 * p.sin(p.PI * x/50 + i * p.PI/4) * (1 + x/200);
                                p.vertex(x, y);
                            }
                            p.endShape();
                        }

                        // Draw Earth
                        p.noStroke();
                        p.fill('#4169E1');
                        p.circle(0, 0, earthRadius * 2);

                        // Draw landmasses
                        p.fill('#228B22');
                        p.arc(-15, -10, 40, 30, -p.PI, p.PI/3);
                        p.arc(10, 15, 30, 20, 0, p.PI);

                        // Van Allen belts
                        p.fill(0, 100, 0, 100);
                        p.ellipse(0, 0, earthRadius * 3, earthRadius * 3);
                        p.fill(50, 205, 50, 100);
                        p.ellipse(0, 0, earthRadius * 4, earthRadius * 4);

                        // Magnetotail
                        p.fill(0, 0, 139, 100);
                        p.beginShape();
                        p.vertex(50, -60);
                        p.vertex(300, -60);
                        p.vertex(300, 60);
                        p.vertex(50, 60);
                        p.endShape(p.CLOSE);

                        p.pop();

                        // Draw solar wind
                        p.strokeWeight(2);
                        p.stroke('#990000');
                        p.fill('#990000');
                        for (let y = -150; y <= 150; y += 30) {
                            p.line(0, y, centerX - 200, y);
                            const arrowX = centerX - 200;
                            const arrowY = y;
                            const arrowSize = 10;
                            p.triangle(
                                arrowX, arrowY,
                                arrowX - arrowSize, arrowY - arrowSize/2,
                                arrowX - arrowSize, arrowY + arrowSize/2
                            );
                        }
                    };

                    p.windowResized = function() {
                        p.resizeCanvas(
                            document.getElementById('visualization-area').offsetWidth,
                            document.getElementById('visualization-area').offsetHeight
                        );
                    };
                };
                p5Instance = new p5(sketch);
            }

            function initThree() {
                const container = document.getElementById('visualization-area');
                const scene = new THREE.Scene();
                const camera = new THREE.PerspectiveCamera(
                    75, 
                    container.offsetWidth / container.offsetHeight, 
                    0.1, 
                    1000
                );

                const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas') });
                renderer.setSize(container.offsetWidth, container.offsetHeight);
                renderer.setClearColor(0x000000);

                // Lighting
                const ambientLight = new THREE.AmbientLight(0x404040);
                scene.add(ambientLight);

                const sunLight = new THREE.PointLight(0xffffff, 1);
                sunLight.position.set(0, 0, 0);
                scene.add(sunLight);

                // Sun
                const sunGeometry = new THREE.SphereGeometry(10, 32, 32);
                const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
                const sun = new THREE.Mesh(sunGeometry, sunMaterial);
                scene.add(sun);

                // Earth group
                const earthGroup = new THREE.Group();

                // Earth
                const earthGeometry = new THREE.SphereGeometry(3, 32, 32);
                const earthMaterial = new THREE.MeshStandardMaterial({ 
                    color: 0x4169E1,
                    roughness: 0.5,
                    metalness: 0.5
                });
                const earth = new THREE.Mesh(earthGeometry, earthMaterial);
                earthGroup.add(earth);

                // Earth's axis
                const axisDir = new THREE.Vector3(0, 1, 0);
                const axisLength = 8;
                const arrowHelper = new THREE.ArrowHelper(
                    axisDir,
                    new THREE.Vector3(0, 0, 0),
                    axisLength,
                    0x00FF00
                );
                earthGroup.add(arrowHelper);

                scene.add(earthGroup);

                // Orbit
                const orbitCurve = new THREE.EllipseCurve(
                    -50, 0,
                    70, 30,
                    0, 2 * Math.PI,
                    false,
                    0
                );
                const orbitPoints = orbitCurve.getPoints(100);
                const orbitGeometry = new THREE.BufferGeometry().setFromPoints(orbitPoints);
                const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xFFFFFF });
                const orbit = new THREE.Line(orbitGeometry, orbitMaterial);
                orbit.rotation.x = Math.PI / 2;
                scene.add(orbit);

                // Magnetosphere
                const magnetosphereShape = new THREE.LatheGeometry(
                    [
                        new THREE.Vector2(3, 0),
                        new THREE.Vector2(4, 1),
                        new THREE.Vector2(4.5, 2),
                        new THREE.Vector2(4, 3),
                        new THREE.Vector2(2, 5),
                        new THREE.Vector2(2, 8),
                        new THREE.Vector2(3, 12)
                    ],
                    20
                );
                const magnetosphereMaterial = new THREE.MeshStandardMaterial({
                    color: 0x0000FF,
                    transparent: true,
                    opacity: 0.4,
                    side: THREE.DoubleSide
                });
                const magnetosphere = new THREE.Mesh(magnetosphereShape, magnetosphereMaterial);
                earthGroup.add(magnetosphere);

                // Camera and controls
                camera.position.set(0, 40, 80);
                const controls = new THREE.OrbitControls(camera, renderer.domElement);

                threeInstance = {
                    updateScene: function(month) {
                        const angle = (month / 12) * 2 * Math.PI;
                        const radiusX = 70;
                        const radiusZ = 30;

                        earthGroup.position.x = -50 + Math.cos(angle) * radiusX;
                        earthGroup.position.z = Math.sin(angle) * radiusZ;

                        // Tilt earth axis
                        const tilt = 23.5 * Math.cos(2 * Math.PI * month / 12);
                        earthGroup.rotation.z = tilt * Math.PI / 180;

                        // Orient magnetosphere tail
                        const toSun = new THREE.Vector3(0, 0, 0).sub(earthGroup.position);
                        magnetosphere.lookAt(toSun.multiplyScalar(-1));
                    },
                    cleanup: function() {
                        scene.traverse((object) => {
                            if (object.geometry) object.geometry.dispose();
                            if (object.material) object.material.dispose();
                        });
                        renderer.dispose();
                    }
                };

                threeInstance.updateScene(currentMonth);

                function animate() {
                    requestAnimationFrame(animate);
                    controls.update();
                    renderer.render(scene, camera);
                }
                animate();

                // Handle resize
                window.addEventListener('resize', () => {
                    camera.aspect = container.offsetWidth / container.offsetHeight;
                    camera.updateProjectionMatrix();
                    renderer.setSize(container.offsetWidth, container.offsetHeight);
                });
            }

            // Initialize with current month
            document.getElementById('slider-month').value = new Date().getMonth();
            document.getElementById('slider-month').dispatchEvent(new Event('input'));
        </script>
    </div>
</body>
</html>
