<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Distillation Boundary Ternary Plot</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            background-color: #f0f0f0;
        }
        #canvas-container {
            border: 2px solid #333;
        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        // Azeotrope coordinates
        const azeotropes = {
            A1: { x: 0.655, y: 0 },
            A2: { x: 0, y: 0.795 },
            A3: { x: 0.605, y: 0.345 },
            A4: { x: 0.26, y: 0.335 }
        };

        // Drawing parameters
        const canvasSize = 650;
        const margin = 60;
        const plotSize = canvasSize - 2 * margin;

        // Locator state
        let locator = {
            x: 0.2,
            y: 0.22,
            radius: 8
        };

        let isDragging = false;
        let distillationPoints = [];

        // Vector field function as specified
        function getVector(x, y) {
            const A3 = {x: 0.605, y: 0.345};
            const M = {x: 0, y: 0};

            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        // Convert data coordinates to canvas coordinates
        function dataToCanvas(x, y) {
            return {
                x: margin + x * plotSize,
                y: canvasSize - margin - y * plotSize
            };
        }

        // Convert canvas coordinates to data coordinates
        function canvasToData(canvasX, canvasY) {
            return {
                x: (canvasX - margin) / plotSize,
                y: (canvasSize - margin - canvasY) / plotSize
            };
        }

        // Calculate distance from point to line segment
        function distanceToSegment(px, py, x1, y1, x2, y2) {
            const dx = x2 - x1;
            const dy = y2 - y1;
            const lenSq = dx * dx + dy * dy;

            if (lenSq === 0) return Math.sqrt((px - x1) ** 2 + (py - y1) ** 2);

            const t = Math.max(0, Math.min(1, ((px - x1) * dx + (py - y1) * dy) / lenSq));
            const projX = x1 + t * dx;
            const projY = y1 + t * dy;

            return Math.sqrt((px - projX) ** 2 + (py - projY) ** 2);
        }

        // Find closest point on distillation boundary
        function closestPointOnBoundary(x, y) {
            const boundary = [azeotropes.A2, azeotropes.A4, azeotropes.A1];
            let minDist = Infinity;
            let closest = null;

            for (let i = 0; i < boundary.length - 1; i++) {
                const seg1 = boundary[i];
                const seg2 = boundary[i + 1];

                const dx = seg2.x - seg1.x;
                const dy = seg2.y - seg1.y;

                const t = Math.max(0, Math.min(1, ((x - seg1.x) * dx + (y - seg1.y) * dy) / (dx * dx + dy * dy)));
                const px = seg1.x + t * dx;
                const py = seg1.y + t * dy;

                const dist = Math.sqrt((x - px) ** 2 + (y - py) ** 2);

                if (dist < minDist) {
                    minDist = dist;
                    closest = { x: px, y: py };
                }
            }

            return closest || { x: azeotropes.A2.x, y: azeotropes.A2.y };
        }

        // Calculate distillation line
        function calculateDistillationLine() {
            const points = [];
            const stepSize = 0.005;
            const maxSteps = 250;

            // Forward integration
            let currentX = locator.x;
            let currentY = locator.y;
            for (let i = 0; i < maxSteps; i++) {
                if (currentX < 0 || currentY < 0 || currentX + currentY > 1) break;

                const vec = getVector(currentX, currentY);
                points.push({ x: currentX, y: currentY });

                currentX += vec.dx * stepSize;
                currentY += vec.dy * stepSize;
            }

            // Backward integration
            currentX = locator.x;
            currentY = locator.y;
            for (let i = 0; i < maxSteps; i++) {
                if (currentX < 0 || currentY < 0 || currentX + currentY > 1) break;

                const vec = getVector(currentX, currentY);

                currentX -= vec.dx * stepSize;
                currentY -= vec.dy * stepSize;

                if (currentX >= 0 && currentY >= 0 && currentX + currentY <= 1) {
                    points.unshift({ x: currentX, y: currentY });
                }
            }

            return points;
        }

        // p5.js setup function
        function setup() {
            const canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            noLoop();
            distillationPoints = calculateDistillationLine();
        }

        // p5.js draw function
        function draw() {
            background('#FFFFFF');
            stroke('#000000');
            strokeWeight(1);

            // Draw axes
            line(margin, canvasSize - margin, canvasSize - margin, canvasSize - margin);
            line(margin, canvasSize - margin, margin, margin);

            // Draw hypotenuse
            const topLeft = dataToCanvas(0, 1);
            const bottomRight = dataToCanvas(1, 0);
            line(topLeft.x, topLeft.y, bottomRight.x, bottomRight.y);

            // Draw ticks and labels
            fill('#000000');
            textSize(12);
            textAlign(CENTER, CENTER);

            for (let val = 0; val <= 1.0; val += 0.2) {
                // X axis
                const xTick = dataToCanvas(val, 0);
                line(xTick.x, canvasSize - margin, xTick.x, canvasSize - margin + 5);
                text(val.toFixed(1), xTick.x, canvasSize - margin + 15);

                // Y axis
                const yTick = dataToCanvas(0, val);
                line(margin - 5, yTick.y, margin, yTick.y);
                text(val.toFixed(1), margin - 15, yTick.y);
            }

            // Draw component labels
            fill('#FF0000');
            textSize(14);
            textAlign(LEFT, CENTER);
            text("methanol", margin - 40, canvasSize - margin + 20);
            textAlign(CENTER, TOP);
            text("chloroform", dataToCanvas(0.5, -0.15));
            textAlign(RIGHT, CENTER);
            text("acetone", dataToCanvas(-0.15, 0.5));

            // Draw azeotrope points
            fill('#990000');
            noStroke();
            for (const [key, point] of Object.entries(azeotropes)) {
                const canvasPos = dataToCanvas(point.x, point.y);
                ellipse(canvasPos.x, canvasPos.y, 12, 12);

                fill('#000000');
                textAlign(CENTER, TOP);
                text(key.replace(/(\d+)/, (_, num) => '_' + num), canvasPos.x, canvasPos.y - 20);
                fill('#990000');
            }

            // Draw distillation line
            stroke('#0000FF');
            strokeWeight(2);
            noFill();
            beginShape();
            for (const point of distillationPoints) {
                const canvasPos = dataToCanvas(point.x, point.y);
                curveVertex(canvasPos.x, canvasPos.y);
            }
            endShape();

            // Draw locator
            const locatorPos = dataToCanvas(locator.x, locator.y);
            fill('#FFFFFF');
            stroke('#000000');
            strokeWeight(2);
            ellipse(locatorPos.x, locatorPos.y, locator.radius * 2, locator.radius * 2);
        }

        // Mouse interaction functions
        function mousePressed() {
            const locatorPos = dataToCanvas(locator.x, locator.y);
            const dist = Math.sqrt((mouseX - locatorPos.x) ** 2 + (mouseY - locatorPos.y) ** 2);
            if (dist <= locator.radius) {
                isDragging = true;
                loop();
            }
        }

        function mouseDragged() {
            if (isDragging) {
                const dataPos = canvasToData(mouseX, mouseY);
                const constrainedPos = closestPointOnBoundary(dataPos.x, dataPos.y);
                locator.x = constrainedPos.x;
                locator.y = constrainedPos.y;
                distillationPoints = calculateDistillationLine();
            }
        }

        function mouseReleased() {
            if (isDragging) {
                isDragging = false;
                noLoop();
                redraw();
            }
        }
    </script>
</body>
</html>
