                <label for="slider-a">a</label>
                <input type="range" id="slider-a" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-a" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-b" class="slider-group">
                <label for="slider-b">b</label>
                <input type="range" id="slider-b" min="0.1" max="5" step="0.1" value="1.5">
                <input type="number" id="input-b" min="0.1" max="5" step="0.1" value="1.5">
            </div>
            <div id="slider-group-c" class="slider-group">
                <label for="slider-c">c</label>
                <input type="range" id="slider-c" min="0.1" max="5" step="0.1" value="1.0">
                <input type="number" id="input-c" min="0.1" max="5" step="0.1" value="1.0">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Initialize Three.js scene
        const container = document.getElementById('canvas-container');
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0x000000);

        const camera = new THREE.PerspectiveCamera(
            45,
            window.innerWidth / (window.innerHeight - 100),
            0.1,
            1000
        );
        camera.position.set(5, 5, 5);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight - 100);
        container.appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Add lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
        directionalLight.position.set(10, 10, 10);
        scene.add(directionalLight);

        // Add coordinate axes
        const axesGeometry = new THREE.BufferGeometry();
        const axesPositions = new Float32Array([
            -5, 0, 0, 5, 0, 0,  // X axis
            0, -5, 0, 0, 5, 0,   // Y axis
            0, 0, -5, 0, 0, 5    // Z axis
        ]);
        axesGeometry.setAttribute('position', new THREE.BufferAttribute(axesPositions, 3));
        const axesMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });
        const axes = new THREE.LineSegments(axesGeometry, axesMaterial);
        scene.add(axes);

        let paraboloid = null;

        function createEllipticParaboloid(a, b, c) {
            // Remove existing paraboloid
            if (paraboloid) {
                scene.remove(paraboloid);
            }

            const group = new THREE.Group();

            // Parametric function for elliptic paraboloid
            function paraboloidFunction(u, v, target) {
                const range = 2.0;
                const x = a * v * range * Math.cos(u * 2 * Math.PI);
                const y = b * v * range * Math.sin(u * 2 * Math.PI);
                const z = c * (v * range) * (v * range);
                target.set(x, y, z);
            }

            // Create geometry
            const geometry = new THREE.ParametricGeometry(paraboloidFunction, 50, 50);

            // Create color array
            const colors = [];
            const zMax = c * 4; // Since range is 2, z_max = c * (2*2)
            const colorStart = new THREE.Color('#FFD700');
            const colorEnd = new THREE.Color('#FF4500');

            const positions = geometry.attributes.position;
            for (let i = 0; i < positions.count; i++) {
                const z = positions.getZ(i);
                const t = Math.max(0, Math.min(1, z / zMax));
                const color = new THREE.Color().lerpColors(colorStart, colorEnd, t);
                colors.push(color.r, color.g, color.b);
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Create materials and meshes
            const solidMaterial = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true
            });

            const wireframeMaterial = new THREE.MeshBasicMaterial({
                color: 0xcccccc,
                wireframe: true
            });

            const solidMesh = new THREE.Mesh(geometry, solidMaterial);
            const wireframeMesh = new THREE.Mesh(geometry, wireframeMaterial);

            group.add(solidMesh);
            group.add(wireframeMesh);

            paraboloid = group;
            scene.add(paraboloid);
        }

        // Initial creation
        createEllipticParaboloid(1.5, 1.5, 1.0);

        // Sync sliders and inputs
        const sliderA = document.getElementById('slider-a');
        const inputA = document.getElementById('input-a');
        const sliderB = document.getElementById('slider-b');
        const inputB = document.getElementById('input-b');
        const sliderC = document.getElementById('slider-c');
        const inputC = document.getElementById('input-c');

        // Update function
        function updateParaboloid() {
            const a = parseFloat(sliderA.value);
            const b = parseFloat(sliderB.value);
            const c = parseFloat(sliderC.value);
            createEllipticParaboloid(a, b, c);
        }

        // Add event listeners
        sliderA.addEventListener('input', () => {
            inputA.value = sliderA.value;
            updateParaboloid();
        });
        inputA.addEventListener('input', () => {
            sliderA.value = inputA.value;
            updateParaboloid();
        });

        sliderB.addEventListener('input', () => {
            inputB.value = sliderB.value;
            updateParaboloid();
        });
        inputB.addEventListener('input', () => {
            sliderB.value = inputB.value;
            updateParaboloid();
        });

        sliderC.addEventListener('input', () => {
            inputC.value = sliderC.value;
            updateParaboloid();
        });
        inputC.addEventListener('input', () => {
            sliderC.value = inputC.value;
            updateParaboloid();
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / (window.innerHeight - 100);
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight - 100);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();
    </script>
</body>
</html>
