    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let j1 = 1.5, m1 = 0.5, j2 = 1.0, m2 = 0.0, j = 2.5;
        let isAnimating = false;
        let animationPhase = 0;
        let phi_z = 0, phi_j = 0;

        // Vector objects
        let vectorJ, vectorJ1, vectorJ2;
        let coneJ, coneJ1, coneJ2;

        // Initialize three.js scene
        function initScene() {
            const canvas = document.getElementById('main-canvas');
            const container = document.getElementById('canvas-container');

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(
                75, 
                container.clientWidth / container.clientHeight, 
                0.1, 
                1000
            );
            camera.position.set(5, 5, 5);

            renderer = new THREE.WebGLRenderer({ canvas: canvas });
            renderer.setSize(container.clientWidth, container.clientHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.1;

            // Adjust camera orientation for Z-up
            camera.up.set(0, 0, 1);
            camera.lookAt(0, 0, 0);
            controls.update();

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Axes helper
            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            // Add coordinate labels
            const createTextLabel = (text, position) => {
                const div = document.createElement('div');
                div.className = 'coordinate-label';
                div.textContent = text;
                div.style.position = 'absolute';
                div.style.color = '#333';
                div.style.fontSize = '14px';
                document.body.appendChild(div);
                return div;
            };
        }

        // Calculate vector lengths
        function getVectorLength(q) {
            return Math.sqrt(q * (q + 1));
        }

        // Update selectors based on current values
        function updateSelectors() {
            // Update j1 selector
            document.querySelectorAll('#j1-selector button').forEach(btn => {
                btn.classList.toggle('selected', parseFloat(btn.value) === j1);
            });

            // Update j2 selector
            document.querySelectorAll('#j2-selector button').forEach(btn => {
                btn.classList.toggle('selected', parseFloat(btn.value) === j2);
            });

            // Update m1 selector
            const m1Container = document.getElementById('m1-selector');
            m1Container.innerHTML = '';
            const m1Values = [];
            for (let m = -j1; m <= j1; m += 1) {
                m1Values.push(m);
            }
            m1Values.forEach(m => {
                const btn = document.createElement('button');
                btn.textContent = m.toString();
                btn.addEventListener('click', () => {
                    m1 = m;
                    updateVisualization();
                });
                if (m === m1) btn.classList.add('selected');
                m1Container.appendChild(btn);
            });

            // Update m2 selector
            const m2Container = document.getElementById('m2-selector');
            m2Container.innerHTML = '';
            const m2Values = [];
            for (let m = -j2; m <= j2; m += 1) {
                m2Values.push(m);
            }
            m2Values.forEach(m => {
                const btn = document.createElement('button');
                btn.textContent = m.toString();
                btn.addEventListener('click', () => {
                    m2 = m;
                    updateVisualization();
                });
                if (m === m2) btn.classList.add('selected');
                m2Container.appendChild(btn);
            });

            // Update j selector
            const jContainer = document.getElementById('j-selector');
            jContainer.innerHTML = '';
            const minJ = Math.abs(j1 - j2);
            const maxJ = j1 + j2;
            const jValues = [];
            for (let jVal = minJ; jVal <= maxJ; jVal += 1) {
                jValues.push(jVal);
            }
            jValues.forEach(jVal => {
                const btn = document.createElement('button');
                btn.textContent = jVal.toString();
                btn.addEventListener('click', () => {
                    j = jVal;
                    updateVisualization();
                });
                if (jVal === j) btn.classList.add('selected');
                jContainer.appendChild(btn);
            });
        }

        // Create an arrow helper
        function createArrow(origin, dir, color, length) {
            return new THREE.ArrowHelper(
                dir.normalize(),
                origin,
                length,
                new THREE.Color(color),
                0.2 * length,
                0.1 * length
            );
        }

        // Create a precession cone
        function createCone(length, angle, color) {
            const geometry = new THREE.ConeGeometry(
                length * Math.sin(angle),
                length,
                32,
                1,
                true
            );
            const material = new THREE.MeshBasicMaterial({
                color: new THREE.Color(color),
                transparent: true,
                opacity: 0.2,
                side: THREE.DoubleSide
            });
            return new THREE.Mesh(geometry, material);
        }

        // Update visualization based on current quantum numbers
        function updateVisualization() {
            // Clear existing objects
            scene.children = scene.children.filter(child => 
                !(child instanceof THREE.ArrowHelper || 
                  (child instanceof THREE.Mesh && child.geometry instanceof THREE.ConeGeometry))
            );

            const L_J = getVectorLength(j);
            const L_J1 = getVectorLength(j1);
            const L_J2 = getVectorLength(j2);

            const m_total = m1 + m2;

            // Check model type
            const useCoupled = (j >= j1 && j >= j2);

            if (useCoupled) {
                // Coupled model
                const theta_z = Math.acos(m_total / L_J);
                const theta_j = Math.acos((L_J * L_J + L_J1 * L_J1 - L_J2 * L_J2) / (2 * L_J * L_J1));

                // Create J vector
                const J_dir = new THREE.Vector3(
                    Math.sin(theta_z) * Math.cos(phi_z),
                    Math.sin(theta_z) * Math.sin(phi_z),
                    Math.cos(theta_z)
                );
                vectorJ = createArrow(new THREE.Vector3(0, 0, 0), J_dir, 0x000000, L_J);
                scene.add(vectorJ);

                // Create cones
                coneJ = createCone(L_J, theta_z, 0x000000);
                coneJ.position.y = -L_J / 2 * Math.cos(theta_z);
                coneJ.lookAt(new THREE.Vector3(0, 0, 0));
                scene.add(coneJ);

                // Create J1 and J2 vectors (precessing around J)
                const J1_vec = new THREE.Vector3(
                    Math.sin(theta_j) * Math.cos(phi_j),
                    Math.sin(theta_j) * Math.sin(phi_j),
                    Math.cos(theta_j)
                ).applyQuaternion(
                    new THREE.Quaternion().setFromUnitVectors(
                        new THREE.Vector3(0, 0, 1), J_dir
                    )
                ).multiplyScalar(L_J1);

                const J2_vec = new THREE.Vector3().subVectors(J_dir.clone().multiplyScalar(L_J), J1_vec);

                vectorJ1 = new THREE.ArrowHelper(
                    J1_vec.clone().normalize(),
                    new THREE.Vector3(0, 0, 0),
                    L_J1,
                    0xff0000,
                    0.2 * L_J1,
                    0.1 * L_J1
                );
                scene.add(vectorJ1);

                vectorJ2 = new THREE.ArrowHelper(
                    J2_vec.clone().normalize(),
                    new THREE.Vector3(0, 0, 0),
                    J2_vec.length(),
                    0x0000ff,
                    0.2 * L_J2,
                    0.1 * L_J2
                );
                scene.add(vectorJ2);

            } else {
                // Uncoupled model
                const theta1 = Math.acos(m1 / L_J1);
                const theta2 = Math.acos(m2 / L_J2);

                // Create independent J1 vector
                const J1_dir = new THREE.Vector3(
                    Math.sin(theta1) * Math.cos(phi_z),
                    Math.sin(theta1) * Math.sin(phi_z),
                    Math.cos(theta1)
                );
                vectorJ1 = createArrow(new THREE.Vector3(0, 0, 0), J1_dir, 0xff0000, L_J1);
                scene.add(vectorJ1);

                // Create independent J2 vector
                const J2_dir = new THREE.Vector3(
                    Math.sin(theta2) * Math.cos(phi_z + Math.PI), // Offset for visual separation
                    Math.sin(theta2) * Math.sin(phi_z + Math.PI),
                    Math.cos(theta2)
                );
                vectorJ2 = createArrow(new THREE.Vector3(0, 0, 0), J2_dir, 0x0000ff, L_J2);
                scene.add(vectorJ2);

                // Create cones for precession paths
                coneJ1 = createCone(L_J1, theta1, 0xff0000);
                coneJ1.position.y = -L_J1 / 2 * Math.cos(theta1);
                coneJ1.lookAt(new THREE.Vector3(0, 0, 0));
                scene.add(coneJ1);

                coneJ2 = createCone(L_J2, theta2, 0x0000ff);
                coneJ2.position.y = -L_J2 / 2 * Math.cos(theta2);
                coneJ2.lookAt(new THREE.Vector3(0, 0, 0));
                scene.add(coneJ2);
            }

            // Reset animation phase
            animationPhase = 0;
            phi_z = 0;
            phi_j = 0;
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            if (isAnimating) {
                animationPhase += 0.01;
                phi_z = animationPhase;
                phi_j = animationPhase * 2; // Different precession speed for visual effect
                updateVisualization();
            }

            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isAnimating = !isAnimating;
            this.textContent = isAnimating ? '❚❚' : '▶';
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            isAnimating = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            updateVisualization();
        });

        // Initialize j1, j2 selectors
        document.querySelectorAll('#j1-selector button').forEach(btn => {
            btn.addEventListener('click', function() {
                j1 = parseFloat(this.value);
                updateSelectors();
                updateVisualization();
            });
        });

        document.querySelectorAll('#j2-selector button').forEach(btn => {
            btn.addEventListener('click', function() {
                j2 = parseFloat(this.value);
                updateSelectors();
                updateVisualization();
            });
        });

        // Handle window resize
        window.addEventListener('resize', () => {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        });

        // Initialize everything
        initScene();
        updateSelectors();
        updateVisualization();
        animate();

        // Set initial selected values
        document.querySelector('#j1-selector button[value="1.5"]').click();
        document.querySelector('#j2-selector button[value="1.0"]').click();
    </script>
</body>
