        let startX = 4;
        let startY = 4;

        // Random seed for consistent scattered pattern
        let randomSeedValue = 42;

        // p5.js sketches
        let recursiveSketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                canvas.parent('canvas-recursive');
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);
                drawGrid(p, recursiveHistory);
            };
        };

        let iterativeSketch = function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                canvas.parent('canvas-iterative');
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);
                drawGrid(p, iterativeHistory);
            };
        };

        // Initialize p5.js sketches
        new p5(recursiveSketch);
        new p5(iterativeSketch);

        function drawGrid(p, history) {
            const slider = document.getElementById('steps-slider');
            const steps = parseInt(slider.value);

            // Draw grid lines
            p.stroke(0);
            p.strokeWeight(1);
            for (let i = 0; i <= GRID_SIZE; i++) {
                p.line(i * CELL_SIZE, 0, i * CELL_SIZE, CANVAS_SIZE);
                p.line(0, i * CELL_SIZE, CANVAS_SIZE, i * CELL_SIZE);
            }

            // Draw obstacles
            p.fill(0);
            for (let y = 0; y < GRID_SIZE; y++) {
                for (let x = 0; x < GRID_SIZE; x++) {
                    if (grid[y][x] === OBSTACLE) {
                        p.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                    }
                }
            }

            // Draw filled cells
            p.fill(0, 0, 255);
            for (let i = 0; i < Math.min(steps, history.length); i++) {
                const [x, y] = history[i];
                p.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
            }
        }

        function generateGrid(pattern) {
            // Reset random seed for consistent patterns
            Math.seedrandom = function(s) {
                let mask = 0xffffffff;
                let m_w  = (123456789 + s) & mask;
                let m_z  = (987654321 - s) & mask;
                return function() {
                    m_z = (36969 * (m_z & 65535) + (m_z >>> 16)) & mask;
                    m_w = (18000 * (m_w & 65535) + (m_w >>> 16)) & mask;
                    let result = ((m_z << 16) + (m_w & 65535)) >>> 0;
                    result /= 4294967296;
                    return result;
                }
            };

            let rng = Math.seedrandom(randomSeedValue);
            let newGrid = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(EMPTY));

            switch (pattern) {
                case 'none':
                    break;
                case 'box':
                    for (let i = 0; i < GRID_SIZE; i++) {
                        newGrid[0][i] = OBSTACLE;
                        newGrid[GRID_SIZE - 1][i] = OBSTACLE;
                        newGrid[i][0] = OBSTACLE;
                        newGrid[i][GRID_SIZE - 1] = OBSTACLE;
                    }
                    break;
                case 'scattered':
                    for (let y = 0; y < GRID_SIZE; y++) {
                        for (let x = 0; x < GRID_SIZE; x++) {
                            if ((x !== startX || y !== startY) && rng() < 0.25) {
                                newGrid[y][x] = OBSTACLE;
                            }
                        }
                    }
                    break;
            }

            return newGrid;
        }

        function floodFillRecursive(x, y, visited, history) {
            if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;
            if (grid[y][x] === OBSTACLE) return;
            if (visited[y][x]) return;

            visited[y][x] = true;
            history.push([x, y]);

            // Visit in order: North, West, South, East
            floodFillRecursive(x, y - 1, visited, history);
            floodFillRecursive(x - 1, y, visited, history);
            floodFillRecursive(x, y + 1, visited, history);
            floodFillRecursive(x + 1, y, visited, history);
        }

        function floodFillIterative(startX, startY) {
            let history = [];
            let visited = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(false));
            let queue = [[startX, startY]];

            while (queue.length > 0) {
                const [x, y] = queue.shift();

                if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) continue;
                if (grid[y][x] === OBSTACLE) continue;
                if (visited[y][x]) continue;

                visited[y][x] = true;
                history.push([x, y]);

                // Add neighbors in order: North, West, South, East
                queue.push([x, y - 1]);
                queue.push([x - 1, y]);
                queue.push([x, y + 1]);
                queue.push([x + 1, y]);
            }

            return history;
        }

        function updateSimulation() {
            grid = generateGrid(currentPattern);

            // Run recursive algorithm
            recursiveHistory = [];
            let visited = Array(GRID_SIZE).fill().map(() => Array(GRID_SIZE).fill(false));
            floodFillRecursive(startX, startY, visited, recursiveHistory);

            // Run iterative algorithm
            iterativeHistory = floodFillIterative(startX, startY);

            // Update slider
            const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            const slider = document.getElementById('steps-slider');
            slider.max = maxSteps;
            slider.value = 0;
            document.getElementById('steps-value').textContent = '+ 0';

            // Redraw both canvases
            document.querySelectorAll('canvas').forEach(canvas => {
                const p = canvas.__p5;
                if (p) p.redraw();
            });
        }

        function updateDisplay() {
            const slider = document.getElementById('steps-slider');
            const value = slider.value;
            document.getElementById('steps-value').textContent = `+ ${value}`;

            // Redraw both canvases
            document.querySelectorAll('canvas').forEach(canvas => {
                const p = canvas.__p5;
                if (p) p.redraw();
            });
        }

        // Event listeners
        document.getElementById('steps-slider').addEventListener('input', updateDisplay);

        document.getElementById('btn-none').addEventListener('click', function() {
            setActiveButton(this);
            currentPattern = 'none';
            updateSimulation();
        });

        document.getElementById('btn-box').addEventListener('click', function() {
            setActiveButton(this);
            currentPattern = 'box';
            updateSimulation();
        });

        document.getElementById('btn-scattered').addEventListener('click', function() {
            setActiveButton(this);
            currentPattern = 'scattered';
            updateSimulation();
        });

        function setActiveButton(activeBtn) {
            document.querySelectorAll('#control-panel button').forEach(btn => {
                btn.classList.remove('active');
            });
            activeBtn.classList.add('active');
        }

        // Initialize
        updateSimulation();
    </script>
</body>
</html>
