        function draw() {
            background(255);

            const example = examples[currentExample - 1];
            const original = example.original;

            // Get unique triplets and sort them
            const triplets = [];
            for (let i = 0; i < original.length; i += 3) {
                const triplet = original.slice(i, i + 3).join('');
                if (!triplets.includes(triplet)) {
                    triplets.push(triplet);
                }
            }
            triplets.sort();

            // Calculate positions
            const bitSize = 20;
            const bitGap = 2;
            const tripletGap = 8;
            const rowGap = 80;
            const topY = 80;
            const bottomY = topY + rowGap;

            // Calculate top row positions
            const topPositions = [];
            let currentX = 50;
            for (let i = 0; i < original.length; i += 3) {
                topPositions.push(currentX);
                currentX += 3 * (bitSize + bitGap) + tripletGap;
            }

            // Calculate bottom row positions
            const bottomPositions = [];
            const keyWidth = 3 * bitSize + 2 * bitGap + 20; // 3 bits + marker
            const keyGap = 10;
            let bottomX = 50;

            // Key blocks
            const keyPositions = {};
            for (const triplet of triplets) {
                keyPositions[triplet] = bottomX;
                bottomX += keyWidth + keyGap;
            }

            // Codeword blocks
            const codewordPositions = [];
            for (let i = 0; i < original.length; i += 3) {
                const triplet = original.slice(i, i + 3).join('');
                const codeword = fullEncodingMap[triplet].codeword;
                const width = codeword.length * (bitSize + bitGap) - bitGap;
                codewordPositions.push(bottomX);
                bottomX += width + keyGap;
            }

            // Draw top row
            fill(0);
            textSize(14);
            text('Original Data', 50, topY - 20);

            for (let i = 0; i < original.length; i += 3) {
                const x = topPositions[i / 3];
                for (let j = 0; j < 3; j++) {
                    const bit = original[i + j];
                    fill(bit === 1 ? 0 : 211);
                    stroke(0);
                    rect(x + j * (bitSize + bitGap), topY, bitSize, bitSize);
                }
            }

            // Draw bottom row
            text('Encoded Data', 50, bottomY - 20);

            // Draw key blocks
            for (const triplet of triplets) {
                const x = keyPositions[triplet];
                const info = fullEncodingMap[triplet];

                // Draw marker
                fill(0);
                text('>', x + 2, bottomY + bitSize / 2 + 5);

                // Draw triplet bits
                for (let j = 0; j < 3; j++) {
                    const bit = parseInt(triplet[j]);
                    fill(bit === 1 ? 0 : 211);
                    rect(x + 15 + j * (bitSize + bitGap), bottomY, bitSize, bitSize);
                }

                // Draw label tab
                fill(224);
                stroke(0);
                beginShape();
                vertex(x + 5, bottomY + bitSize + 5);
                vertex(x + keyWidth - 5, bottomY + bitSize + 5);
                vertex(x + keyWidth - 5, bottomY + bitSize + 25);
                vertex(x + 5, bottomY + bitSize + 25);
                endShape(CLOSE);

                // Draw label
                fill(0);
                noStroke();
                textAlign(CENTER, CENTER);
                text(info.label, x + keyWidth / 2, bottomY + bitSize + 15);
            }

            // Draw codeword blocks
            for (let i = 0; i < original.length; i += 3) {
                const triplet = original.slice(i, i + 3).join('');
                const info = fullEncodingMap[triplet];
                const x = codewordPositions[i / 3];

                // Draw codeword bits
                for (let j = 0; j < info.codeword.length; j++) {
                    const bit = info.codeword[j];
                    fill(bit === 1 ? 0 : 211);
                    stroke(0);
                    rect(x + j * (bitSize + bitGap), bottomY, bitSize, bitSize);
                }

                // Draw label tab
                const width = info.codeword.length * (bitSize + bitGap) - bitGap;
                fill(224);
                stroke(0);
                beginShape();
                vertex(x + 5, bottomY + bitSize + 5);
                vertex(x + width - 5, bottomY + bitSize + 5);
                vertex(x + width - 5, bottomY + bitSize + 25);
                vertex(x + 5, bottomY + bitSize + 25);
                endShape(CLOSE);

                // Draw label
                fill(0);
                noStroke();
                textAlign(CENTER, CENTER);
                text(info.label, x + width / 2, bottomY + bitSize + 15);
            }

            // Draw arrows
            stroke(128);
            fill(128);
            for (let i = 0; i < original.length; i += 3) {
                const triplet = original.slice(i, i + 3).join('');
                const topX = topPositions[i / 3] + 1.5 * bitSize + bitGap;
                const bottomX = codewordPositions[i / 3] + 
                    fullEncodingMap[triplet].codeword.length * (bitSize + bitGap) / 2 - bitGap / 2;

                // Draw line
                line(topX, topY + bitSize, bottomX, bottomY);

                // Draw arrowhead
                const angle = atan2(bottomY - (topY + bitSize), bottomX - topX);
                push();
                translate(bottomX, bottomY);
                rotate(angle);
                triangle(0, 0, -8, -4, -8, 4);
                pop();
            }
        }
    </script>
