    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>

    <div id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </div>

    <script>
        let centerX, centerY;
        let radius = 200;
        let points = {};
        let draggedPoint = null;
        let dragOffset = { x: 0, y: 0 };

        function setup() {
            let canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');

            centerX = width / 2;
            centerY = height / 2;

            // Initialize points
            points.A = { angle: 7 * PI / 6, x: 0, y: 0 };
            points.B = { angle: 11 * PI / 6, x: 0, y: 0 };
            points.C = { angle: PI / 2, x: 0, y: 0 };
            points.A_prime = { angle: 0, x: 0, y: 0 };
            points.B_prime = { angle: 0, x: 0, y: 0 };
            points.C_prime = { angle: 0, x: 0, y: 0 };

            updateAllPoints();
        }

        function draw() {
            background('#F0F0F0');

            // Draw circumcircle
            stroke('#6A5ACD');
            strokeWeight(4);
            noFill();
            circle(centerX, centerY, radius * 2);

            // Update calculated points
            updateCalculatedPoints();

            // Draw triangle ABC
            stroke('#6A5ACD');
            strokeWeight(4);
            line(points.A.x, points.A.y, points.B.x, points.B.y);
            line(points.B.x, points.B.y, points.C.x, points.C.y);
            line(points.C.x, points.C.y, points.A.x, points.A.y);

            // Draw triangle A'B'C' and bisectors
            stroke('#FFA500');
            strokeWeight(4);
            line(points.A_prime.x, points.A_prime.y, points.B_prime.x, points.B_prime.y);
            line(points.B_prime.x, points.B_prime.y, points.C_prime.x, points.C_prime.y);
            line(points.C_prime.x, points.C_prime.y, points.A_prime.x, points.A_prime.y);

            // Draw bisectors
            line(points.A.x, points.A.y, points.A_prime.x, points.A_prime.y);
            line(points.B.x, points.B.y, points.B_prime.x, points.B_prime.y);
            line(points.C.x, points.C.y, points.C_prime.x, points.C_prime.y);

            // Draw points and labels
            drawPoint('A', points.A.x, points.A.y, true);
            drawPoint('B', points.B.x, points.B.y, true);
            drawPoint('C', points.C.x, points.C.y, true);
            drawPoint("A'", points.A_prime.x, points.A_prime.y, false);
            drawPoint("B'", points.B_prime.x, points.B_prime.y, false);
            drawPoint("C'", points.C_prime.x, points.C_prime.y, false);
        }

        function updateAllPoints() {
            // Update ABC points
            points.A.x = centerX + radius * cos(points.A.angle);
            points.A.y = centerY + radius * sin(points.A.angle);
            points.B.x = centerX + radius * cos(points.B.angle);
            points.B.y = centerY + radius * sin(points.B.angle);
            points.C.x = centerX + radius * cos(points.C.angle);
            points.C.y = centerY + radius * sin(points.C.angle);

            updateCalculatedPoints();
        }

        function updateCalculatedPoints() {
            // Calculate A' as midpoint of arc BC not containing A
            points.A_prime.angle = atan2(sin(points.B.angle) + sin(points.C.angle), 
                                       cos(points.B.angle) + cos(points.C.angle));
            points.A_prime.x = centerX + radius * cos(points.A_prime.angle);
            points.A_prime.y = centerY + radius * sin(points.A_prime.angle);

            // Calculate B' as midpoint of arc AC not containing B
            points.B_prime.angle = atan2(sin(points.A.angle) + sin(points.C.angle), 
                                       cos(points.A.angle) + cos(points.C.angle));
            points.B_prime.x = centerX + radius * cos(points.B_prime.angle);
            points.B_prime.y = centerY + radius * sin(points.B_prime.angle);

            // Calculate C' as midpoint of arc AB not containing C
            points.C_prime.angle = atan2(sin(points.A.angle) + sin(points.B.angle), 
                                       cos(points.A.angle) + cos(points.B.angle));
            points.C_prime.x = centerX + radius * cos(points.C_prime.angle);
            points.C_prime.y = centerY + radius * sin(points.C_prime.angle);
        }

        function drawPoint(label, x, y, isInteractive) {
            // Draw circle
            fill('#FFA500');
            stroke('#000000');
            strokeWeight(1);
            circle(x, y, 12);

            // Draw crosshair for interactive points
            if (isInteractive) {
                stroke('#000000');
                strokeWeight(1);
                line(x - 6, y, x + 6, y);
                line(x, y - 6, x, y + 6);
            }

            // Draw label
            fill('#000000');
            noStroke();
            textSize(16);

            // Calculate label offset
            let offsetX = 15;
            let offsetY = -15;

            // Adjust offset based on position relative to center
            let dx = x - centerX;
            let dy = y - centerY;
            let dist = sqrt(dx * dx + dy * dy);
            if (dist > 0) {
                offsetX = 15 * dx / dist;
                offsetY = 15 * dy / dist;
            }

            text(label, x + offsetX, y + offsetY);
        }

        function mousePressed() {
            // Check if mouse is near any point
            let tolerance = 15;
            let pointsToCheck = ['A', 'B', 'C'];

            for (let pointName of pointsToCheck) {
                let d = dist(mouseX, mouseY, points[pointName].x, points[pointName].y);
                if (d < tolerance) {
                    draggedPoint = pointName;
                    dragOffset.x = mouseX - points[pointName].x;
                    dragOffset.y = mouseY - points[pointName].y;
                    return;
                }
            }
        }

        function mouseDragged() {
            if (draggedPoint) {
                // Calculate angle from center to mouse
                let dx = mouseX - centerX;
                let dy = mouseY - centerY;
                points[draggedPoint].angle = atan2(dy, dx);

                // Update point position
                points[draggedPoint].x = centerX + radius * cos(points[draggedPoint].angle);
                points[draggedPoint].y = centerY + radius * sin(points[draggedPoint].angle);
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }
    </script>
</body>
</html>
