            font-size: 14px;
            margin-right: 12px;
            user-select: none;
        }
        input[type=range] {
            width: 300px;
            vertical-align: middle;
        }
        #canvas-container {
            position: relative;
            border: none;
        }
        canvas {
            display: block;
        }
        /* p5.js creates its canvas within canvas-container; no extra styling needed */
    </style>
</head>
<body>

    <div id="container">
        <div id="controls">
            <label for="slider-steps">carbon cycle steps</label>
            <input type="range" id="slider-steps" min="0" max="7" value="0" step="1">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let slider;
        let canvas;

        // Coordinates and dimensions (approximate on 600×500 canvas)
        const process = {
            photosynthesis: { pos: [360, 120], label: "Photosynthesis" },
            respiration: { pos: [150, 180], label: "Cellular Respiration" },
            decay: { pos: [240, 320], label: "Waste and Decay" },
            fossils: { pos: [240, 450], label: "Fossil Fuels Form" },
            emissions: { pos: [440, 150], label: "Fossil Fuel Emissions" },
            extraction: { pos: [400, 350], label: "Fuel Extraction" },
            diffusion: { pos: [480, 420], label: "CO₂ Diffusion" }
        };

        // Step descriptions for tooltips
        const tooltipText = [
            "", // step 0
            "Animals and plants break down sugars for energy, releasing carbon dioxide.",
            "Decomposers break down dead organic matter, returning carbon to the soil & atmosphere.",
            "Over millions of years, buried organic matter can turn into coal and oil.",
            "Plants use sunlight, water, and CO₂ to produce food, releasing oxygen.",
            "Burning fossil fuels releases large amounts of carbon dioxide into the atmosphere.",
            "Humans drill and mine fossil fuels from the ground for energy.",
            "Carbon dioxide dissolves into and is released from oceans and other bodies of water."
        ];

        let currentTooltip = "";

        function setup() {
            canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            noLoop();  // redraw only on slider change
            slider = select("#slider-steps");
            slider.input(() => {
                currentTooltip = ""; // clear tooltip on slider move
                redraw();
            });
        }

        function draw() {
            background(255);
            drawStaticScene();

            const val = int(slider.value());
            // Reveal cumulative; order kept in case we want different ordering
            if (val >= 4)  drawPhotosynthesis();          // step 4
            if (val >= 1)  drawRespiration();             // step 1
            if (val >= 2)  drawWasteDecay();              // step 2
            if (val >= 3)  drawFossils();                 // step 3
            if (val >= 5)  drawEmissions();               // step 5
            if (val >= 6)  drawExtraction();              // step 6
            if (val >= 7)  drawDiffusion();                // step 7
        }

        // ---------- static background scene ----------
        function drawStaticScene() {
            // sky
            fill('#4169E1');
            noStroke();
            rect(0, 0, width, 200);

            // clouds
            fill(255, 255, 255, 200);
            ellipse(100, 50, 60, 30);
            ellipse(340, 70, 80, 35);

            // sun
            fill('#FFD700');
            ellipse(470, 60, 50, 50);

            // grass
            fill('#228B22');
            rect(0, 180, width, 120);

            // soil
            fill('#8B4513');
            rect(0, 300, width, 200);

            // water
            fill('#0000CD');
            beginShape();
            vertex(400, 300);
            vertex(600, 280);
            vertex(600, 500);
            vertex(500, 500);
            endShape(CLOSE);

            // trees
            // tree 1
            fill('#8B4513');
            rect(240, 200, 20, 80);
            fill('#006400');
            ellipse(250, 170, 80, 120);
            // tree 2
            fill('#8B4513');
            rect(340, 220, 30, 60);
            fill('#006400');
            ellipse(355, 190, 100, 130);

            // sheep
            fill(255);
            ellipse(200, 260, 40, 40);
            ellipse(190, 250, 20, 30);
            ellipse(210, 250, 20, 30);
            fill(0);
            ellipse(203, 248, 3, 3);

            // factory
            fill('#696969');
            rect(440, 220, 80, 80);
            rect(460, 200, 40, 20);
            rect(470, 180, 5, 20);
            rect(485, 180, 5, 20);

            // fossil deposits
            fill(20);
            ellipse(230, 430, 8, 8);
            ellipse(270, 445, 10, 10);
            ellipse(210, 460, 7, 7);
        }

        // ---------- arrows and labels ----------
        // helper: draw arrow between two points
        function drawArrow(x1, y1, x2, y2, weight = 1.2) {
            stroke(0);
            strokeWeight(weight * 2);
            line(x1, y1, x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            push();
            translate(x2, y2);
            rotate(angle);
            fill(0);
            noStroke();
            triangle(-4 * weight, -6 * weight, 0, 0, 4 * weight, -6 * weight);
            pop();
            strokeWeight(1);
        }

        // label drawing with bounding box for tooltip
        function drawLabel(name, x, y) {
            noStroke();
            fill(0);
            textSize(12);
            textAlign(CENTER, TOP);
            text(name, x, y);
            // store bounding box for hover
            return { x, y, w: textWidth(name), h: 15 };
        }

        // each process function keeps its own hover state generator
        function handleHover(box, text) {
            if (mouseX >= box.x - box.w / 2 && mouseX <= box.x + box.w / 2 &&
                mouseY >= box.y && mouseY <= box.y + box.h) {
                currentTooltip = text;
            }
        }

        function drawPhotosynthesis() {
            drawArrow(350, 80, 300, 180);
            let b = drawLabel("Photosynthesis", process.photosynthesis.pos[0], process.photosynthesis.pos[1]);
            handleHover(b, tooltipText[4]);
        }

        function drawRespiration() {
            drawArrow(200, 240, 250, 180);
            let b = drawLabel("Cellular\nRespiration", process.respiration.pos[0], process.respiration.pos[1]);
            handleHover(b, tooltipText[1]);
        }

        function drawWasteDecay() {
            drawArrow(220, 280, 220, 350);
            let b = drawLabel("Waste &\nDecay", process.decay.pos[0], process.decay.pos[1]);
            handleHover(b, tooltipText[2]);
        }

        function drawFossils() {
            drawArrow(220, 370, 220, 440);
            let b = drawLabel("Fossil Fuels\nForm", process.fossils.pos[0], process.fossils.pos[1]);
            handleHover(b, tooltipText[3]);
        }

        function drawEmissions() {
            drawArrow(480, 200, 430, 120);
            let b = drawLabel("Fossil Fuel\nEmissions", process.emissions.pos[0], process.emissions.pos[1]);
            handleHover(b, tooltipText[5]);
        }

        function drawExtraction() {
            drawArrow(450, 420, 480, 300);
            let b = drawLabel("Fuel\nExtraction", process.extraction.pos[0], process.extraction.pos[1]);
            handleHover(b, tooltipText[6]);
        }

        function drawDiffusion() {
            drawArrow(520, 250, 550, 400);
            drawArrow(570, 400, 540, 250);
            let b = drawLabel("CO₂ Diffusion", process.diffusion.pos[0], process.diffusion.pos[1]);
            handleHover(b, tooltipText[7]);
        }

        // draw tooltip on top
        function mouseMoved() {
            redraw(); // redraw to enable hover check
            if (currentTooltip) {
                fill(0, 0, 0, 150);
                noStroke();
                rect(mouseX + 8, mouseY - 25, textWidth(currentTooltip) + 15, 18, 4);
                fill(255);
                textAlign(LEFT, TOP);
                text(currentTooltip, mouseX + 15, mouseY - 20);
            }
        }
    </script>
</body>
</html>
