</head>
<body>
  <!-- === (Copied from Plan – component IDs unchanged) ========= -->
  <div id="main-container">

    <div id="control-panel">
      <label for="slider-time">time</label>
      <div class="slider-container">
        <input type="range" id="slider-time" min="0" max="6.283" step="0.01" value="0.4">
        <button id="btn-reset-time" title="Reset time">+</button>
      </div>

      <div>
        <label>position</label>
        <div id="toggle-position" class="toggle-switch">
          <input type="radio" id="radio-position-off" name="position" value="off" checked>
          <label for="radio-position-off">off</label>
          <input type="radio" id="radio-position-on"  name="position" value="on">
          <label for="radio-position-on">on</label>
        </div>
      </div>

      <div>
        <label>velocity</label>
        <div id="toggle-velocity" class="toggle-switch">
          <input type="radio" id="radio-velocity-off" name="velocity" value="off" checked>
          <label for="radio-velocity-off">off</label>
          <input type="radio" id="radio-velocity-on"  name="velocity" value="on">
          <label for="radio-velocity-on">on</label>
        </div>
      </div>

      <div>
        <label>acceleration</label>
        <div id="toggle-acceleration" class="toggle-switch">
          <input type="radio" id="radio-acceleration-off" name="acceleration" value="off">
          <label for="radio-acceleration-off">off</label>
          <input type="radio" id="radio-acceleration-on"  name="acceleration" value="on" checked>
          <label for="radio-acceleration-on">on</label>
        </div>
      </div>

      <label for="select-coords">coordinate system</label>
      <select id="select-coords">
        <option value="rectangular">rectangular</option>
        <option value="normal-tangential">normal-tangential</option>
        <option value="polar">polar</option>
      </select>

      <div>
        <label>instantaneous circle</label>
        <div id="toggle-circle" class="toggle-switch">
          <input type="radio" id="radio-circle-off" name="circle" value="off" checked>
          <label for="radio-circle-off">off</label>
          <input type="radio" id="radio-circle-on"  name="circle" value="on">
          <label for="radio-circle-on">on</label>
        </div>
      </div>
    </div>

    <div id="canvas-container"></div>

  </div>

  <script>
    /*********************** GLOBAL STATE & HELPERS ***************************/
    const state = {
      t: 0.4,
      showPosition:false,
      showVelocity:false,
      showAcceleration:true,
      coords:'rectangular',
      showCircle:false
    };

    const SCALE = 90;            // pixels per 1 unit
    const CENTER = {x:0, y:0};   // will be the center of canvas

    // ----------------- Kinematics functions -----------------------------
    function pos(t){ return {x:Math.cos(t), y:Math.sin(2*t)}; }
    function vel(t){ return {x:-Math.sin(t), y:2*Math.cos(2*t)}; }
    function acc(t){ return {x:-Math.cos(t), y:-4*Math.sin(2*t)}; }

    // ---------- Vector utilities --------------
    const mag = v => Math.hypot(v.x, v.y);
    const normalize = v => {const m=mag(v); return m===0?{x:0,y:0}:{x:v.x/m,y:v.y/m};};
    const add = (v1,v2) => ({x:v1.x+v2.x, y:v1.y+v2.y});
    const scale = (v,s) => ({x:v.x*s, y:v.y*s});
    const rotate90CCW = v => ({x:-v.y, y:v.x});
    const rotate90CW  = v => ({x:v.y,  y:-v.x});

    // ------------- p5 sketch ------------------
    let sketch = p => {
      p.setup = () => {
        const container = document.getElementById('canvas-container');
        const w = container.clientWidth, h = container.clientHeight;
        p.createCanvas(w, h).parent(container);
        CENTER.x = p.width/2; CENTER.y = p.height/2;
        attachEventListeners();              // bind controls once
      };

      p.draw = () => {
        p.background(252);
        p.translate(CENTER.x, CENTER.y);
        p.scale(1, -1); // +y up
        drawAxes();
        drawCurve();
        const pt = pos(state.t), v=vel(state.t), a=acc(state.t);

        // ---- Draw toggled vectors ------------
        if(state.showPosition){
          p.stroke(0,180,0); drawArrow({x:0,y:0},pt,'r',0);
        }
        if(state.showVelocity){
          p.stroke(180,0,0); drawArrow(pt,add(pt,v),'v',0);
        }
        if(state.showAcceleration){
          p.stroke(128,0,255); drawArrow(pt,add(pt,a),'a',0);
        }

        // ---- Basis vectors for chosen system ---
        drawBasis(pt,v);

        // ---- Osculating circle ----------------
        if(state.showCircle){
          drawOscCircle(pt,v,a);
        }

        // ---- Point itself always visible -----
        p.noStroke(); p.fill(0);
        p.ellipse(pt.x*SCALE, pt.y*SCALE, 8,8);
      };

      // -------------------------------------------------------
      function drawAxes(){
        p.strokeWeight(1); p.stroke(0);
        // axes & ticks
        for(let i=-2;i<=2;i++){
          let dpi=i*SCALE;
          // y-axis ticks
          p.line(dpi,-0.05*SCALE, dpi,0.05*SCALE);
          p.push();p.scale(1,-1);p.fill(0);p.noStroke();
          p.text(Math.abs(i)<0.01?"0":i, dpi+4, -0.08*SCALE);p.pop();
          // x-axis ticks
          p.line(-0.05*SCALE,dpi,0.05*SCALE,dpi);
        }
        // x and y axes lines
        p.strokeWeight(1);
        p.line(-2.5*SCALE,0,2.5*SCALE,0);
        p.line(0,-2.5*SCALE,0,2.5*SCALE);
        p.push();p.scale(1,-1);p.fill(0);p.noStroke();
        p.text("x(t)", 2.2*SCALE, 20);
        p.text("y(t)", -20, -2.2*SCALE);
        p.pop();
      }

      function drawCurve(){
        p.strokeWeight(2); p.stroke(0,100,255);
        p.noFill();
        p.beginShape();
        for(let t=0; t<=2*Math.PI; t+=0.02){
          let pt=pos(t);
          p.vertex(pt.x*SCALE, pt.y*SCALE);
        }
        p.endShape();
      }

      // arrow with head, label in scaled coords
      function drawArrow(from,to,label,rotate){
        p.push();
        p.line(from.x*SCALE, from.y*SCALE, to.x*SCALE, to.y*SCALE);
        const dir = normalize({x:to.x-from.x,y:to.y-from.y});
        const px = to.x*SCALE - dir.x*6;
        const py = to.y*SCALE - dir.y*6;
        p.triangle(
          to.x*SCALE,to.y*SCALE,
          px - dir.y*4, py + dir.x*4,
          px + dir.y*4, py - dir.x*4
        );
        // label
        if(label){
          p.push();p.scale(1,-1);
          let lx=(to.x-Math.sign(dir.x)*0.1)*SCALE;
          let ly=(-to.y+Math.sign(dir.y)*0.1)*SCALE;
          let angle = Math.atan2(dir.y,dir.x);
          if(rotate!==0) angle+=rotate;
          p.textAlign(p.CENTER,p.CENTER);
          p.noStroke();p.fill(0);
          p.text(label, lx, ly);
          p.pop();
        }
        p.pop();
      }

      // dashed vectors basis
      function drawDashedVector(from,to,label,off=0){
        p.push();
        p.stroke(0); p.strokeWeight(1);
        p.drawingContext.setLineDash([4,4]);
        drawArrow(from,to,label,off);
        p.drawingContext.setLineDash([]);
        p.pop();
      }

      function drawBasis(pt,v){
        const scaleSmall = 0.6;
        switch(state.coords){
          case 'rectangular':
            drawDashedVector(pt,add(pt,{x:scaleSmall,y:0}),'î',0);
            drawDashedVector(pt,add(pt,{x:0,y:scaleSmall}),'ĵ',0);
            break;
          case 'normal-tangential':
            {
              if(mag(v)===0) break;
              const eT = normalize(v);
              const eN = rotate90CW(eT);
              drawDashedVector(pt,add(pt,scale(eT,scaleSmall)),'ê_T',0);
              drawDashedVector(pt,add(pt,scale(eN,scaleSmall)),'ê_N',0);
            }
            break;
          case 'polar':
            {
              const rvec = {x:pt.x,y:pt.y};
              if(mag(rvec)===0) break;
              const er = normalize(rvec);
              const et = rotate90CCW(er);
              drawDashedVector(pt,add(pt,scale(er,scaleSmall)),'ê_r',0);
              drawDashedVector(pt,add(pt,scale(et,scaleSmall)),'ê_θ',0);
            }
            break;
        }
      }

      function drawOscCircle(ptObj,vObj,aObj){
        const denom = vObj.x*aObj.y - vObj.y*aObj.x;
        if(Math.abs(denom)<1e-6) return;
        const vMag = mag(vObj);
        const rho = Math.pow(vMag,3) / Math.abs(denom);
        const eT = normalize(vObj);
        const eN = rotate90CW(eT);
        const center = add(ptObj, scale(eN,rho));
        p.push();
        p.stroke(128);
        p.strokeWeight(1);
        p.drawingContext.setLineDash([8,5]);
        p.noFill();
        p.ellipse(center.x*SCALE, center.y*SCALE, 2*rho*SCALE,2*rho*SCALE);
        p.drawingContext.setLineDash([]);
        p.pop();
      }

      function attachEventListeners(){
        // reset btn
        document.getElementById('btn-reset-time').addEventListener('click', () => {
          document.getElementById('slider-time').value = 0.4;
          state.t = 0.4;
        });
        // slider
        document.getElementById('slider-time').addEventListener('input', e => state.t = parseFloat(e.target.value));

        // radios
        document.querySelectorAll('input[type=radio], select').forEach(el=>el.addEventListener('change',syncState));
      }

      function syncState(){
        state.t = parseFloat(document.getElementById('slider-time').value);
        state.showPosition = document.querySelector('input[name=position]:checked').value==='on';
        state.showVelocity = document.querySelector('input[name=velocity]:checked').value==='on';
        state.showAcceleration = document.querySelector('input[name=acceleration]:checked').value==='on';
        state.coords = document.getElementById('select-coords').value;
        state.showCircle = document.querySelector('input[name=circle]:checked').value==='on';
      }
    };

    new p5(sketch);

    // Window resize
    window.addEventListener('resize', ()=> {
      const container = document.getElementById('canvas-container');
      sketch.resizeCanvas(container.clientWidth, container.clientHeight);
      CENTER.x = sketch.width/2; CENTER.y = sketch.height/2;
    });
  </script>
</body>
</html>
