      flex: 1;
      background: #fff;
      border: 1px solid #ccc;
      border-radius: 6px;
    }
  </style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <div id="amplitude-control">
        <label for="slider-amplitude">amplitude</label>
        <input id="slider-amplitude" type="range" min="0.1" max="1.0" step="0.001" value="0.3">
        <span id="amplitude-value">0.3</span>
      </div>
      <div id="stiffness-control">
        <label for="slider-stiffness">stiffness</label>
        <input id="slider-stiffness" type="range" min="0.1" max="10" step="0.01" value="1">
        <span id="stiffness-value">1.00</span>
      </div>
      <div id="mass-control">
        <label for="slider-mass">mass</label>
        <input id="slider-mass" type="range" min="0.1" max="10" step="0.01" value="1">
        <span id="mass-value">1.00</span>
      </div>
      <div id="time-controls">
        <label>time</label>
        <div style="display:flex; gap:4px;">
          <button id="btn-step-back">◀</button>
          <button id="btn-play-pause">▶</button>
          <button id="btn-step-forward">▶|</button>
        </div>
      </div>
      <button id="btn-reset">x</button>
    </div>
    <div id="visualization-container">
      <div id="plot-canvas-container"></div>
      <div id="animation-canvas-container"></div>
    </div>
  </div>

  <script>
    // State variables
    let amplitude = 0.3;
    let stiffness = 1.0;
    let mass = 1.0;
    let time = 0;
    let isPlaying = false;
    let angularFrequency;
    let positionHistory = [];

    // p5 instances
    let plotSketch, animSketch;

    // Slider references
    const ampSlider = document.getElementById('slider-amplitude');
    const stiffSlider = document.getElementById('slider-stiffness');
    const massSlider = document.getElementById('slider-mass');

    function recalcAngularFrequency() {
      angularFrequency = Math.sqrt(stiffness / mass);
    }

    function resetState() {
      time = 0;
      positionHistory = [];
      isPlaying = false;
      document.getElementById('btn-play-pause').textContent = '▶';
      recalcAngularFrequency();
    }

    function updateSlidersToDefaults() {
      ampSlider.value = 0.3;
      stiffSlider.value = 1;
      massSlider.value = 1;
      document.getElementById('amplitude-value').textContent = '0.3';
      document.getElementById('stiffness-value').textContent = '1.00';
      document.getElementById('mass-value').textContent = '1.00';
      amplitude = 0.3;
      stiffness = 1;
      mass = 1;
      resetState();
    }

    // Slider input events
    ampSlider.addEventListener('input', () => {
      amplitude = parseFloat(ampSlider.value);
      document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
      resetState();
    });
    stiffSlider.addEventListener('input', () => {
      stiffness = parseFloat(stiffSlider.value);
      document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
      resetState();
    });
    massSlider.addEventListener('input', () => {
      mass = parseFloat(massSlider.value);
      document.getElementById('mass-value').textContent = mass.toFixed(2);
      resetState();
    });

    // Button interactions
    document.getElementById('btn-play-pause').addEventListener('click', () => {
      isPlaying = !isPlaying;
      document.getElementById('btn-play-pause').textContent = isPlaying ? '||' : '▶';
    });

    document.getElementById('btn-step-back').addEventListener('click', () => {
      if (!isPlaying) {
        time = Math.max(0, time - 0.1);
        redrawPlots();
      }
    });

    document.getElementById('btn-step-forward').addEventListener('click', () => {
      if (!isPlaying) {
        time += 0.1;
        redrawPlots();
      }
    });

    document.getElementById('btn-reset').addEventListener('click', () => {
      updateSlidersToDefaults();
      redrawPlots();
    });

    function setup() {
      // Plot canvas sketch
      const plotContainer = document.getElementById('plot-canvas-container');
      plotSketch = new p5((p) => {
        p.setup = () => {
          const w = plotContainer.clientWidth;
          const h = 400;
          p.createCanvas(w, h);
        };
        p.draw = () => {
          drawPlot(p);
        };
        p.windowResized = () => {
          p.resizeCanvas(plotContainer.clientWidth, p.height);
        };
      }, plotContainer);

      // Animation canvas sketch
      const animContainer = document.getElementById('animation-canvas-container');
      animSketch = new p5((p) => {
        p.setup = () => {
          const w = animContainer.clientWidth;
          const h = 400;
          p.createCanvas(w, h);
        };
        p.draw = () => {
          drawAnimation(p);
        };
        p.windowResized = () => {
          p.resizeCanvas(animContainer.clientWidth, p.height);
        };
      }, animContainer);
    }

    function drawPlot(p) {
      p.background(255);
      const w = p.width;
      const h = p.height;
      const margin = 40;
      const plotH = h - 2 * margin;
      const plotW = w - 2 * margin;

      // Map time to x, position to y
      let xMax = 25;
      if (time > xMax) xMax = time + 5;
      const xToPx = (t) => margin + (t / xMax) * plotW;
      const yToPy = (y) => margin + ((-y + 1) / 2) * plotH;

      // Draw axes
      p.stroke(0);
      p.strokeWeight(1);
      p.line(margin, yToPy(0), margin + plotW, yToPy(0));
      p.line(margin, margin, margin, margin + plotH);
      // Axis labels
      p.push();
      p.noStroke();
      p.fill(0);
      p.textAlign(p.CENTER, p.CENTER);
      p.text('0', margin - 15, yToPy(0));
      p.text('1', margin - 15, yToPy(1));
      p.text('-1', margin - 15, yToPy(-1));
      // Gridlines
      p.stroke(200);
      p.strokeWeight(0.5);
      p.line(margin, yToPy(1), margin + plotW, yToPy(1));
      p.line(margin, yToPy(-1), margin + plotW, yToPy(-1));
      p.pop();

      // Plot waveform
      p.stroke('#6363B2');
      p.strokeWeight(2);
      p.noFill();
      p.beginShape();
      for (let pt of positionHistory) {
        let px = xToPx(pt.t);
        let py = yToPy(pt.y);
        p.vertex(px, py);
      }
      p.endShape();
    }

    function drawAnimation(p) {
      p.background(255);
      const w = p.width;
      const h = p.height;
      const margin = 20;
      const range = 2.4; // -1.2 to 1.2
      const scale = (h - 2 * margin) / range;

      // Coordinate mapping
      const yToPy = (y) => (h / 2) - y * scale;

      // Draw vertical axis
      p.stroke(0);
      p.strokeWeight(1);
      p.line(w / 2, margin, w / 2, h - margin);
      // Labels
      p.push();
      p.noStroke();
      p.fill(0);
      p.textAlign(p.LEFT, p.CENTER);
      p.text('1', w / 2 + 10, yToPy(1));
      p.text('0', w / 2 + 10, yToPy(0));
      p.text('-1', w / 2 + 10, yToPy(-1));
      p.pop();

      // Current block position
      let y = amplitude * Math.sin(angularFrequency * time);
      let blockY = yToPy(y);

      // Draw anchor
      p.stroke(0);
      p.strokeWeight(2);
      p.line(w / 2 - 10, margin, w / 2 + 10, margin);

      // Draw spring as zigzag
      const coils = 10;
      const springTop = margin;
      const springBottom = blockY - 15;
      const springH = springBottom - springTop;
      const dx = w / 8; // amplitude of zigzag
      p.stroke(0);
      p.strokeWeight(1);
      p.beginShape();
      p.noFill();
      for (let i = 0; i <= coils * 2; i++) {
        let yy = springTop + (i / (coils * 2)) * springH;
        let xx = w / 2 + (i % 2 === 0 ? -dx : dx);
        p.vertex(xx, yy);
      }
      p.endShape();

      // Draw mass block
      p.fill('#6363B2');
      p.noStroke();
      let blockH = 30;
      p.rect(w / 2 - 20, blockY - blockH / 2, 40, blockH);
    }

    function redrawPlots() {
      if (plotSketch && plotSketch.draw) plotSketch.draw();
      if (animSketch && animSketch.draw) animSketch.draw();
    }

    // Animation loop
    window.addEventListener('DOMContentLoaded', () => {
      setup();
      recalcAngularFrequency();
      redrawPlots();

      // p5.redraw() calls above might not update with history for each step, so ensure history is updated
      setInterval(() => {
        if (!isPlaying) return;
        time += 0.05;
        let y = amplitude * Math.sin(angularFrequency * time);
        positionHistory.push({ t: time, y: y });
        // Keep history length reasonable
        if (positionHistory.length > 10000) positionHistory.shift();
        redrawPlots();
      }, 50);
    });
  </script>
</body>
</html>
