<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Distance Transform Demo</title>
    <style>
        body {
            margin: 0;
            font-family: sans-serif;
            background: #f9f9f9;
        }

        .main-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 2rem;
        }

        .control-panel {
            margin-bottom: 1.5rem;
            display: flex;
            align-items: center;
            gap: 0.5rem;
        }

        .control-panel span {
            font-weight: bold;
            margin-right: 0.25rem;
        }

        button {
            cursor: pointer;
            padding: 0.4rem 0.8rem;
            border: 1px solid #ccc;
            background: #e5e5e5;
            font-size: 0.9rem;
            border-radius: 3px;
            transition: background 0.15s;
        }

        button.active {
            background: #A9A9A9;
            border-color: #666;
            color: #fff;
        }

        #grid-container {
            display: grid;
            grid-template-columns: repeat(7, 40px);
            grid-template-rows: repeat(9, 40px);
            gap: 2px;
        }

        .cell {
            width: 40px;
            height: 40px;
            display: flex;
            justify-content: center;
            align-items: center;
            border: 1px solid #aaa;
            cursor: pointer;
            font-size: 0.9rem;
            user-select: none;
        }

        .cell-on {
            background-color: #FFD700;
        }

        .cell-off {
            background-color: #D3D3D3;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <span>distance function</span>
            <button id="btn-manhattan" class="active">Manhattan</button>
            <button id="btn-euclidean">squared&nbsp;Euclidean</button>
            <button id="btn-chebyshev">Chebyshev</button>
        </div>

        <div id="grid-container"></div>
    </div>

    <script>
        /* ========== STATE ========== */
        let currentDistanceFunction = 'manhattan'; // 'manhattan', 'euclidean', or 'chebyshev'

        const ROWS = 9, COLS = 7;
        // Initialize gridState with sources at (2,1) and (6,4)
        let gridState = Array.from({ length: ROWS }, () => Array(COLS).fill(false));
        gridState[2][1] = true;
        gridState[6][4] = true;

        /* ========== UTILITY FUNCTIONS ========== */
        const manhattanDistance = (r1, c1, r2, c2) => Math.abs(r1 - r2) + Math.abs(c1 - c2);
        const squaredEuclideanDistance = (r1, c1, r2, c2) => (r1 - r2) ** 2 + (c1 - c2) ** 2;
        const chebyshevDistance = (r1, c1, r2, c2) => Math.max(Math.abs(r1 - r2), Math.abs(c1 - c2));

        function calculateDistances() {
            const sources = [];
            for (let r = 0; r < ROWS; r++) {
                for (let c = 0; c < COLS; c++) {
                    if (gridState[r][c]) sources.push({ r, c });
                }
            }

            const distanceLookup = (r1, c1, r2, c2) => {
                switch (currentDistanceFunction) {
                    case 'manhattan':     return manhattanDistance(r1, c1, r2, c2);
                    case 'euclidean':     return squaredEuclideanDistance(r1, c1, r2, c2);
                    case 'chebyshev':     return chebyshevDistance(r1, c1, r2, c2);
                }
            };

            const distances = Array.from({ length: ROWS }, () => Array(COLS).fill(null));

            for (let r = 0; r < ROWS; r++) {
                for (let c = 0; c < COLS; c++) {
                    if (sources.length === 0) {
                        distances[r][c] = NaN; // No sources
                    } else {
                        let minDist = Infinity;
                        for (const src of sources) {
                            const dist = distanceLookup(r, c, src.r, src.c);
                            if (dist < minDist) minDist = dist;
                        }
                        distances[r][c] = minDist;
                    }
                }
            }
            return distances;
        }

        /* ========== RENDER FUNCTIONS ========== */
        function renderGrid() {
            const container = document.getElementById('grid-container');
            container.innerHTML = '';

            const distances = calculateDistances();

            for (let r = 0; r < ROWS; r++) {
                for (let c = 0; c < COLS; c++) {
                    const cell = document.createElement('div');
                    cell.className = 'cell ' + (gridState[r][c] ? 'cell-on' : 'cell-off');
                    const value = Number.isNaN(distances[r][c]) ? '' : distances[r][c];
                    cell.textContent = value;
                    cell.dataset.row = r;
                    cell.dataset.col = c;

                    cell.addEventListener('click', () => {
                        gridState[r][c] = !gridState[r][c];
                        renderGrid();
                    });

                    container.appendChild(cell);
                }
            }
        }

        function highlightActiveButton() {
            document.querySelectorAll('.control-panel button').forEach(btn => {
                btn.classList.toggle('active', btn.id === `btn-${currentDistanceFunction}`);
            });
        }

        /* ========== INITIALIZATION ========== */
        function init() {
            // Wire up distance-function buttons
            document.getElementById('btn-manhattan').addEventListener('click', () => {
                currentDistanceFunction = 'manhattan';
                highlightActiveButton();
                renderGrid();
            });

            document.getElementById('btn-euclidean').addEventListener('click', () => {
                currentDistanceFunction = 'euclidean';
                highlightActiveButton();
                renderGrid();
            });

            document.getElementById('btn-chebyshev').addEventListener('click', () => {
                currentDistanceFunction = 'chebyshev';
                highlightActiveButton();
                renderGrid();
            });

            highlightActiveButton();
            renderGrid();
        }

        init();
    </script>
</body>
</html>
