    </div>

    <script>
        // Utility functions
        function toKelvin(celsius) {
            return celsius + 273.15;
        }

        function formatNumber(num, digits = 0) {
            return num.toFixed(digits).replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        }

        // Initialize state
        let state = {
            model: 'ep',
            tmaxLimit: 'low',
            n: 6,
            tmin: 25,
            tmax: 100,
            tref: 50,
            param: 4000
        };

        // Get DOM elements
        const btnModelEP = document.getElementById('btn-model-ep');
        const btnModelAR = document.getElementById('btn-model-ar');
        const btnTmaxLow = document.getElementById('btn-tmax-low');
        const btnTmaxHigh = document.getElementById('btn-tmax-high');
        const sliderN = document.getElementById('slider-n');
        const sliderTmin = document.getElementById('slider-tmin');
        const sliderTmax = document.getElementById('slider-tmax');
        const sliderTref = document.getElementById('slider-tref');
        const sliderParam = document.getElementById('slider-param');
        const labelN = document.getElementById('label-n');
        const labelTmin = document.getElementById('label-tmin');
        const labelTmax = document.getElementById('label-tmax');
        const labelTref = document.getElementById('label-tref');
        const labelParamName = document.getElementById('label-param-name');
        const labelParamValue = document.getElementById('label-param-value');
        const fitResults = document.getElementById('fit-results');
        const plotContainer = document.getElementById('plot-container');

        // Update button states
        function updateButtons() {
            [btnModelEP, btnModelAR].forEach(btn => btn.classList.remove('active'));
            [btnTmaxLow, btnTmaxHigh].forEach(btn => btn.classList.remove('active'));

            document.getElementById(`btn-model-${state.model === 'ep' ? 'ep' : 'ar'}`).classList.add('active');
            document.getElementById(`btn-tmax-${state.tmaxLimit}`).classList.add('active');
        }

        // Generate data points
        function generateData() {
            const points = [];
            const step = (state.tmax - state.tmin) / (state.n - 1);

            for (let i = 0; i < state.n; i++) {
                const t = state.tmin + i * step;
                const tK = toKelvin(t);
                const tRefK = toKelvin(state.tref);

                let y;
                if (state.model === 'ep') {
                    y = (tK / tRefK) * Math.exp(state.param * (1/tRefK - 1/tK));
                } else {
                    y = Math.exp(state.param * (1/tRefK - 1/tK));
                }
                points.push({t, y});
            }
            return points;
        }

        // Fit model
        function fitModel(data, fitModelType) {
            let bestA = 0;
            let bestRSS = Infinity;
            const tRefK = toKelvin(state.tref);

            for (let a = 0; a <= 10000; a += 1) {
                let rss = 0;

                for (const point of data) {
                    const tK = toKelvin(point.t);
                    let predicted;

                    if (fitModelType === 'ep') {
                        predicted = (tK / tRefK) * Math.exp(a * (1/tRefK - 1/tK));
                    } else {
                        predicted = Math.exp(a * (1/tRefK - 1/tK));
                    }
                    rss += Math.pow(point.y - predicted, 2);
                }

                if (rss < bestRSS) {
                    bestRSS = rss;
                    bestA = a;
                }
            }

            // Calculate r^2
            const yMean = data.reduce((sum, d) => sum + d.y, 0) / data.length;
            const ssTot = data.reduce((sum, d) => sum + Math.pow(d.y - yMean, 2), 0);
            const r2 = 1 - (bestRSS / ssTot);

            return {a: bestA, r2};
        }

        // Draw plot
        function drawPlot(data, fitResult) {
            const fitTrace = {
                x: [],
                y: [],
                mode: 'lines',
                name: `${state.model === 'ep' ? 'Arrhenius' : 'Eyring-Polanyi'} fit`,
                line: {color: 'green', width: 2}
            };

            const numPoints = 200;
            const step = (state.tmax - state.tmin) / (numPoints - 1);
            const tRefK = toKelvin(state.tref);

            for (let i = 0; i < numPoints; i++) {
                const t = state.tmin + i * step;
                const tK = toKelvin(t);

                let y;
                if (state.model === 'ep') {
                    y = Math.exp(fitResult.a * (1/tRefK - 1/tK));
                } else {
                    y = (tK / tRefK) * Math.exp(fitResult.a * (1/tRefK - 1/tK));
                }

                fitTrace.x.push(t);
                fitTrace.y.push(y);
            }

            const scatterTrace = {
                x: data.map(d => d.t),
                y: data.map(d => d.y),
                mode: 'markers',
                name: 'data points',
                marker: {size: 8, color: 'blue', line: {color: 'green', width: 2}}
            };

            const layout = {
                title: `${state.model === 'ep' ? 'Arrhenius' : 'Eyring-Polanyi'} model fit to ${state.model === 'ep' ? 'Eyring-Polanyi' : 'Arrhenius'} data`,
                xaxis: {title: 'T °C'},
                yaxis: {title: 'k(T) / k(T<sub>ref</sub>)'},
                margin: {l: 50, r: 50, t: 50, b: 50}
            };

            Plotly.newPlot('plot-container', [scatterTrace, fitTrace], layout);
        }

        // Main update function
        function update() {
            // Update button states
            updateButtons();

            // Update slider constraints
            if (state.tmaxLimit === 'low') {
                sliderTmax.max = 1200;
            } else {
                sliderTmax.max = 6000;
            }

            // Ensure constraints
            if (state.tmin >= state.tmax) {
                state.tmin = Math.max(0, state.tmax - 1);
            }
            if (state.tref > state.tmax) state.tref = state.tmax;
            if (state.tref < state.tmin) state.tref = state.tmin;

            // Update labels
            labelN.textContent = state.n;
            labelTmin.textContent = formatNumber(state.tmin);
            labelTmax.textContent = formatNumber(state.tmax);
            labelTref.textContent = formatNumber(state.tref);
            labelParamValue.textContent = formatNumber(state.param);

            // Update parameter name
            if (state.model === 'ep') {
                labelParamName.innerHTML = '\\(a_{EP}\\)';
            } else {
                labelParamName.innerHTML = '\\(a_{Ar}\\)';
            }

            // Generate data and fit
            const data = generateData();
            const fitResult = fitModel(data, state.model === 'ep' ? 'ar' : 'ep');

            // Update fit results
            const fitParamName = state.model === 'ep' ? 'a_{Ar}' : 'a_{EP}';
            fitResults.innerHTML = `\\(${fitParamName} = ${formatNumber(fitResult.a)}\\) \\)r^2 = ${fitResult.r2.toFixed(3)}\\)`;

            // Redraw plot
            drawPlot(data, fitResult);

            // Typeset MathJax
            MathJax.typesetPromise([labelParamName, fitResults]);
        }

        // Event listeners
        btnModelEP.addEventListener('click', () => { state.model = 'ep'; update(); });
        btnModelAR.addEventListener('click', () => { state.model = 'ar'; update(); });
        btnTmaxLow.addEventListener('click', () => { state.tmaxLimit = 'low'; update(); });
        btnTmaxHigh.addEventListener('click', () => { state.tmaxLimit = 'high'; update(); });

        sliderN.addEventListener('input', (e) => { state.n = parseInt(e.target.value); update(); });
        sliderTmin.addEventListener('input', (e) => { state.tmin = parseInt(e.target.value); update(); });
        sliderTmax.addEventListener('input', (e) => { state.tmax = parseInt(e.target.value); update(); });
        sliderTref.addEventListener('input', (e) => { state.tref = parseInt(e.target.value); update(); });
        sliderParam.addEventListener('input', (e) => { state.param = parseInt(e.target.value); update(); });

        // Initial update
        update();
    </script>
</body>
</html>
