            display: flex;
            justify-content: center;
            align-items: center;
            height: calc(100vh - 60px);
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <span id="n-display">6</span>
        <button id="btn-n-3" onclick="changeN(3)">3</button>
        <button id="btn-n-4" onclick="changeN(4)">4</button>
        <button id="btn-n-5" onclick="changeN(5)">5</button>
        <button id="btn-n-6" onclick="changeN(6)" class="active">6</button>
        <button id="btn-n-7" onclick="changeN(7)">7</button>
        <button id="btn-n-8" onclick="changeN(8)">9</button>
        <button id="btn-n-9" onclick="changeN(9)">9</button>
        <button id="btn-n-10" onclick="changeN(10)">10</button>
    </div>
    <div id="canvas-container"></div>

    <script>
        let n = 6;
        let p5Instance;

        const COLORS = {
            yellow: '#fefd00',
            red: '#ee1f24',
            blue: '#0048a1',
            black: '#000000',
            white: '#ffffff'
        };

        function changeN(newN) {
            n = newN;
            document.getElementById('n-display').textContent = n;

            // Update active button
            document.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-n-${n}`).classList.add('active');

            // Redraw
            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        function sketch(p) {
            p.setup = function() {
                let container = document.getElementById('canvas-container');
                p.createCanvas(container.offsetWidth * 0.9, container.offsetHeight * 0.9).parent('canvas-container');
                p.noLoop();
                drawScene(p);
            };

            p.draw = function() {
                drawScene(p);
            };
        }

        function drawScene(p) {
            p.background(COLORS.white);
            p.strokeWeight(1);

            const R = 150;
            const canvasWidth = p.width;
            const canvasHeight = p.height;

            // Left side: regular 2n-gon
            const leftCx = canvasWidth / 4;
            const leftCy = canvasHeight / 2;
            const angleStep = p.PI / n;

            p.stroke(COLORS.black);

            // Draw triangles in the left polygon
            for (let j = 1; j <= 2 * n; j++) {
                const angle1 = (j - 1) * angleStep - p.PI / 2;
                const angle2 = j * angleStep - p.PI / 2;

                const x1 = leftCx + R * p.cos(angle1);
                const y1 = leftCy + R * p.sin(angle1);
                const x2 = leftCx + R * p.cos(angle2);
                const y2 = leftCy + R * p.sin(angle2);

                const colorIndex = (j - 1) % 3;
                if (colorIndex === 0) p.fill(COLORS.yellow);
                else if (colorIndex === 1) p.fill(COLORS.red);
                else p.fill(COLORS.blue);

                p.triangle(leftCx, leftCy, x1, y1, x2, y2);
            }

            // Right side: rearranged rectangles
            const rightStartX = (3 * canvasWidth) / 4 - 200;
            const rightStartY = canvasHeight / 2 - 100;

            const s = 2 * R * p.sin(angleStep / 2);
            const a = R * p.cos(angleStep / 2);
            const rectWidth = s;
            const rectHeight = 2 * a;

            let currentX = rightStartX;
            const currentY = rightStartY;

            // Full rectangles
            for (let k = 0; k < p.floor(n / 2); k++) {
                // Top-left corner
                const x = currentX + k * (rectWidth + 20);

                // Draw four triangles forming a rectangle
                const halfWidth = rectWidth / 2;
                const halfHeight = rectHeight / 2;
                const centerX = x + halfWidth;
                const centerY = currentY + halfHeight;

                // Top triangle (yellow)
                p.fill(COLORS.yellow);
                p.triangle(centerX, centerY, x, centerY - halfHeight, x + rectWidth, centerY - halfHeight);

                // Bottom triangle (red)
                p.fill(COLORS.red);
                p.triangle(centerX, centerY, x, centerY + halfHeight, x + rectWidth, centerY + halfHeight);

                // Left triangle (blue)
                p.fill(COLORS.blue);
                p.triangle(centerX, centerY, x, centerY - halfHeight, x, centerY + halfHeight);

                // Right triangle (yellow)
                p.fill(COLORS.yellow);
                p.triangle(centerX, centerY, x + rectWidth, centerY - halfHeight, x + rectWidth, centerY + halfHeight);

                // Outline
                p.noFill();
                p.rect(x, currentY, rectWidth, rectHeight);
                p.line(centerX, currentY, centerX, currentY + rectHeight);
                p.line(x, centerY, x + rectWidth, centerY);
            }

            // Half-rectangle for odd n
            if (n % 2 === 1) {
                const halfRectY = currentY + rectHeight + 20;
                const x = rightStartX;

                // Draw two triangles forming half-rectangle
                const halfRectHeight = a * 2;

                // Top triangle (blue)
                p.fill(COLORS.blue);
                p.triangle(x + rectWidth/2, halfRectY + a, x, halfRectY, x + rectWidth, halfRectY);

                // Bottom triangle (red)
                p.fill(COLORS.red);
                p.triangle(x + rectWidth/2, halfRectY + a, x, halfRectY + halfRectHeight, 
                          x + rectWidth, halfRectY + halfRectHeight);

                // Outline
                p.noFill();
                p.rect(x, halfRectY, rectWidth, halfRectHeight);
                p.line(x, halfRectY + a, x + rectWidth, halfRectY + a);
            }
        }

        // Initialize p5
        new p5(sketch);
    </script>
</body>
</html>
