    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <h4>problem</h4>

                <div class="control-group">
                    <label>dimension</label>
                    <div class="button-group">
                        <button id="btn-2d" class="active">2D</button>
                        <button id="btn-3d">3D</button>
                    </div>
                </div>

                <div class="control-group slider-container">
                    <label>
                        random seed
                        <span class="slider-value" id="seed-value">525</span>
                    </label>
                    <input type="range" id="slider-seed" min="0" max="1000" value="525" step="1">
                </div>

                <div class="control-group slider-container">
                    <label>
                        number
                        <span class="slider-value" id="number-value">40</span>
                    </label>
                    <input type="range" id="slider-number" min="5" max="150" value="40" step="1">
                </div>
            </div>

            <div>
                <h4>method</h4>
                <div class="button-group">
                    <button id="btn-prim" class="active">Prim</button>
                    <button id="btn-kruskal">Kruskal</button>
                </div>
            </div>

            <div>
                <h4>process</h4>
                <div class="button-group">
                    <button id="btn-play">►</button>
                    <button id="btn-pause" disabled>||</button>
                    <button id="btn-reset">|◄</button>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // State variables
        let dimension = '2D';
        let algorithm = 'prim';
        let animationState = 'stopped';
        let seed = 525;
        let numPoints = 40;
        let points = [];
        let mstEdges = [];
        let stepIndex = 0;
        let animationInterval = null;

        // Algorithm specific variables
        let primSteps = [];
        let kruskalSteps = [];

        // p5.js instance
        let p5Instance;

        // DOM elements
        const btn2D = document.getElementById('btn-2d');
        const btn3D = document.getElementById('btn-3d');
        const sliderSeed = document.getElementById('slider-seed');
        const seedValue = document.getElementById('seed-value');
        const sliderNumber = document.getElementById('slider-number');
        const numberValue = document.getElementById('number-value');
        const btnPrim = document.getElementById('btn-prim');
        const btnKruskal = document.getElementById('btn-kruskal');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');

        // Event listeners
        btn2D.addEventListener('click', () => {
            dimension = '2D';
            btn2D.classList.add('active');
            btn3D.classList.remove('active');
            reset(true);
        });

        btn3D.addEventListener('click', () => {
            dimension = '3D';
            btn3D.classList.add('active');
            btn2D.classList.remove('active');
            reset(true);
        });

        sliderSeed.addEventListener('input', (e) => {
            seed = parseInt(e.target.value);
            seedValue.textContent = seed;
            reset(true);
        });

        sliderNumber.addEventListener('input', (e) => {
            numPoints = parseInt(e.target.value);
            numberValue.textContent = numPoints;
            reset(true);
        });

        btnPrim.addEventListener('click', () => {
            algorithm = 'prim';
            btnPrim.classList.add('active');
            btnKruskal.classList.remove('active');
            reset(false);
        });

        btnKruskal.addEventListener('click', () => {
            algorithm = 'kruskal';
            btnKruskal.classList.add('active');
            btnPrim.classList.remove('active');
            reset(false);
        });

        btnPlay.addEventListener('click', playAnimation);
        btnPause.addEventListener('click', pauseAnimation);
        btnReset.addEventListener('click', reset);

        function playAnimation() {
            if (animationState === 'stopped' || animationState === 'paused') {
                animationState = 'playing';
                btnPlay.disabled = true;
                btnPause.disabled = false;

                if (algorithm === 'prim' && primSteps.length === 0) {
                    calculatePrimSteps();
                } else if (algorithm === 'kruskal' && kruskalSteps.length === 0) {
                    calculateKruskalSteps();
                }

                // Start animation loop
                animationInterval = setInterval(() => {
                    if (stepIndex < mstEdges.length) {
                        stepIndex++;
                        p5Instance.redraw();
                    } else {
                        pauseAnimation();
                        btnPlay.disabled = true;
                        btnPause.disabled = true;
                        animationState = 'stopped';
                    }
                }, 100);
            }
        }

        function pauseAnimation() {
            animationState = 'paused';
            btnPlay.disabled = false;
            btnPause.disabled = true;
            clearInterval(animationInterval);
        }

        function reset(fullReset = false) {
            animationState = 'stopped';
            clearInterval(animationInterval);

            btnPlay.disabled = false;
            btnPause.disabled = true;

            stepIndex = 0;
            mstEdges = [];
            primSteps = [];
            kruskalSteps = [];

            if (fullReset) {
                generatePoints();
            } else {
                // Only reset algorithm state, keep points
                calculatePrimSteps();
                calculateKruskalSteps();
            }

            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        function generatePoints() {
            points = [];

            const canvas = p5Instance.canvas;
            const width = canvas.parentElement.offsetWidth;
            const height = canvas.parentElement.offsetHeight;

            p5Instance.randomSeed(seed);

            for (let i = 0; i < numPoints; i++) {
                if (dimension === '2D') {
                    points.push({
                        x: p5Instance.random(50, width - 50),
                        y: p5Instance.random(50, height - 50),
                        z: 0
                    });
                } else {
                    points.push({
                        x: p5Instance.random(-300, 300),
                        y: p5Instance.random(-300, 300),
                        z: p5Instance.random(-300, 300)
                    });
                }
            }
        }

        function calculatePrimSteps() {
            if (points.length === 0) return;

            const visited = new Set([0]);
            const edges = [];

            // Generate all edges from visited to unvisited nodes
            for (let j = 1; j < points.length; j++) {
                const dist = distance(points[0], points[j]);
                edges.push({ weight: dist, from: 0, to: j });
            }

            edges.sort((a, b) => a.weight - b.weight);

            const primSteps = [];

            while (visited.size < points.length) {
                const edge = edges.find(e => visited.has(e.from) !== visited.has(e.to));

                if (!edge) break;

                primSteps.push({ from: edge.from, to: edge.to });
                visited.add(edge.to);

                // Add new edges
                for (let j = 0; j < points.length; j++) {
                    if (!visited.has(j)) {
                        const dist = distance(points[edge.to], points[j]);
                        edges.push({ weight: dist, from: edge.to, to: j });
                    }
                }

                edges.sort((a, b) => a.weight - b.weight);
            }

            // Store the calculated steps
            if (algorithm === 'prim') {
                primSteps.forEach(step => mstEdges.push(step));
            }
        }

        function calculateKruskalSteps() {
            const edges = [];

            // Generate all possible edges
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    edges.push({
                        from: i,
                        to: j,
                        weight: distance(points[i], points[j])
                    });
                }
            }

            edges.sort((a, b) => a.weight - b.weight);

            const parent = new Array(points.length).fill(0).map((_, i) => i);

            function find(u) {
                if (parent[u] !== u) {
                    parent[u] = find(parent[u]);
                }
                return parent[u];
            }

            const kruskalSteps = [];

            for (const edge of edges) {
                const uRoot = find(edge.from);
                const vRoot = find(edge.to);

                if (uRoot !== vRoot) {
                    kruskalSteps.push({ from: edge.from, to: edge.to });
                    parent[uRoot] = vRoot;
                }
            }

            // Store the calculated steps
            if (algorithm === 'kruskal') {
                kruskalSteps.forEach(step => mstEdges.push(step));
            }
        }

        function distance(p1, p2) {
            const dx = p1.x - p2.x;
            const dy = p1.y - p2.y;
            const dz = dimension === '3D' ? p1.z - p2.z : 0;
            return Math.sqrt(dx * dx + dy * dy + dz * dz);
        }

        function newP5Sketch(sketch) {
            sketch.setup = function() {
                const container = document.getElementById('canvas-container');
                const canvas = sketch.createCanvas(
                    container.offsetWidth,
                    container.offsetHeight,
                    dimension === '2D' ? sketch.P2D : sketch.WEBGL
                );
                canvas.parent('canvas-container');
                sketch.angleMode(sketch.DEGREES);

                generatePoints();
                sketch.noLoop();
            };

            sketch.draw = function() {
                if (dimension === '2D') {
                    sketch.background('#E0E0E0');

                    // Draw points
                    points.forEach(point => {
                        sketch.fill('blue');
                        sketch.noStroke();
                        sketch.ellipse(point.x, point.y, 10, 10);
                    });

                    // Draw MST edges
                    for (let i = 0; i < stepIndex; i++) {
                        const edge = mstEdges[i];
                        if (edge) {
                            sketch.stroke('red');
                            sketch.strokeWeight(2);
                            const p1 = points[edge.from];
                            const p2 = points[edge.to];
                            sketch.line(p1.x, p1.y, p2.x, p2.y);
                        }
                    }
                } else {
                    sketch.background('#E0E0E0');

                    // Draw wireframe cube
                    sketch.stroke(200);
                    sketch.noFill();
                    sketch.rotateY(sketch.frameCount * 0.5);
                    sketch.box(600);

                    // Draw points
                    sketch.fill('blue');
                    sketch.noStroke();
                    points.forEach(point => {
                        sketch.push();
                        sketch.translate(point.x, point.y, point.z);
                        sketch.sphere(5);
                        sketch.pop();
                    });

                    // Draw MST edges
                    for (let i = 0; i < stepIndex; i++) {
                        const edge = mstEdges[i];
                        if (edge) {
                            const p1 = points[edge.from];
                            const p2 = points[edge.to];

                            sketch.stroke('red');
                            sketch.strokeWeight(2);
                            sketch.line(p1.x, p1.y, p1.z, p2.x, p2.y, p2.z);
                        }
                    }
                }
            };

            sketch.windowResized = function() {
                const container = document.getElementById('canvas-container');
                sketch.resizeCanvas(
                    container.offsetWidth,
                    container.offsetHeight
                );
            };
        }

        // Initialize p5.js
        document.addEventListener('DOMContentLoaded', function() {
            p5Instance = new p5(newP5Sketch);
        });
    </script>
</body>
</html>
