    </style>
</head>
<body>
    <div id="main-container">
        <div id="decorative-plus-btn">+</div>

        <div id="control-panel">
            <div class="control-section">
                <label for="select-method">method</label>
                <select id="select-method">
                    <option value="estimation">estimation</option>
                    <option value="place values">place values</option>
                </select>
            </div>

            <hr>

            <div class="control-section">
                <label>first number</label>
                <div class="slider-container">
                    <label for="slider-num1">numerical value</label>
                    <input type="range" id="slider-num1" min="0" max="20" step="0.1" value="1.1">
                    <span>+</span>
                </div>
            </div>

            <hr>

            <div class="control-section">
                <label>multiply by</label>
                <div class="toggle-container">
                    <label>integers only</label>
                    <button id="btn-int-yes" class="toggle-btn active">yes</button>
                    <button id="btn-int-no" class="toggle-btn inactive">no</button>
                </div>
                <div class="slider-container">
                    <label for="slider-num2">numerical value</label>
                    <input type="range" id="slider-num2" min="0" max="20" step="1" value="2">
                    <span>+</span>
                </div>
            </div>
        </div>

        <div id="output-display">
            <p>start with: 1.1 × 2</p>
            <p>convert to: 11. × 2. = 22.</p>
            <p>estimate as: 1 × 2 = 2</p>
            <p>result is: 2.2</p>
        </div>
    </div>

    <script>
        // Helper functions
        function getDecimalPlaces(num) {
            const str = num.toString();
            const dotIndex = str.indexOf('.');
            return dotIndex === -1 ? 0 : str.length - dotIndex - 1;
        }

        function getFactor(num) {
            const decimalPlaces = getDecimalPlaces(num);
            return Math.pow(10, decimalPlaces);
        }

        function getIntString(num) {
            return num.toString().replace('.', '');
        }

        function updateDisplay() {
            const method = document.getElementById('select-method').value;
            const num1 = parseFloat(document.getElementById('slider-num1').value);
            const num2 = parseFloat(document.getElementById('slider-num2').value);

            const outputDisplay = document.getElementById('output-display');
            let content = [];

            if (method === 'estimation') {
                const num1Int = getIntString(num1);
                const num2Int = getIntString(num2);
                const productOfInts = parseInt(num1Int) * parseInt(num2Int);
                const rounded1 = Math.round(num1);
                const rounded2 = Math.round(num2);
                const productOfRounded = rounded1 * rounded2;

                content = [
                    `start with: ${num1} × ${num2}`,
                    `convert to: ${num1Int}. × ${num2Int}. = ${productOfInts}.`,
                    `estimate as: ${rounded1} × ${rounded2} = ${productOfRounded}`,
                    `result is: ${(num1 * num2).toFixed(2)}`
                ];
            } else if (method === 'place values') {
                const factor1 = getFactor(num1);
                const factor2 = getFactor(num2);
                const num1Int = getIntString(num1);
                const num2Int = getIntString(num2);
                const productOfInts = parseInt(num1Int) * parseInt(num2Int);
                const productOfFactors = factor1 * factor2;

                if (factor1 === 1 && factor2 === 1) {
                    content = [
                        `start with: ${num1} × ${num2}`,
                        `= ${num1} × ${num2}`,
                        `= ${num1 * num2}`
                    ];
                } else if (factor1 === 1) {
                    content = [
                        `start with: ${num1} × ${num2}`,
                        `rewrite as: ${num1} × (${num2Int}. ÷ ${factor2})`,
                        `= (${num1} × ${num2Int}.) ÷ ${factor2}`,
                        `= ${num1 * num2Int}. ÷ ${factor2}`,
                        `= ${(num1 * num2).toFixed(2)}`
                    ];
                } else if (factor2 === 1) {
                    content = [
                        `start with: ${num1} × ${num2}`,
                        `rewrite as: (${num1Int}. ÷ ${factor1}) × ${num2}`,
                        `= (${num1Int}. × ${num2}) ÷ ${factor1}`,
                        `= ${num1Int * num2}. ÷ ${factor1}`,
                        `= ${(num1 * num2).toFixed(2)}`
                    ];
                } else {
                    content = [
                        `start with: ${num1} × ${num2}`,
                        `rewrite as: (${num1Int}. ÷ ${factor1}) × (${num2Int}. ÷ ${factor2})`,
                        `= (${num1Int}. × ${num2Int}.) ÷ (${factor1} × ${factor2})`,
                        `= ${productOfInts}. ÷ ${productOfFactors}`,
                        `= ${(num1 * num2).toFixed(2)}`
                    ];
                }
            }

            outputDisplay.innerHTML = content.map(line => `<p>${line}</p>`).join('');
        }

        // Event listeners
        document.getElementById('select-method').addEventListener('change', updateDisplay);
        document.getElementById('slider-num1').addEventListener('input', updateDisplay);
        document.getElementById('slider-num2').addEventListener('input', updateDisplay);

        document.getElementById('btn-int-yes').addEventListener('click', function() {
            const slider = document.getElementById('slider-num2');
            slider.step = '1';
            slider.value = Math.round(parseFloat(slider.value));
            this.className = 'toggle-btn active';
            document.getElementById('btn-int-no').className = 'toggle-btn inactive';
            updateDisplay();
        });

        document.getElementById('btn-int-no').addEventListener('click', function() {
            const slider = document.getElementById('slider-num2');
            slider.step = '0.1';
            this.className = 'toggle-btn active';
            document.getElementById('btn-int-yes').className = 'toggle-btn inactive';
            updateDisplay();
        });

        // Initialize display
        updateDisplay();
    </script>
</body>
</html>
