<body>
<div class="container">
  <!-- Control Panel -->
  <div class="control-panel">
    <label for="slider-time">time (seconds)</label>
    <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
    <span id="span-time-value">9.8</span>
    <button id="btn-reset" title="Reset simulation">+</button>
  </div>

  <!-- Material Visualization -->
  <div>
    <div class="visualization-title">Material System Visualization</div>
    <canvas id="canvas-material-bar" width="600" height="90"></canvas>
  </div>

  <!-- Plot Area -->
  <div>
    <div class="visualization-title">Stefan distance versus time</div>
    <div id="div-plot" style="width:700px;height:400px;"></div>
  </div>
</div>

<script>
const TOTAL_MM = 16;   // total span to visualize on canvas
const GLASS MM = 6;    // fixed 6 mm for glass

// ------------------------------------------------------------
// Stefan distance helper
function stefanDistance(t){
  if(t <= 0) return 0;
  return 0.155 * Math.sqrt(t);
}

// ------------------------------------------------------------
// Canvas drawing
const canvas  = document.getElementById('canvas-material-bar');
const ctx     = canvas.getContext('2d');
const pxPerMm = canvas.width / TOTAL_MM;

function drawMaterialBar(t){
  ctx.clearRect(0,0,canvas.width, canvas.height);
  const smm = stefanDistance(t);
  const meltedMm = sMm;
  const meltedPx = meltedMm * pxPerMm;
  const glassPx = GLASS_MM * pxPerMm;

  // Glass (0-6 mm): red to white gradient
  const glassGradient = ctx.createLinearGradient(0,0,glassPx,0);
  glassGradient.addColorStop(0,"#FF0000");
  glassGradient.addColorStop(1,"#FFFFFF");
  ctx.fillStyle = glassGradient;
  ctx.fillRect(0,30,glassPx,40);

  // Melted water
  ctx.fillStyle = "#00BFFF";
  ctx.fillRect(glassPx,30,meltedPx,40);

  // Ice
  ctx.fillStyle = "#003366";
  const iceLeftPx = glassPx + meltedPx;
  ctx.fillRect(iceLeftPx,30,canvas.width - iceLeftPx,40);

  // Horizontal axis
  ctx.beginPath();
  ctx.moveTo(0,80);
  ctx.lineTo(canvas.width,80);
  ctx.strokeStyle="#000";
  ctx.stroke();

  // Axis labels
  ctx.fillStyle="#000";
  ctx.textAlign="center";
  ctx.font="12px Arial";
  ctx.fillText("0",0,95);
  ctx.fillText("5",pxPerMm*5,95);
  ctx.fillText("10",pxPerMm*10,95);
  ctx.fillText("15",pxPerMm*15,95);
}

// ------------------------------------------------------------
// Plotly plot
const plotDiv = document.getElementById('div-plot');
const tArray = [];
const sArray = [];
for(let t=0; t<=100; t+=0.1){
  tArray.push(t);
  sArray.push(stefanDistance(t));
}
const trace = {
  x: tArray,
  y: sArray,
  type:'scatter',
  mode:'lines',
  line:{color:'#FFA500',width:2},
  name:'s(t)'
};
const layout = {
  margin:{l:60,r:40,t:40,b:60},
  xaxis:{title:'time (s)', range:[0,100]},
  yaxis:{title:'distance (mm)', range:[0,1.6]*Math.max(...sArray)*1.1},
  showlegend:false
};
Plotly.newPlot(plotDiv,[trace],layout);

function updatePlotIndicator(t){
  const sVal = stefanDistance(t);
  const shape = {
    type:'line',
    x0:t,y0:0,x1:t,y1:sVal,
    yref:'y',
    xref:'x',
    line:{color:'#000000',width:2}
  };
  Plotly.relayout(plotDiv,{shapes:[shape]});
}

// ------------------------------------------------------------
// Master update
const slider = document.getElementById('slider-time');
const valueDisplay = document.getElementById('span-time-value');

function updateAll(){
  const t = parseFloat(slider.value);
  valueDisplay.textContent = t.toFixed(1);
  drawMaterialBar(t);
  updatePlotIndicator(t);
}

slider.addEventListener('input',updateAll);

// Reset button
document.getElementById('btn-reset').addEventListener('click',()=>{
  slider.value = 0;
  updateAll();
});

// Initial initialization
updateAll();
</script>
</body>
</html>
